INSERT INTO `pokemon_pokemon` (`id`, `name`, `nom`, `pv`, `attaque`, `defense`, `attaque_speciale`, `defense_speciale`, `vitesse`, `evolution_de`, `description`, `type_1`, `type_2`, `gen_id`) VALUES
(1, 'bulbasaur', 'Bulbizarre', 45, 49, 49, 65, 65, 45, NULL, 'Au matin de sa vie, la graine sur son dos lui fournit les éléments dont il a besoin pour grandir.\r\n', 4, 12, 1),
(2, 'ivysaur', 'Herbizarre', 60, 62, 63, 80, 80, 60, 1, 'Lorsque le bourgeon sur son dos\r\néclot, il répand un doux parfum\r\npour célébrer sa floraison.\r\n', 4, 12, 1),
(3, 'venusaur', 'Florizarre', 80, 82, 83, 100, 100, 80, 2, 'Le parfum de sa fleur se fait plus\r\npénétrant les lendemains de pluie.\r\nCela appâte les autres Pokémon.\r\n', 4, 12, 1),
(4, 'charmander', 'Salamèche', 39, 52, 43, 60, 50, 65, NULL, 'La flamme de sa queue symbolise sa\r\nvitalité. Elle est intense quand il\r\nest en bonne santé.\r\n', 10, NULL, 1),
(5, 'charmeleon', 'Reptincel', 58, 64, 58, 80, 65, 80, 4, 'La nuit, la queue ardente du Reptincel\r\nbrille comme une étoile dans son\r\nrepaire montagneux.\r\n', 10, NULL, 1),
(6, 'charizard', 'Dracaufeu', 78, 84, 78, 109, 85, 100, 5, 'On raconte que la flamme du\r\nDracaufeu s’intensifie après\r\nun combat difficile.\r\n', 3, 10, 1),
(7, 'squirtle', 'Carapuce', 44, 48, 65, 50, 64, 43, NULL, 'Il se réfugie dans sa carapace\r\net réplique en éclaboussant\r\nl’ennemi à la première occasion.\r\n', 11, NULL, 1),
(8, 'wartortle', 'Carabaffe', 59, 63, 80, 65, 80, 58, 7, 'On prétend qu’il vit 10 000 ans.\r\nSa queue duveteuse est un\r\nsymbole de longévité populaire.\r\n', 11, NULL, 1),
(9, 'blastoise', 'Tortank', 79, 83, 100, 85, 105, 78, 8, 'Les trombes d’eau projetées par\r\nles canons de sa carapace peuvent\r\npercer le métal le plus résistant.\r\n', 11, NULL, 1),
(10, 'caterpie', 'Chenipan', 45, 30, 35, 20, 20, 45, NULL, 'Ses antennes rouges libèrent une\r\npuanteur qui repousse l’ennemi.\r\nIl grandit par mues régulières.\r\n', 7, NULL, 1),
(11, 'metapod', 'Chrysacier', 50, 20, 55, 25, 25, 30, 10, 'Son corps frêle est protégé par sa\r\ncarapace d’acier. Il encaisse les\r\ncoups durs en attendant d’évoluer.\r\n', 7, NULL, 1),
(12, 'butterfree', 'Papilusion', 60, 45, 50, 90, 80, 70, 11, 'Il raffole du nectar des fleurs. Il\r\nest capable de repérer la plus\r\npetite quantité de pollen.\r\n', 3, 7, 1),
(13, 'weedle', 'Aspicot', 40, 35, 30, 20, 20, 50, NULL, 'Il mange chaque jour son poids en\r\nfeuilles. Il utilise l’aiguillon sur sa\r\ntête pour repousser l’ennemi.\r\n', 4, 7, 1),
(14, 'kakuna', 'Coconfort', 45, 25, 50, 25, 25, 35, 13, 'Il se cache sous les feuilles et les\r\nbranches pour fuir les prédateurs\r\nen attendant d’évoluer.\r\n', 4, 7, 1),
(15, 'beedrill', 'Dardargnan', 65, 90, 40, 45, 80, 75, 14, 'Il virevolte rapidement autour de\r\nl’ennemi et frappe de son dard\r\nempoisonné avant de décamper.\r\n', 4, 7, 1),
(16, 'pidgey', 'Roucool', 40, 45, 40, 35, 35, 56, NULL, 'Ce Pokémon docile préfère éviter\r\nle combat. Toutefois, il se montre\r\ntrès féroce quand on l’agresse.\r\n', 1, 3, 1),
(17, 'pidgeotto', 'Roucoups', 63, 60, 55, 50, 50, 71, 16, 'Il survole son vaste territoire en\r\nquête d’une proie avant de fondre\r\nsur elle toutes griffes en avant.\r\n', 1, 3, 1),
(18, 'pidgeot', 'Roucarnage', 83, 80, 75, 70, 70, 101, 17, 'En battant des ailes de toutes ses\r\nforces, Roucarnage génère une\r\nrafale à en déraciner les arbres.\r\n', 1, 3, 1),
(19, 'rattata', 'Rattata', 30, 56, 35, 25, 35, 72, NULL, 'D’une prudence extrême, sa nature\r\nrobuste lui permet de s’adapter à\r\ntous les terrains.\r\n', 1, NULL, 1),
(20, 'raticate', 'Rattatac', 55, 81, 60, 50, 70, 97, 19, 'Il grignote sans cesse pour apaiser\r\nla poussée de ses crocs. Il peut\r\ntranspercer un mur de parpaings.\r\n', 1, NULL, 1),
(21, 'spearow', 'Piafabec', 40, 60, 30, 31, 31, 70, NULL, 'Il est incapable de voler à haute altitude.\r\nIl se déplace très vite pour protéger\r\nson territoire.\r\n', 1, 3, 1),
(22, 'fearow', 'Rapasdepic', 65, 90, 65, 61, 61, 100, 21, 'Il a assez d’énergie pour voler\r\ntoute la journée avec ses grandes\r\nailes. Il frappe de son bec acéré.\r\n', 1, 3, 1),
(23, 'ekans', 'Abo', 35, 60, 44, 40, 54, 55, NULL, 'Il se faufile dans l’herbe sans un\r\nbruit et frappe dans le dos quand\r\nsa proie s’y attend le moins.\r\n', 4, NULL, 1),
(24, 'arbok', 'Arbok', 60, 95, 69, 65, 79, 80, 23, 'Il utilise la marque sur son ventre\r\npour intimider l’ennemi. Il étouffe\r\nl’ennemi pétrifié par la peur.\r\n', 4, NULL, 1),
(25, 'pikachu', 'Pikachu', 35, 55, 40, 50, 50, 90, 172, 'Il lui arrive de remettre d’aplomb\r\nun Pikachu allié en lui envoyant\r\nune décharge électrique.\r\n', 13, NULL, 1),
(26, 'raichu', 'Raichu', 60, 90, 55, 90, 80, 110, 25, 'Il se protège des décharges grâce\r\nà sa queue qui dissipe l’électricité\r\ndans le sol.\r\n', 13, NULL, 1),
(27, 'sandshrew', 'Sabelette', 50, 75, 85, 20, 30, 40, NULL, 'Il vit sur des terres arides épargnées\r\npar la pluie. Il se roule en boule pour\r\nse protéger.\r\n', 5, NULL, 1),
(28, 'sandslash', 'Sablaireau', 75, 100, 110, 45, 55, 65, 27, 'Il se met en boule pour percuter\r\nl’ennemi. Ses épines aiguisées\r\nfont beaucoup de dégâts.\r\n', 5, NULL, 1),
(29, 'nidoran-f', 'Nidoran♀', 55, 47, 52, 40, 40, 41, NULL, 'Bien qu’il rechigne à se battre, une\r\ngoutte du poison sécrété par ses\r\npiquants peut s’avérer fatale.\r\n', 4, NULL, 1),
(30, 'nidorina', 'Nidorina', 70, 62, 67, 55, 55, 56, 29, 'Ce Pokémon dresse ses piquants\r\nen cas de danger. Ils poussent\r\nmoins vite que ceux de Nidorino.\r\n', 4, NULL, 1),
(31, 'nidoqueen', 'Nidoqueen', 90, 92, 87, 75, 85, 76, 30, 'Son corps est recouvert d’écailles\r\nsolides. Il donnera sa vie pour\r\nsecourir les petits de son terrier.\r\n', 4, 5, 1),
(32, 'nidoran-m', 'Nidoran♂', 46, 57, 40, 40, 40, 50, NULL, 'Il jauge le terrain en laissant\r\nses oreilles dépasser de l’herbe.\r\nIl se défend avec sa corne toxique.\r\n', 4, NULL, 1),
(33, 'nidorino', 'Nidorino', 61, 72, 57, 55, 55, 65, 32, 'D’un tempérament violent, il\r\nempale l’ennemi sur sa corne\r\nqui distille un poison puissant.\r\n', 4, NULL, 1),
(34, 'nidoking', 'Nidoking', 81, 102, 77, 85, 75, 85, 33, 'Un coup de sa puissante queue\r\npeut briser un poteau téléphonique\r\ncomme une allumette.\r\n', 4, 5, 1),
(35, 'clefairy', 'Mélofée', 70, 45, 48, 60, 65, 35, 173, 'On dit que ceux qui voient danser\r\nun groupe de Mélofée sous la pleine\r\nlune connaîtront un grand bonheur.\r\n', 18, NULL, 1),
(36, 'clefable', 'Mélodelfe', 95, 70, 73, 95, 90, 60, 35, 'Il est très farouche et se laisse\r\nrarement approcher. De plus, il\r\ndétecte les sons à plus d’1 km.\r\n', 18, NULL, 1),
(37, 'vulpix', 'Goupix', 38, 41, 40, 50, 65, 65, NULL, 'Il envoie des boules de feu. Avec\r\nl’âge, ses six queues en forment de\r\nnouvelles. \r\n', 10, NULL, 1),
(38, 'ninetales', 'Feunard', 73, 76, 75, 81, 100, 100, 37, 'On raconte que ses neuf queues\r\ndétiennent un pouvoir mystique.\r\nIl peut vivre pendant mille ans.\r\n', 10, NULL, 1),
(39, 'jigglypuff', 'Rondoudou', 115, 45, 20, 45, 25, 20, 174, 'Lorsqu’il roule ses grands yeux\r\nronds, il entonne une berceuse\r\nqui endort son auditoire.\r\n', 1, 18, 1),
(40, 'wigglytuff', 'Grodoudou', 140, 70, 45, 85, 50, 45, 39, 'Sa fourrure est d’une douceur\r\nincomparable au toucher. Il \r\npeut gonfler en aspirant de l’air.\r\n', 1, 18, 1),
(41, 'zubat', 'Nosferapti', 40, 45, 35, 30, 40, 55, NULL, 'Il se repère dans l’espace grâce\r\naux ultrasons émis par sa gueule.\r\n', 3, 4, 1),
(42, 'golbat', 'Nosferalto', 75, 80, 70, 65, 75, 90, 41, 'Ses crocs acérés et creux peuvent\r\npénétrer la plus épaisse des peaux\r\net sucer le sang de la victime.\r\n', 3, 4, 1),
(43, 'oddish', 'Mystherbe', 45, 50, 55, 75, 65, 30, NULL, 'En journée, il plante ses pieds-racines\r\ndans le sol. La nuit, il se promène en\r\nsemant des graines.\r\n', 4, 12, 1),
(44, 'gloom', 'Ortide', 60, 65, 70, 85, 75, 40, 43, 'L’odeur du nectar de sa bouche est\r\nsi répugnante qu’elle agresse les\r\nnarines à deux kilomètres.\r\n', 4, 12, 1),
(45, 'vileplume', 'Rafflesia', 75, 80, 85, 110, 90, 50, 44, 'Ses pétales sont les plus grands\r\ndu monde. Il marche en répandant\r\nun pollen extrêmement allergène.\r\n', 4, 12, 1),
(46, 'paras', 'Paras', 35, 70, 55, 45, 55, 25, NULL, 'Des champignons appelés “tochukaso”\r\npoussent sur son dos. Ils évoluent\r\navec le Paras hôte.\r\n', 7, 12, 1),
(47, 'parasect', 'Parasect', 60, 95, 80, 60, 80, 30, 46, 'Un champignon parasite plus gros\r\nque Parasect contrôle son corps.\r\nIl répand des spores empoisonnées.\r\n', 7, 12, 1),
(48, 'venonat', 'Mimitoss', 60, 55, 50, 40, 55, 45, NULL, 'Ses grands yeux sont en fait des\r\namas d’yeux minuscules. La nuit,\r\nil est attiré par la lumière.\r\n', 4, 7, 1),
(49, 'venomoth', 'Aéromite', 70, 65, 60, 90, 75, 90, 48, 'Il répand ses écailles poudreuses\r\nen battant des ailes. Elles sont\r\ntoxiques au toucher.\r\n', 4, 7, 1),
(50, 'diglett', 'Taupiqueur', 10, 55, 25, 35, 45, 95, NULL, 'Un Pokémon qui vit sous terre.\r\nHabitué aux souterrains, il fuit la\r\nlumière du jour.\r\n', 5, NULL, 1),
(51, 'dugtrio', 'Triopikeur', 35, 100, 50, 50, 70, 120, 50, 'Ses trois têtes creusent le sol à\r\ntour de rôle. Il peut atteindre des\r\nprofondeurs de 100 kilomètres.\r\n', 5, NULL, 1),
(52, 'meowth', 'Miaouss', 40, 45, 35, 40, 40, 90, NULL, 'Son regard s’anime à la vue\r\nd’un objet brillant. C’est un\r\nPokémon nocturne.\r\n', 1, NULL, 1),
(53, 'persian', 'Persian', 65, 70, 60, 65, 65, 115, 52, 'Un Pokémon très snob. La taille du\r\njoyau qui orne son front alimente\r\nbien des débats parmi ses fans.\r\n', 1, NULL, 1),
(54, 'psyduck', 'Psykokwak', 50, 52, 48, 65, 50, 55, NULL, 'Ses pouvoirs déconcertants\r\net mystérieux lui font subir\r\nde constantes migraines.\r\n', 11, NULL, 1),
(55, 'golduck', 'Akwakwak', 80, 82, 78, 95, 80, 85, 54, 'Ses membres palmés et son corps\r\nparfaitement adapté font de lui\r\nun nageur exceptionnel.\r\n', 11, NULL, 1),
(56, 'mankey', 'Férosinge', 40, 80, 35, 35, 45, 70, NULL, 'Ils vivent en colonies sylvestres.\r\nQuand un Férosinge s’énerve, toute\r\nla colonie suit son exemple.\r\n', 2, NULL, 1),
(57, 'primeape', 'Colossinge', 65, 105, 60, 60, 70, 95, 56, 'Il grogne quand on le toise, rugit\r\nquand on s’enfuit et devient fou\r\nde rage quand on le frappe.\r\n', 2, NULL, 1),
(58, 'growlithe', 'Caninos', 55, 70, 45, 70, 50, 60, NULL, 'Un Pokémon très loyal. Il restera\r\nimmobile jusqu’à ce que son\r\nDresseur lui donne un ordre.\r\n', 10, NULL, 1),
(59, 'arcanine', 'Arcanin', 90, 110, 80, 100, 80, 95, 58, 'Son port altier et son attitude\r\nfière ont depuis longtemps\r\nconquis le cœur des hommes.\r\n', 10, NULL, 1),
(60, 'poliwag', 'Ptitard', 40, 50, 40, 40, 40, 90, NULL, 'Sa peau est si mince qu’on voit\r\nses organes internes. Il tient\r\nà peine sur ses nouveaux pieds.\r\n', 11, NULL, 1),
(61, 'poliwhirl', 'Têtarte', 65, 65, 65, 50, 50, 90, 60, 'La spirale qui orne son ventre ondule\r\ndoucement. Celui qui la fixe est saisi\r\nd’une étrange torpeur.\r\n', 11, NULL, 1),
(62, 'poliwrath', 'Tartard', 90, 95, 95, 70, 90, 70, 61, 'Il possède de sacrés biscoteaux.\r\nIl peut parcourir sans relâche\r\nl’Océan Pacifique.\r\n', 2, 11, 1),
(63, 'abra', 'Abra', 25, 20, 15, 105, 55, 90, NULL, 'L’utilisation de ses pouvoirs\r\nmentaux le fatigue tellement\r\nqu’il dort 18 heures par jour.\r\n', 14, NULL, 1),
(64, 'kadabra', 'Kadabra', 40, 35, 30, 120, 70, 105, 63, 'Il fixe sa cuillère d’argent afin de\r\nconcentrer son esprit et d’émettre\r\nun maximum d’ondes alpha.\r\n', 14, NULL, 1),
(65, 'alakazam', 'Alakazam', 55, 50, 45, 135, 95, 120, 64, 'On dit que les cuillères qu’il tient\r\nen permanence ont été créées\r\npar la puissance de son esprit.\r\n', 14, NULL, 1),
(66, 'machop', 'Machoc', 70, 80, 50, 35, 35, 35, NULL, 'Malgré sa petite taille, sa force\r\nlui permet de soulever plusieurs\r\nRacaillou à la fois.\r\n', 2, NULL, 1),
(67, 'machoke', 'Machopeur', 80, 100, 70, 50, 60, 45, 66, 'Il soulève les plus lourdes charges\r\navec plaisir et facilité. Il donne un\r\ncoup de main tout en s’entraînant!\r\n', 2, NULL, 1),
(68, 'machamp', 'Mackogneur', 90, 130, 80, 65, 85, 55, 67, 'La puissance et la rapidité de ses\r\nquatre bras puissants font des\r\nravages sur ses adversaires.\r\n', 2, NULL, 1),
(69, 'bellsprout', 'Chétiflor', 50, 75, 35, 70, 30, 40, NULL, 'Il préfère les climats chauds et\r\nhumides. Ses lianes peuvent\r\ncapturer une proie en un clin d’œil.\r\n', 4, 12, 1),
(70, 'weepinbell', 'Boustiflor', 65, 90, 50, 85, 45, 55, 69, 'Un Pokémon végétal. Il capture les\r\nproies étourdies en les endormant\r\nà l’aide d’une poudre toxique.\r\n', 4, 12, 1),
(71, 'victreebel', 'Empiflor', 80, 105, 65, 100, 70, 70, 70, 'Sa bouche sécrète un fluide à\r\nl’odeur de miel, qui s’avère être\r\nun acide extrêmement corrosif.\r\n', 4, 12, 1),
(72, 'tentacool', 'Tentacool', 40, 40, 35, 50, 100, 70, NULL, 'Son corps est principalement composé d’eau.\r\nSes yeux cristallins projettent d’étranges rayons.\r\n', 4, 11, 1),
(73, 'tentacruel', 'Tentacruel', 80, 70, 65, 80, 120, 100, 72, 'Ses 80 tentacules lui permettent\r\nd’emprisonner ses adversaires\r\ndans un redoutable filet venimeux.\r\n', 4, 11, 1),
(74, 'geodude', 'Racaillou', 40, 80, 100, 30, 30, 20, NULL, 'Au repos, rien ne le distingue d’un\r\nvulgaire caillou. Malheur à ceux qui\r\nlui marchent dessus par mégarde!\r\n', 5, 6, 1),
(75, 'graveler', 'Gravalanch', 55, 95, 115, 45, 45, 35, 74, 'Il dévale les montagnes pour se\r\ndéplacer. Une fois qu’il a pris tout\r\nson élan, dur dur de l’arrêter!\r\n', 5, 6, 1),
(76, 'golem', 'Grolem', 80, 120, 130, 55, 65, 45, 75, 'Aucun explosif ne pourrait entamer\r\nsa carapace de pierre. Il mue une\r\nfois par an.\r\n', 5, 6, 1),
(77, 'ponyta', 'Ponyta', 50, 85, 55, 65, 65, 90, NULL, 'Chancelantes à la naissance, ses\r\npattes deviennent très vite sûres\r\net solides à force de galoper.\r\n', 10, NULL, 1),
(78, 'rapidash', 'Galopa', 65, 100, 70, 80, 80, 105, 77, 'Au grand galop, sa crinière de feu\r\ndisperse au vent une myriade\r\nd’étincelles ardentes.\r\n', 10, NULL, 1),
(79, 'slowpoke', 'Ramoloss', 90, 65, 65, 40, 40, 15, NULL, 'Bien que lent, c’est un pêcheur\r\nadroit qui utilise sa queue. Elle\r\nest insensible aux morsures.\r\n', 11, 14, 1),
(80, 'slowbro', 'Flagadoss', 95, 75, 110, 100, 80, 30, 79, 'Ce grand benêt connaît des éclairs\r\nde lucidité lorsque le Kokiyas de sa\r\nqueue se met à mordre.\r\n', 11, 14, 1),
(81, 'magnemite', 'Magnéti', 25, 35, 70, 95, 55, 45, NULL, 'Plus la rotation de ses extrémités\r\nest rapide, plus la force du champ\r\nmagnétique qu’il génère est grande.\r\n', 9, 13, 1),
(82, 'magneton', 'Magnéton', 50, 60, 95, 120, 70, 70, 81, 'Des groupes apparaissent si des\r\ntaches solaires couvrent le soleil.\r\nIls brouillent les télévisions.\r\n', 9, 13, 1),
(83, 'farfetchd', 'Canarticho', 52, 90, 55, 58, 62, 60, NULL, 'Il ne peut pas vivre sans son\r\nlégume, c’est pourquoi il le\r\nprotégera au péril de sa vie.\r\n', 1, 3, 1),
(84, 'doduo', 'Doduo', 35, 85, 45, 35, 35, 75, NULL, 'Ses deux cerveaux semblent\r\ncommuniquer leurs émotions\r\ngrâce à un lien télépathique.\r\n', 1, 3, 1),
(85, 'dodrio', 'Dodrio', 60, 110, 70, 60, 60, 110, 84, 'Quand Doduo connaît cette étrange\r\névolution, l’une de ses têtes se\r\ndédouble. Il atteint les 60 km/h.\r\n', 1, 3, 1),
(86, 'seel', 'Otaria', 65, 45, 55, 45, 70, 45, NULL, 'Un habitant des icebergs.\r\nEn mer, il utilise la corne sur\r\nsa tête pour briser la banquise.\r\n', 11, NULL, 1),
(87, 'dewgong', 'Lamantine', 90, 70, 80, 70, 95, 70, 86, 'Son corps est couvert d’un grand\r\nmanteau blanc qui, dans la neige, le\r\ndissimule aux yeux des prédateurs.\r\n', 11, 15, 1),
(88, 'grimer', 'Tadmorv', 80, 80, 50, 40, 50, 25, NULL, 'Il est né d’un torrent de boue\r\nexposé aux rayons X de la lune.\r\nIl vit dans les ordures.\r\n', 4, NULL, 1),
(89, 'muk', 'Grotadmorv', 105, 105, 75, 65, 100, 50, 88, 'Son corps exsude un fluide toxique\r\nqui tue instantanément les plantes\r\net les arbres au contact.\r\n', 4, NULL, 1),
(90, 'shellder', 'Kokiyas', 30, 65, 100, 45, 25, 40, NULL, 'Il nage à reculons en ouvrant et en\r\nrefermant ses deux coquilles. Il\r\nlaisse traîner sa large langue.\r\n', 11, NULL, 1),
(91, 'cloyster', 'Crustabri', 50, 95, 180, 85, 45, 70, 90, 'Il se défend en fermant sa coquille\r\net projette des piquants qui\r\nrepoussent ses agresseurs.\r\n', 11, 15, 1),
(92, 'gastly', 'Fantominus', 30, 35, 30, 100, 35, 80, NULL, 'Son corps composé de gaz toxique\r\npourrait asphyxier n’importe qui\r\nen quelques secondes.\r\n', 4, 8, 1),
(93, 'haunter', 'Spectrum', 45, 50, 45, 115, 55, 95, 92, 'Il adore se tapir dans l’ombre et\r\nfaire frissonner ses proies en leur\r\ntouchant l’épaule.\r\n', 4, 8, 1),
(94, 'gengar', 'Ectoplasma', 60, 65, 60, 130, 75, 110, 93, 'Si vous croisez un regard\r\ninquiétant qui perce la nuit,\r\nc’est sûrement un Ectoplasma.\r\n', 4, 8, 1),
(95, 'onix', 'Onix', 35, 45, 160, 30, 45, 70, NULL, 'Il se nourrit des pierres qu’il\r\nrencontre en creusant le sol.\r\nIl peut creuser à 80 km/h!\r\n', 5, 6, 1),
(96, 'drowzee', 'Soporifik', 60, 48, 45, 43, 90, 42, NULL, 'Son grand nez lui permet de lire\r\nles rêves d’autrui. Il adore les\r\nsonges amusants.\r\n', 14, NULL, 1),
(97, 'hypno', 'Hypnomade', 85, 73, 70, 73, 115, 67, 96, 'La vue de son pendule oscillant\r\nendort en trois secondes, même\r\nquand on vient de se réveiller.\r\n', 14, NULL, 1),
(98, 'krabby', 'Krabby', 30, 105, 90, 25, 25, 50, NULL, 'Il creuse son terrier sur des plages\r\nsablonneuses. Ses pinces repoussent\r\nsi on les brise.\r\n', 11, NULL, 1),
(99, 'kingler', 'Krabboss', 55, 130, 115, 50, 50, 75, 98, 'Sa grande pince possède une puissance\r\nde 10 000 chevaux. Son poids la rend\r\ndifficile à manier.\r\n', 11, NULL, 1),
(100, 'voltorb', 'Voltorbe', 40, 30, 50, 55, 55, 100, NULL, 'Il ressemble à une Poké Ball. Ce\r\nPokémon dangereux peut exploser\r\nou s’électrifier au toucher.\r\n', 13, NULL, 1),
(101, 'electrode', 'Électrode', 60, 50, 70, 80, 80, 150, 100, 'Il se laisse porter par les vents\r\nlorsque son corps est gonflé\r\nd’électricité à en éclater.\r\n', 13, NULL, 1),
(102, 'exeggcute', 'Noeunoeuf', 60, 40, 80, 60, 45, 40, NULL, 'Ces six œufs communiquent par\r\ntélépathie. Ils peuvent se réunir\r\nrapidement si on les sépare.\r\n', 12, 14, 1),
(103, 'exeggutor', 'Noadkoko', 95, 95, 85, 125, 75, 55, 102, 'On l’appelle “jungle sur pattes”.\r\nSi une tête devient trop grosse,\r\nelle tombe et produit un Noeunoeuf.\r\n', 12, 14, 1),
(104, 'cubone', 'Osselait', 50, 50, 95, 40, 50, 35, NULL, 'Il pleure en pensant à sa mère\r\ndisparue, et ses larmes résonnent\r\ndans son crâne creux.\r\n', 5, NULL, 1),
(105, 'marowak', 'Ossatueur', 60, 80, 110, 50, 80, 45, 104, 'Ce Pokémon sauvage possède des os\r\ndepuis sa naissance. Il s’en sert\r\npour combattre avec dextérité.\r\n', 5, NULL, 1),
(106, 'hitmonlee', 'Kicklee', 50, 120, 53, 35, 110, 87, 236, 'Ses pattes élastiques s’allongent, ce\r\nqui ne manque jamais de surprendre\r\nau premier combat.\r\n', 2, NULL, 1),
(107, 'hitmonchan', 'Tygnon', 50, 105, 79, 35, 110, 76, 236, 'Même le béton cède sous ses \r\npoings dévastateurs. Au combat, il\r\ns’essouffle au bout de 3 minutes.\r\n', 2, NULL, 1),
(108, 'lickitung', 'Excelangue', 90, 55, 75, 60, 75, 30, NULL, 'Quand il déploie sa langue de près\r\nde deux mètres, sa queue s’agite.\r\nElles seraient reliées entre elles.\r\n', 1, NULL, 1),
(109, 'koffing', 'Smogo', 40, 65, 95, 60, 45, 35, NULL, 'Il flotte en retenant des gaz plus\r\nlégers que l’air. Ceux-ci sont\r\nexplosifs, en plus d’être fétides.\r\n', 4, NULL, 1),
(110, 'weezing', 'Smogogo', 65, 90, 120, 85, 70, 60, 109, 'Il grandit en absorbant les gaz des\r\ndétritus. Des triplés existent, bien\r\nqu’ils soient fort rares.\r\n', 4, NULL, 1),
(111, 'rhyhorn', 'Rhinocorne', 80, 85, 95, 30, 30, 25, NULL, 'Ses puissantes charges pourraient\r\ndémolir n’importe quel bâtiment.\r\nDommage qu’il soit stupide.\r\n', 5, 6, 1),
(112, 'rhydon', 'Rhinoféros', 105, 130, 120, 45, 45, 40, 111, 'La station debout a libéré ses\r\npattes avant et l’a rendu plus\r\nintelligent, mais il est distrait.\r\n', 5, 6, 1),
(113, 'chansey', 'Leveinard', 250, 5, 5, 35, 105, 50, 440, 'Ce Pokémon très serviable distribue\r\nses œufs hautement nutritifs aux\r\nhumains et Pokémon blessés.\r\n', 1, NULL, 1),
(114, 'tangela', 'Saquedeneu', 65, 55, 115, 100, 40, 60, NULL, 'Il se cache derrière des lianes\r\nbleues recouvertes d’un fin duvet.\r\nIl est chatouilleux.\r\n', 12, NULL, 1),
(115, 'kangaskhan', 'Kangourex', 105, 95, 80, 40, 80, 90, NULL, 'Il élève ses petits dans sa poche\r\nventrale. Il attend d’être en lieu\r\nsûr pour les laisser jouer dehors.\r\n', 1, NULL, 1),
(116, 'horsea', 'Hypotrempe', 30, 40, 70, 70, 25, 60, NULL, 'Il niche à l’ombre du corail. Quand\r\nil se sent menacé, il disparaît\r\ndans un nuage d’encre opaque.\r\n', 11, NULL, 1),
(117, 'seadra', 'Hypocéan', 55, 65, 95, 95, 45, 85, 116, 'Son épine dorsale le protège.\r\nSes os et ses nageoires sont très\r\nprisés en médecine traditionnelle.\r\n', 11, NULL, 1),
(118, 'goldeen', 'Poissirène', 45, 67, 60, 35, 50, 63, NULL, 'Malgré son élégance quand il\r\nnage, ses coups de corne sont\r\nredoutables.\r\n', 11, NULL, 1),
(119, 'seaking', 'Poissoroy', 80, 92, 65, 65, 80, 68, 118, 'En automne, à la saison des amours,\r\nil fait des réserves de graisse et\r\narbore des couleurs chatoyantes.\r\n', 11, NULL, 1),
(120, 'staryu', 'Stari', 30, 45, 55, 70, 55, 85, NULL, 'Même amoché, son corps se régénère\r\ntant que le noyau rouge est intact.\r\nLe noyau s’illumine à minuit.\r\n', 11, NULL, 1),
(121, 'starmie', 'Staross', 60, 75, 85, 100, 85, 115, 120, 'Un noyau rouge trône en son centre.\r\nIl envoie des signaux radio mystérieux\r\nvers le ciel nocturne.\r\n', 11, 14, 1),
(122, 'mr-mime', 'M. Mime', 40, 45, 65, 100, 120, 90, 439, 'En modifiant les molécules de l’air\r\ndu bout de ses doigts, il parvient\r\nà créer un mur invisible devant lui.\r\n', 14, 18, 1),
(123, 'scyther', 'Insécateur', 70, 110, 80, 55, 80, 105, NULL, 'À force de trancher des objets\r\nsolides, les faux de ses bras\r\nsont très aiguisées.\r\n', 3, 7, 1),
(124, 'jynx', 'Lippoutou', 65, 50, 35, 115, 95, 95, 238, 'Son cri ressemble à des paroles\r\nhumaines. Mais nul n’est jamais\r\nparvenu à les comprendre.\r\n', 14, 15, 1),
(125, 'electabuzz', 'Élektek', 65, 83, 57, 95, 85, 105, 239, 'Il fait tournoyer ses bras pour\r\ndonner de la force à ses coups.\r\nProfitez-en pour filer!\r\n', 13, NULL, 1),
(126, 'magmar', 'Magmar', 65, 95, 57, 100, 85, 93, 240, 'Quand il respire profondément, des\r\nvagues de chaleur émanent de son\r\ncorps et le rendent difficile à voir.\r\n', 10, NULL, 1),
(127, 'pinsir', 'Scarabrute', 65, 125, 100, 55, 70, 85, NULL, 'Il serre les proies dans ses pinces\r\npour les trancher en deux. S’il n’y\r\narrive pas, il les jette au loin.\r\n', 7, NULL, 1),
(128, 'tauros', 'Tauros', 75, 100, 95, 40, 70, 110, NULL, 'Après avoir choisi sa cible, il fonce\r\ndessus tête baissée. Il est réputé\r\npour sa nature violente.\r\n', 1, NULL, 1),
(129, 'magikarp', 'Magicarpe', 20, 10, 55, 15, 20, 80, NULL, 'Un vénérable Magicarpe peut\r\nfranchir une montagne en utilisant\r\nTrempette. Mais c’est tout...\r\n', 11, NULL, 1),
(130, 'gyarados', 'Léviator', 95, 125, 79, 60, 100, 81, 129, 'Quand il se laisse emporter par la\r\nrage, il ne se calme qu’après avoir\r\ndétruit tout ce qui l’entoure.\r\n', 3, 11, 1),
(131, 'lapras', 'Lokhlass', 130, 85, 80, 85, 95, 60, NULL, 'Il aime naviguer en portant des\r\nhumains et des Pokémon sur son\r\ndos. Il comprend le langage humain.\r\n', 11, 15, 1),
(132, 'ditto', 'Métamorph', 48, 48, 48, 48, 48, 48, NULL, 'Il a la capacité de modifier sa\r\nstructure cellulaire pour prendre\r\nl’apparence de ce qu’il voit.\r\n', 1, NULL, 1),
(133, 'eevee', 'Évoli', 55, 55, 50, 45, 65, 55, NULL, 'Son ADN particulier lui permet de\r\ns’adapter très rapidement à son\r\nenvironnement.\r\n', 1, NULL, 1),
(134, 'vaporeon', 'Aquali', 130, 65, 60, 110, 95, 65, 133, 'Sa composition moléculaire est\r\nproche de celle de l’eau, ce qui\r\nlui permet de se liquéfier.\r\n', 11, NULL, 1),
(135, 'jolteon', 'Voltali', 65, 65, 60, 110, 95, 130, 133, 'Face au danger, il fait appel à\r\nl’électricité pour dresser ses\r\npoils et lancer des décharges.\r\n', 13, NULL, 1),
(136, 'flareon', 'Pyroli', 65, 130, 60, 95, 110, 65, 133, 'L’air qu’il inspire est chauffé par\r\nla glande enflammée de son corps,\r\natteignant les 1 700 °C.\r\n', 10, NULL, 1),
(137, 'porygon', 'Porygon', 65, 60, 70, 85, 75, 40, NULL, 'Ce Pokémon créé par des\r\nscientifiques peut arpenter\r\nlibrement le cyberespace.\r\n', 1, NULL, 1),
(138, 'omanyte', 'Amonita', 35, 40, 100, 90, 55, 35, NULL, 'Un Pokémon ramené à la vie par\r\nla science à partir d’un fossile. Il\r\npeuplait autrefois les mers.\r\n', 6, 11, 1),
(139, 'omastar', 'Amonistar', 70, 60, 125, 115, 70, 55, 138, 'On pense que ce Pokémon a disparu\r\nparce que sa coquille en spirale\r\nétait devenue trop grosse.\r\n', 6, 11, 1),
(140, 'kabuto', 'Kabuto', 30, 80, 90, 55, 45, 55, NULL, 'On pense qu’il peuplait les plages\r\nil y a 300 millions d’années. Il est\r\nprotégé par une coquille robuste.\r\n', 6, 11, 1),
(141, 'kabutops', 'Kabutops', 60, 115, 105, 65, 70, 80, 140, 'On pense que ce Pokémon est\r\nvenu sur la terre ferme pour\r\nsuivre l’évolution de ses proies.\r\n', 6, 11, 1),
(142, 'aerodactyl', 'Ptéra', 80, 105, 65, 60, 75, 130, NULL, 'Un Pokémon qui arpentait le ciel\r\nau temps des dinosaures. Ses\r\ncrocs sont pareils à des scies.\r\n', 3, 6, 1),
(143, 'snorlax', 'Ronflex', 160, 110, 65, 65, 110, 30, 446, 'Une fois le ventre plein, il est trop\r\namorphe pour lever le petit doigt.\r\nSautez sur son ventre!\r\n', 1, NULL, 1),
(144, 'articuno', 'Artikodin', 90, 85, 100, 95, 125, 85, NULL, 'Un Pokémon Oiseau légendaire.\r\nIl peut provoquer des blizzards\r\nen gelant l’humidité de l’air.\r\n', 3, 15, 1),
(145, 'zapdos', 'Électhor', 90, 90, 85, 125, 90, 100, NULL, 'Un Pokémon Oiseau légendaire dont\r\non dit qu’il vit dans les nuages\r\nd’orage. Il contrôle la foudre.\r\n', 3, 13, 1),
(146, 'moltres', 'Sulfura', 90, 100, 90, 125, 85, 90, NULL, 'L’un des Pokémon Oiseaux légendaires.\r\nOn dit que sa venue annonce l’arrivée\r\ndu printemps.\r\n', 3, 10, 1),
(147, 'dratini', 'Minidraco', 41, 64, 45, 50, 50, 50, NULL, 'On l’appelle “Pokémon mirage”\r\nen raison de sa rareté. On a\r\ndécouvert sa mue.\r\n', 16, NULL, 1),
(148, 'dragonair', 'Draco', 61, 84, 65, 70, 70, 70, 147, 'La météo change brusquement quand\r\nil est entouré d’une aura. On dit\r\nqu’il peuple les mers et les lacs.\r\n', 16, NULL, 1),
(149, 'dragonite', 'Dracolosse', 91, 134, 95, 100, 100, 80, 148, 'On raconte qu’il vit quelque part\r\nen mer. Il guide les équipages\r\nnaufragés jusqu’à la terre ferme.\r\n', 3, 16, 1),
(150, 'mewtwo', 'Mewtwo', 106, 110, 90, 154, 90, 130, NULL, 'Un Pokémon conçu en réorganisant\r\nles gènes de Mew. On raconte qu’il\r\ns’agit du Pokémon le plus féroce.\r\n', 14, NULL, 1),
(151, 'mew', 'Mew', 100, 100, 100, 100, 100, 100, NULL, 'Nombre de scientifiques voient en\r\nlui l’ancêtre des Pokémon car il\r\nmaîtrise toutes leurs capacités.\r\n', 14, NULL, 1),
(152, 'chikorita', 'Germignon', 45, 49, 65, 49, 65, 45, NULL, 'Il jauge la température et l’humidité\r\ngrâce à la feuille sur sa tête. Il\r\nraffole des bains de soleil.\r\n', 12, NULL, 2),
(153, 'bayleef', 'Macronium', 60, 62, 80, 63, 80, 60, 152, 'Le collier de bourgeons à son cou\r\nrépand un arôme épicé qui vous\r\ndonne du tonus.\r\n', 12, NULL, 2),
(154, 'meganium', 'Méganium', 80, 82, 100, 83, 100, 80, 153, 'Son souffle a l’incroyable capacité\r\nde ranimer les plantes et les fleurs\r\nflétries.\r\n', 12, NULL, 2),
(155, 'cyndaquil', 'Héricendre', 39, 52, 43, 60, 50, 65, NULL, 'Ce Pokémon est un grand timide.\r\nLes flammes sur son dos s’intensifient\r\nlorsqu’il prend peur.\r\n', 10, NULL, 2),
(156, 'quilava', 'Feurisson', 58, 64, 58, 80, 65, 80, 155, 'La chaleur de ses flammes intimide\r\nl’ennemi. Elles s’intensifient\r\nlorsqu’il se prépare à combattre.\r\n', 10, NULL, 2),
(157, 'typhlosion', 'Typhlosion', 78, 84, 78, 109, 85, 100, 156, 'Il attaque en projetant des flammes.\r\nIl se dissimule derrière les vagues\r\nde chaleur qu’il produit.\r\n', 10, NULL, 2),
(158, 'totodile', 'Kaiminus', 50, 65, 64, 44, 48, 43, NULL, 'Il broie tout ce qu’il trouve avec\r\nses mâchoires puissantes. Même\r\nson Dresseur doit s’en méfier.\r\n', 11, NULL, 2),
(159, 'croconaw', 'Crocrodil', 65, 80, 80, 59, 63, 58, 158, 'Quand il mord, il ne lâche pas prise\r\navant que ses crocs se brisent.\r\nIls repoussent par la suite.\r\n', 11, NULL, 2),
(160, 'feraligatr', 'Aligatueur', 85, 105, 100, 79, 83, 78, 159, 'Ce Pokémon à l’air pataud est\r\ncapable d’attaquer à la vitesse\r\nde l’éclair pour mordre sa proie.\r\n', 11, NULL, 2),
(161, 'sentret', 'Fouinette', 35, 46, 34, 35, 45, 20, NULL, 'Ce Pokémon très nerveux\r\nprend appui sur sa queue pour\r\nobserver ses alentours.\r\n', 1, NULL, 2),
(162, 'furret', 'Fouinar', 85, 76, 64, 45, 55, 90, 161, 'La femelle s’enroule autour des\r\npetits pour les bercer. Il accule\r\nl’ennemi grâce à sa vitesse.\r\n', 1, NULL, 2),
(163, 'hoothoot', 'Hoothoot', 60, 30, 30, 36, 56, 50, NULL, 'Il hulule chaque jour à la même\r\nheure. Dans certaines cultures,\r\nil incarne la sagesse.\r\n', 1, 3, 2),
(164, 'noctowl', 'Noarfang', 100, 50, 50, 86, 96, 70, 163, 'Ses yeux sont capables de voir\r\ndistinctement même quand il fait\r\ntrès sombre.\r\n', 1, 3, 2),
(165, 'ledyba', 'Coxy', 40, 20, 30, 40, 80, 55, NULL, 'Il est si timide qu’il ne se déplace\r\nqu’au milieu d’un essaim. Il exprime\r\nses émotions par l’odeur.\r\n', 3, 7, 2),
(166, 'ledian', 'Coxyclaque', 55, 35, 50, 55, 110, 85, 165, 'Il tire sa force de la lumière des\r\nétoiles. Plus il y en a et plus ses\r\nmotifs dorsaux sont grands.\r\n', 3, 7, 2),
(167, 'spinarak', 'Mimigal', 40, 60, 40, 40, 40, 30, NULL, 'Il tisse une toile fine mais solide\r\npour poser des pièges et se poste\r\ndans l’attente d’une proie.\r\n', 4, 7, 2),
(168, 'ariados', 'Migalos', 70, 90, 70, 60, 70, 40, 167, 'Il attache un fil à sa proie avant\r\nde la libérer. Il s’en servira pour\r\nla retrouver, elle et ses amis.\r\n', 4, 7, 2),
(169, 'crobat', 'Nostenfer', 85, 90, 80, 70, 80, 130, 42, 'La transformation de ses pattes en\r\nailes a certes amélioré sa capacité\r\nà voler, mais pas à marcher.\r\n', 3, 4, 2),
(170, 'chinchou', 'Loupio', 75, 38, 38, 56, 56, 67, NULL, 'Il envoie des décharges électriques\r\npositives et négatives du bout des\r\nantennes pour paralyser sa proie.\r\n', 11, 13, 2),
(171, 'lanturn', 'Lanturn', 125, 58, 58, 76, 76, 67, 170, 'La lumière de Lanturn surgit des\r\nprofondeurs. On le surnomme\r\n“étoile des profondeurs”.\r\n', 11, 13, 2),
(172, 'pichu', 'Pichu', 20, 40, 15, 35, 35, 60, NULL, 'Les poches électriques de ses\r\njoues sont encore petites, mais\r\nrisquent de l’électrocuter.\r\n', 13, NULL, 2),
(173, 'cleffa', 'Mélo', 50, 25, 28, 45, 55, 15, NULL, 'On dit qu’il se déplace à dos\r\nd’étoile filante. Il se montre\r\nsouvent quand on en voit.\r\n', 18, NULL, 2),
(174, 'igglybuff', 'Toudoudou', 90, 30, 15, 40, 20, 15, NULL, 'Son corps est doux et élastique.\r\nQuand il commence à rebondir,\r\non ne peut plus l’arrêter.\r\n', 1, 18, 2),
(175, 'togepi', 'Togepi', 35, 20, 65, 40, 65, 20, NULL, 'Il transforme les émotions\r\npositives des autres en bonheur\r\nqu’il stocke dans sa coquille.\r\n', 18, NULL, 2),
(176, 'togetic', 'Togetic', 55, 40, 85, 80, 105, 40, 175, 'Il parcourt le monde pour\r\ndistribuer de la joie aux\r\nêtres bons.\r\n', 3, 18, 2),
(177, 'natu', 'Natu', 40, 50, 45, 70, 45, 70, NULL, 'Il mange des cactus en prenant\r\nsoin d’éviter les bourgeons et les\r\népines. Il ne tient pas en place. \r\n', 3, 14, 2),
(178, 'xatu', 'Xatu', 65, 75, 70, 95, 70, 95, 177, 'Cet étrange Pokémon peut voir\r\nl’avenir et le passé. Il guette\r\nsans cesse la course du soleil.\r\n', 3, 14, 2),
(179, 'mareep', 'Wattouat', 55, 40, 40, 65, 45, 35, NULL, 'Sa laine fournie enfle lorsqu’il\r\naccumule de l’électricité statique.\r\nSon contact peut choquer. \r\n', 13, NULL, 2),
(180, 'flaaffy', 'Lainergie', 70, 55, 55, 80, 60, 45, 179, 'Sa queue s’allume lorsque sa\r\nlaine se charge d’électricité.\r\nIl projette des poils électrifiés.\r\n', 13, NULL, 2),
(181, 'ampharos', 'Pharamp', 90, 75, 85, 115, 90, 55, 180, 'Le bout de sa queue brille avec\r\nintensité. Jadis, on s’en servait\r\npour envoyer des signaux.\r\n', 13, NULL, 2),
(182, 'bellossom', 'Joliflor', 75, 80, 95, 90, 100, 50, 44, 'À la fin de la saison des pluies,\r\nil sort danser en plein air,\r\nattiré par la chaleur du soleil.\r\n', 12, NULL, 2),
(183, 'marill', 'Marill', 70, 20, 50, 20, 50, 40, 298, 'Sa queue à l’extrémité remplie\r\nd’huile fait office de flotteur et\r\nlui permet de ne pas couler.\r\n', 11, 18, 2),
(184, 'azumarill', 'Azumarill', 100, 50, 80, 60, 80, 50, 183, 'Ses poumons peuvent contenir\r\nassez d’air pour lui permettre de\r\npasser une journée sous l’eau.\r\n', 11, 18, 2),
(185, 'sudowoodo', 'Simularbre', 70, 100, 115, 30, 65, 30, 438, 'Il prend l’apparence d’un arbre\r\npour éviter les attaques, mais il\r\ndéteste l’eau et fuit la pluie.\r\n', 6, NULL, 2),
(186, 'politoed', 'Tarpaud', 90, 75, 75, 90, 100, 70, 61, 'Si Ptitard et Têtarte entendent son cri, ils se\r\nrassemblent pour venir le rencontrer.\r\n', 11, NULL, 2),
(187, 'hoppip', 'Granivol', 35, 35, 40, 35, 55, 50, NULL, 'Il dérive avec le vent. Sa présence\r\nen nombre dans les champs et les\r\nmontagnes annonce le printemps.\r\n', 3, 12, 2),
(188, 'skiploom', 'Floravol', 55, 45, 50, 45, 65, 80, 187, 'Il fleurit lorsque les températures\r\nremontent. Il flotte dans le ciel \r\npour absorber les rayons du soleil.\r\n', 3, 12, 2),
(189, 'jumpluff', 'Cotovol', 75, 55, 70, 55, 95, 110, 188, 'Poussé par les vents saisonniers,\r\nil fait le tour du globe en semant\r\ndes spores de coton.\r\n', 3, 12, 2),
(190, 'aipom', 'Capumain', 55, 70, 55, 40, 55, 85, NULL, 'Sa queue est aussi agile que ses\r\nmains. Il vit en haut des grands\r\narbres.\r\n', 1, NULL, 2),
(191, 'sunkern', 'Tournegrin', 30, 30, 30, 30, 30, 30, NULL, 'Il tombe du ciel sans crier gare en\r\nmatinée. On en croise énormément \r\nl’année suivant un été froid.\r\n', 12, NULL, 2),
(192, 'sunflora', 'Héliatronc', 75, 75, 55, 105, 85, 30, 191, 'Les rayons du soleil lui donnent de\r\nl’énergie. Il est connu pour migrer\r\nvers les régions ensoleillées.\r\n', 12, NULL, 2),
(193, 'yanma', 'Yanma', 65, 65, 45, 75, 45, 95, NULL, 'La vitesse de ses ailes lui permet\r\nun vol stationnaire. Il surveille\r\nainsi tout son territoire.\r\n', 3, 7, 2),
(194, 'wooper', 'Axoloto', 55, 45, 45, 25, 25, 15, NULL, 'Sur la terre ferme, il couvre son\r\ncorps d’un fluide toxique qui le\r\nprotège de la déshydratation.\r\n', 5, 11, 2),
(195, 'quagsire', 'Maraiste', 95, 85, 85, 65, 65, 35, 194, 'De nature placide, il a tendance à\r\nse cogner la tête contre les récifs\r\net autres bateaux de passage.\r\n', 5, 11, 2),
(196, 'espeon', 'Mentali', 65, 65, 60, 130, 95, 110, 133, 'Sa fourrure est si sensible qu’il\r\npeut détecter les vibrations de\r\nl’air et prédire le temps qu’il fera.\r\n', 14, NULL, 2),
(197, 'umbreon', 'Noctali', 95, 65, 110, 60, 130, 65, 133, 'Quand il s’expose à la lumière de la\r\nlune, ses anneaux brillent et il\r\nobtient un mystérieux pouvoir.\r\n', 17, NULL, 2),
(198, 'murkrow', 'Cornèbre', 60, 85, 42, 85, 42, 91, NULL, 'Quand il est en danger, il tente de\r\nsemer ses poursuivants dans les\r\nchemins montagneux.\r\n', 3, 17, 2),
(199, 'slowking', 'Roigada', 95, 75, 80, 100, 110, 30, 79, 'La morsure de Kokiyas a fait de lui\r\nun génie dont l’intelligence rivalise\r\navec celle d’un prix Nobel.\r\n', 11, 14, 2),
(200, 'misdreavus', 'Feuforêve', 60, 60, 60, 85, 85, 85, NULL, 'Il fait peur aux aventuriers en\r\npleine nuit et se nourrit de leur\r\nfrayeur.\r\n', 8, NULL, 2),
(201, 'unown', 'Zarbi', 48, 72, 48, 72, 48, 48, NULL, 'Quand il est seul, il ne se passe\r\nrien... Mais en groupe, il semble\r\nhabité par un mystérieux pouvoir.\r\n', 14, NULL, 2),
(202, 'wobbuffet', 'Qulbutoké', 190, 33, 58, 33, 58, 33, 360, 'Il cherche désespérément à cacher\r\nsa queue noire. Certains pensent\r\nqu’elle renferme un secret.\r\n', 14, NULL, 2),
(203, 'girafarig', 'Girafarig', 70, 80, 65, 90, 65, 85, NULL, 'La tête qu’il a sur la queue dispose\r\nde son propre cerveau et lui\r\npermet de couvrir ses arrières.\r\n', 1, 14, 2),
(204, 'pineco', 'Pomdepik', 50, 65, 90, 35, 35, 15, NULL, 'Il ressemble à une pomme de pin.\r\nSa carapace le protège des Pokémon\r\nOiseaux qui le picorent par erreur.\r\n', 7, NULL, 2),
(205, 'forretress', 'Foretress', 75, 90, 140, 60, 60, 40, 204, 'Il est prisonnier d’une coquille\r\nd’acier. Ses yeux vigilants sont la\r\nseule partie visible de son corps.\r\n', 7, 9, 2),
(206, 'dunsparce', 'Insolourdo', 100, 70, 70, 65, 65, 45, NULL, 'Son nid est un véritable labyrinthe\r\nqu’il creuse à l’aide de sa queue.\r\nIl sait à peine voler.\r\n', 1, NULL, 2),
(207, 'gligar', 'Scorplane', 65, 75, 105, 35, 65, 85, NULL, 'Il fend l’air et fond sur sa proie\r\npour s’agripper à son visage et\r\nla frapper avec son dard.\r\n', 3, 5, 2),
(208, 'steelix', 'Steelix', 75, 85, 200, 55, 65, 30, 95, 'On pense que son corps s’est\r\ntransformé à cause du fer contenu\r\ndans la terre dont il se nourrit.\r\n', 5, 9, 2),
(209, 'snubbull', 'Snubbull', 60, 80, 50, 40, 40, 30, NULL, 'Son visage horrible fait fuir les\r\npetits Pokémon. Par contre, les\r\nfemmes le trouvent mignon.\r\n', 18, NULL, 2),
(210, 'granbull', 'Granbull', 90, 120, 75, 60, 60, 45, 209, 'En dépit des apparences, c’est un\r\ngrand timide. Quand il s’énerve, il\r\nmord de ses énormes crocs.\r\n', 18, NULL, 2),
(211, 'qwilfish', 'Qwilfish', 65, 95, 85, 55, 55, 85, NULL, 'Il envoie ses piquants empoisonnés\r\nà la ronde. Sa forme sphérique en\r\nfait un piètre nageur.\r\n', 4, 11, 2),
(212, 'scizor', 'Cizayox', 70, 130, 100, 55, 80, 65, 123, 'Les yeux sur ses pinces servent à\r\nintimider l’ennemi avant de passer\r\nà l’attaque.\r\n', 7, 9, 2),
(213, 'shuckle', 'Caratroc', 20, 10, 230, 10, 230, 5, NULL, 'Il stocke des Baies sous sa\r\ncarapace. Elles fermentent et\r\nproduisent des jus délicieux.\r\n', 6, 7, 2),
(214, 'heracross', 'Scarhino', 80, 125, 75, 40, 95, 85, NULL, 'Il défend vaillamment sa réserve de\r\nmiel personnelle à grand renfort\r\nde coups de corne.\r\n', 2, 7, 2),
(215, 'sneasel', 'Farfuret', 55, 95, 55, 35, 75, 115, NULL, 'Ce Pokémon rusé agit parfois en\r\néquipe pour voler des œufs, l’un\r\nd’entre eux faisant diversion.\r\n', 15, 17, 2),
(216, 'teddiursa', 'Teddiursa', 60, 80, 50, 50, 50, 40, NULL, 'Il baigne ses griffes dans le miel \r\npour les lécher. Chaque paire\r\nde pattes a un goût unique.\r\n', 1, NULL, 2),
(217, 'ursaring', 'Ursaring', 90, 130, 75, 75, 75, 55, 216, 'Avec sa capacité à sentir toute odeur, il trouve sans\r\nfaillir de la nourriture enfouie dans le sol.\r\n', 1, NULL, 2),
(218, 'slugma', 'Limagma', 40, 40, 40, 70, 40, 20, NULL, 'Son corps est fait de magma.\r\nS’il cesse de bouger, il refroidit\r\net durcit.\r\n', 10, NULL, 2),
(219, 'magcargo', 'Volcaropod', 60, 50, 120, 90, 80, 30, 218, 'Sa température corporelle avoisine\r\nles 10 000 °C. Des flammes fusent\r\ndes fissures de sa coquille.\r\n', 6, 10, 2),
(220, 'swinub', 'Marcacrin', 50, 50, 40, 30, 30, 50, NULL, 'Son nez très sensible détecte\r\nchampignons, Baies et sources\r\nchaudes enfouis sous la glace.\r\n', 5, 15, 2),
(221, 'piloswine', 'Cochignon', 100, 100, 80, 60, 60, 50, 220, 'Il est protégé du froid mordant par\r\nson épais manteau de fourrure. Ses\r\ncornes sont formées de glace.\r\n', 5, 15, 2),
(222, 'corsola', 'Corayon', 65, 55, 95, 65, 95, 35, NULL, 'On en trouve beaucoup dans les mers\r\nclaires du sud. On dirait qu’il ne peut\r\npas vivre en eau polluée.\r\n', 6, 11, 2),
(223, 'remoraid', 'Rémoraid', 35, 65, 35, 65, 35, 65, NULL, 'Son puissant jet d’eau ne manque\r\njamais sa cible, même quand il est\r\nau fond de l’eau.\r\n', 11, NULL, 2),
(224, 'octillery', 'Octillery', 75, 105, 75, 105, 75, 45, 223, 'Il adore se cacher dans les\r\nfissures de la roche. Il crache\r\nparfois de l’encre sur ses proies.\r\n', 11, NULL, 2),
(225, 'delibird', 'Cadoizo', 45, 55, 45, 65, 45, 75, NULL, 'Il dépose sa nourriture dans sa\r\nqueue enroulée. Il l’offre souvent\r\naux gens égarés en montagne.\r\n', 3, 15, 2),
(226, 'mantine', 'Démanta', 85, 40, 70, 80, 140, 70, 458, 'Il ignore royalement les Rémoraid\r\nqui s’accrochent à ses nageoires\r\npour manger les restes qu’il sème.\r\n', 3, 11, 2),
(227, 'skarmory', 'Airmure', 65, 80, 140, 40, 70, 70, NULL, 'Il peut paraître lourd, avec son armure d’acier,\r\nmais il est en fait capable de voler à une vitesse\r\nde 300 km/h.\r\n', 3, 9, 2),
(228, 'houndour', 'Malosse', 45, 60, 30, 80, 50, 65, NULL, 'Il est assez évolué pour chasser\r\nen meute et communiquer par\r\ndes séries de cris modulés.\r\n', 10, 17, 2),
(229, 'houndoom', 'Démolosse', 75, 90, 50, 110, 80, 95, 228, 'En colère, il crache des flammes\r\ncontenant une toxine qui peut\r\ncauser des dégâts irréversibles.\r\n', 10, 17, 2),
(230, 'kingdra', 'Hyporoi', 75, 95, 95, 95, 95, 85, 117, 'Chacun de ses déplacements provoque\r\ndes tourbillons. Il vit dans les grottes\r\nsous-marines.\r\n', 11, 16, 2),
(231, 'phanpy', 'Phanpy', 90, 60, 60, 40, 40, 40, NULL, 'Il est fort malgré son aspect ramassé.\r\nIl n’a aucun mal à porter un humain\r\nadulte sur son dos.\r\n', 5, NULL, 2),
(232, 'donphan', 'Donphan', 90, 120, 120, 60, 60, 50, 231, 'Il attaque en se roulant en boule\r\npour percuter l’ennemi. Il peut\r\nabattre une maison d’un seul coup.\r\n', 5, NULL, 2),
(233, 'porygon2', 'Porygon2', 85, 80, 90, 105, 95, 60, 137, 'Il a subi une mise à jour censée lui\r\npermettre d’explorer d’autres\r\nplanètes... C’est un échec.\r\n', 1, NULL, 2),
(234, 'stantler', 'Cerfrousse', 73, 95, 62, 85, 65, 85, NULL, 'Quiconque observe ses antennes\r\nressent une sensation étrange,\r\ncomme si leur centre l’attirait.\r\n', 1, NULL, 2),
(235, 'smeargle', 'Queulorior', 55, 20, 35, 20, 45, 75, NULL, 'Il marque son territoire en maniant\r\nsa queue comme un pinceau. On\r\ndénombre plus de 5 000 marques.\r\n', 1, NULL, 2),
(236, 'tyrogue', 'Debugant', 35, 35, 35, 35, 35, 35, NULL, 'Il est réputé pour sa combativité.\r\nIl se blesse toujours en défiant\r\ndes ennemis plus grands.\r\n', 2, NULL, 2),
(237, 'hitmontop', 'Kapoera', 50, 95, 95, 35, 110, 70, 236, 'Il combat en tournoyant comme une\r\ntoupie. La force centrifuge décuple\r\nsa puissance destructrice.\r\n', 2, NULL, 2),
(238, 'smoochum', 'Lippouti', 45, 30, 15, 85, 65, 65, NULL, 'Il goûte à tout du bout des lèvres.\r\nElles gardent en mémoire ses\r\ngoûts et ses dégoûts.\r\n', 14, 15, 2),
(239, 'elekid', 'Élekid', 45, 63, 37, 65, 55, 95, NULL, 'Un faible courant électrique circule\r\nentre ses cornes. Attention à ne\r\npas y mettre la main!\r\n', 13, NULL, 2),
(240, 'magby', 'Magby', 45, 75, 37, 70, 55, 83, NULL, 'Son sang est chaud comme du magma\r\net la température de son corps\r\npeut atteindre 600 °C.\r\n', 10, NULL, 2),
(241, 'miltank', 'Écrémeuh', 95, 80, 105, 40, 70, 100, NULL, 'On raconte que les enfants élevés\r\nau lait d’Écrémeuh deviennent des\r\nadultes braves et robustes.\r\n', 1, NULL, 2),
(242, 'blissey', 'Leuphorie', 255, 10, 10, 75, 135, 55, 113, 'Les œufs que pond Leuphorie sont\r\nun concentré de bonheur dont une\r\nbouchée suffit à apporter la joie.\r\n', 1, NULL, 2),
(243, 'raikou', 'Raikou', 90, 85, 75, 115, 100, 115, NULL, 'On prétend qu’il est tombé avec la\r\nfoudre. Il peut lancer des éclairs\r\ngrâce aux nuages sur son dos.\r\n', 13, NULL, 2),
(244, 'entei', 'Entei', 115, 115, 85, 90, 75, 100, NULL, 'On raconte qu’à chaque fois qu’il\r\nrugit, un volcan entre en éruption\r\nquelque part.\r\n', 10, NULL, 2),
(245, 'suicune', 'Suicune', 100, 75, 115, 90, 115, 85, NULL, 'Il parcourt le vaste monde pour\r\npurifier les eaux polluées. Il\r\naccompagne le vent du nord.\r\n', 11, NULL, 2),
(246, 'larvitar', 'Embrylex', 50, 64, 50, 45, 50, 41, NULL, 'Un Pokémon qui se nourrit de terre.\r\nAprès avoir dévoré une montagne,\r\nil s’endort pour grandir.\r\n', 5, 6, 2),
(247, 'pupitar', 'Ymphect', 70, 84, 70, 65, 70, 51, 246, 'Son corps est dur comme la pierre\r\nsouterraine. Il file comme une\r\nfusée en expulsant des gaz.\r\n', 5, 6, 2),
(248, 'tyranitar', 'Tyranocif', 100, 134, 110, 95, 100, 61, 247, 'Lorsqu’il est en colère, il abat des\r\nmontagnes et enterre des fleuves.\r\nOn doit alors modifier les cartes.\r\n', 6, 17, 2),
(249, 'lugia', 'Lugia', 106, 90, 130, 90, 154, 110, NULL, 'Il dort dans une faille des grands\r\nfonds. Ses battements d’ailes\r\ngénèrent une tempête de 40 jours.\r\n', 3, 14, 2),
(250, 'ho-oh', 'Ho-Oh', 106, 130, 90, 110, 154, 90, NULL, 'Ses plumes possèdent sept couleurs.\r\nOn prétend que sa vue apporte la\r\njoie éternelle.\r\n', 3, 10, 2),
(251, 'celebi', 'Celebi', 100, 100, 100, 100, 100, 100, NULL, 'Il a le pouvoir de voyager dans le\r\ntemps. Cependant, on dit qu’il\r\nn’apparaît qu’en temps de paix.\r\n', 12, 14, 2),
(252, 'treecko', 'Arcko', 40, 45, 35, 65, 55, 70, NULL, 'Ses plantes de pieds sont couvertes\r\nd’épines lui permettant de marcher\r\nau mur et au plafond.\r\n', 12, NULL, 3),
(253, 'grovyle', 'Massko', 50, 65, 45, 85, 65, 95, 252, 'Il vit dans les jungles denses.\r\nIl saute de branche en branche\r\npour approcher de sa proie.\r\n', 12, NULL, 3),
(254, 'sceptile', 'Jungko', 70, 85, 65, 105, 85, 120, 253, 'Les feuilles sur ses bras peuvent\r\ntrancher des troncs épais. C’est\r\nle meilleur combattant des jungles.\r\n', 12, NULL, 3),
(255, 'torchic', 'Poussifeu', 45, 60, 40, 70, 50, 45, NULL, 'Ses câlins réchauffent car il renferme\r\nune fournaise. Il envoie des boules de\r\nfeu à 1 000 °C.\r\n', 10, NULL, 3),
(256, 'combusken', 'Galifeu', 60, 85, 60, 85, 60, 55, 255, 'Ce maître du coup de pied peut donner\r\n10 coups par seconde. Ses cris aigus\r\nintimident l’ennemi.\r\n', 2, 10, 3),
(257, 'blaziken', 'Braségali', 80, 120, 70, 110, 70, 80, 256, 'Des flammes fusent de ses\r\npoignets et ses coups brûlent\r\nl’ennemi.\r\n', 2, 10, 3),
(258, 'mudkip', 'Gobou', 50, 70, 50, 50, 50, 40, NULL, 'Il analyse le courant grâce à la\r\nnageoire sur sa tête. Il est assez\r\nfort pour soulever des rochers.\r\n', 11, NULL, 3),
(259, 'marshtomp', 'Flobio', 70, 85, 70, 60, 70, 50, 258, 'Ses jambes robustes lui donnent\r\nun bon appui, même dans la boue.\r\nIl dort enfoui dans la saleté.\r\n', 5, 11, 3),
(260, 'swampert', 'Laggron', 100, 110, 90, 85, 90, 60, 259, 'Il peut traîner un grand bateau en\r\nnageant. Il assomme l’ennemi d’un\r\ngrand coup de ses bras épais.\r\n', 5, 11, 3),
(261, 'poochyena', 'Medhyèna', 35, 55, 35, 30, 30, 35, NULL, 'Un Pokémon très obstiné qui\r\npoursuit sa proie jusqu’à ce\r\nqu’elle soit épuisée.\r\n', 17, NULL, 3),
(262, 'mightyena', 'Grahyèna', 70, 90, 70, 60, 60, 70, 261, 'Les Grahyèna forment des meutes de dix qui\r\nexcellent au travail de groupe pour capturer leurs\r\nproies.\r\n', 17, NULL, 3),
(263, 'zigzagoon', 'Zigzaton', 38, 30, 41, 30, 41, 60, NULL, 'Il marche en zigzaguant et n’a\r\npas son pareil pour trouver des\r\nobjets dans l’herbe ou sous terre.\r\n', 1, NULL, 3),
(264, 'linoone', 'Linéon', 78, 70, 61, 50, 61, 100, 263, 'Il fonce sur sa proie à plus de 100 km/h.\r\nMais il ne peut pas dévier sa course\r\net loupe souvent sa cible.\r\n', 1, NULL, 3),
(265, 'wurmple', 'Chenipotte', 45, 45, 35, 20, 30, 20, NULL, 'Cible favorite des Pokémon Oiseaux,\r\nil tente de se protéger avec ses\r\népines venimeuses.\r\n', 7, NULL, 3),
(266, 'silcoon', 'Armulys', 50, 35, 55, 25, 25, 15, 265, 'Il attend d’évoluer dans son cocon\r\nde soie accroché aux branches et\r\nse nourrit d’eau de pluie.\r\n', 7, NULL, 3),
(267, 'beautifly', 'Charmillon', 60, 70, 50, 100, 50, 65, 266, 'Malgré les apparences, il est très\r\nagressif et attaque avec sa trompe\r\nquand on le dérange.\r\n', 3, 7, 3),
(268, 'cascoon', 'Blindalys', 50, 35, 55, 25, 25, 15, 265, 'Rancunier, il n’oublie aucune des\r\nattaques subies dans le cocon et\r\nse venge une fois qu’il a évolué.\r\n', 7, NULL, 3),
(269, 'dustox', 'Papinox', 60, 50, 70, 50, 90, 65, 268, 'Il disperse un nuage de poudre\r\ntoxique à chaque coup d’aile. Il\r\naime les feuilles d’arbres urbains.\r\n', 4, 7, 3),
(270, 'lotad', 'Nénupiot', 40, 30, 30, 40, 50, 30, NULL, 'Il ressemble à une plante aquatique\r\net fait la navette pour les Pokémon\r\nqui ne savent pas nager.\r\n', 11, 12, 3),
(271, 'lombre', 'Lombre', 60, 50, 50, 60, 70, 50, 270, 'C’est un mauvais plaisantin. Quand\r\nil repère un pêcheur, il tire sur sa\r\nligne pour le déranger.\r\n', 11, 12, 3),
(272, 'ludicolo', 'Ludicolo', 80, 70, 70, 90, 100, 70, 271, 'Ses muscles se tonifient au son\r\nd’une musique entraînante. Il ne\r\npeut pas s’empêcher de danser.\r\n', 11, 12, 3),
(273, 'seedot', 'Grainipiot', 40, 40, 50, 30, 30, 30, NULL, 'Il ressemble à un gland pendu à\r\nune branche. Il adore effrayer\r\nles autres Pokémon.\r\n', 12, NULL, 3),
(274, 'nuzleaf', 'Pifeuil', 70, 70, 40, 60, 40, 60, 273, 'Le son de sa flûte d’herbe\r\ndéstabilise l’auditeur. Il vit au \r\nplus profond des forêts.\r\n', 12, 17, 3),
(275, 'shiftry', 'Tengalice', 90, 100, 60, 90, 60, 80, 274, 'D’un coup de sa feuille éventail, il\r\ngénère des bourrasques de 30 m/s\r\ncapables de souffler une maison.\r\n', 12, 17, 3),
(276, 'taillow', 'Nirondelle', 40, 55, 30, 30, 30, 85, NULL, 'Ce Pokémon téméraire n’a pas peur\r\nd’affronter des ennemis puissants.\r\nIl vole en quête de climats chauds.\r\n', 1, 3, 3),
(277, 'swellow', 'Hélédelle', 60, 85, 60, 75, 50, 125, 276, 'Il vole en cercle en quête d’une\r\nproie. Dès qu’il l’a repérée, il fond\r\nsur elle en un éclair.\r\n', 1, 3, 3),
(278, 'wingull', 'Goélise', 40, 30, 30, 55, 30, 85, NULL, 'Il utilise les courants ascendants\r\npour planer en altitude. Son bec lui\r\nsert à transporter la nourriture.\r\n', 3, 11, 3),
(279, 'pelipper', 'Bekipan', 60, 50, 100, 95, 70, 65, 278, 'Ce messager du ciel transporte\r\npetits Pokémon et œufs dans son\r\nbec en toute sécurité.\r\n', 3, 11, 3),
(280, 'ralts', 'Tarsal', 28, 25, 25, 45, 35, 40, NULL, 'Quand ses cornes captent les ondes\r\npositives d’un Pokémon ou d’un\r\nhumain, son corps se réchauffe.\r\n', 14, 18, 3),
(281, 'kirlia', 'Kirlia', 38, 35, 35, 65, 55, 50, 280, 'Quand il sent la joie de son\r\nDresseur, il se met à danser,\r\ndébordant d’énergie.\r\n', 14, 18, 3),
(282, 'gardevoir', 'Gardevoir', 68, 65, 65, 125, 115, 80, 281, 'Prêt à tout pour protéger son\r\nDresseur, il peut créer un mini trou\r\nnoir par sa seule force mentale.\r\n', 14, 18, 3);
INSERT INTO `pokemon_pokemon` (`id`, `name`, `nom`, `pv`, `attaque`, `defense`, `attaque_speciale`, `defense_speciale`, `vitesse`, `evolution_de`, `description`, `type_1`, `type_2`, `gen_id`) VALUES
(283, 'surskit', 'Arakdo', 40, 30, 32, 50, 52, 65, NULL, 'Il semble patiner sur l’eau. Le parfum\r\ndiffusé par le sommet de sa tête sert\r\nà attirer ses proies.\r\n', 7, 11, 3),
(284, 'masquerain', 'Maskadra', 70, 60, 62, 100, 82, 80, 283, 'Des motifs en forme d’yeux ornent\r\nses antennes. Ses 4 ailes l’aident\r\nà voler dans toutes les directions.\r\n', 3, 7, 3),
(285, 'shroomish', 'Balignon', 60, 40, 60, 40, 60, 35, NULL, 'Le sommet de sa tête libère des\r\nspores empoisonnées. Il est\r\ndangereux de les inhaler.\r\n', 12, NULL, 3),
(286, 'breloom', 'Chapignon', 60, 130, 80, 60, 60, 70, 285, 'Ses petits bras s’étendent quand il\r\ndonne un coup de poing. Son talent\r\négale celui d’un boxeur pro.\r\n', 2, 12, 3),
(287, 'slakoth', 'Parecool', 60, 60, 60, 35, 35, 30, NULL, 'Il passe le plus clair de son temps\r\naffalé. Rien qu’à le voir, on a\r\nenvie de bâiller.\r\n', 1, NULL, 3),
(288, 'vigoroth', 'Vigoroth', 80, 80, 80, 55, 55, 90, 287, 'Son cœur bat dix fois plus vite que\r\nla normale, c’est pourquoi il ne\r\ntient pas en place.\r\n', 1, NULL, 3),
(289, 'slaking', 'Monaflèmit', 150, 160, 100, 95, 65, 100, 288, 'Le Pokémon le plus fainéant du\r\nmonde. Il s’allonge pour économiser\r\nses forces et contre-attaquer.\r\n', 1, NULL, 3),
(290, 'nincada', 'Ningale', 31, 45, 90, 30, 30, 40, NULL, 'Il grandit sous terre et se repère\r\ngrâce à ses antennes car ses\r\nyeux sont quasiment aveugles.\r\n', 5, 7, 3),
(291, 'ninjask', 'Ninjask', 61, 90, 45, 50, 50, 160, 290, 'Il bouge si vite que l’œil a parfois du\r\nmal à le suivre. Ils se regroupent près\r\nde la sève des arbres.\r\n', 3, 7, 3),
(292, 'shedinja', 'Munja', 1, 90, 45, 30, 30, 40, 290, 'Une carapace d’insecte laissée à\r\nl’abandon. On dit qu’il vole l’âme\r\nde celui qui regarde à l’intérieur.\r\n', 7, 8, 3),
(293, 'whismur', 'Chuchmur', 64, 51, 23, 51, 23, 28, NULL, 'En temps normal, son cri est pareil\r\nà un murmure. Effrayé, il atteint le\r\nvolume d’un avion à réaction.\r\n', 1, NULL, 3),
(294, 'loudred', 'Ramboum', 84, 71, 43, 71, 43, 48, 293, 'L’onde de choc provoquée par son\r\ncri peut renverser un camion. Il\r\ntrépigne pour augmenter sa force.\r\n', 1, NULL, 3),
(295, 'exploud', 'Brouhabam', 104, 91, 63, 91, 73, 68, 294, 'Son hurlement s’entend à 10 km à la\r\nronde. Ses orifices émettent toutes\r\nsortes de bruits.\r\n', 1, NULL, 3),
(296, 'makuhita', 'Makuhita', 72, 60, 30, 20, 30, 25, NULL, 'Il fortifie son corps en fonçant\r\nsur des arbres épais. Son nid est\r\nentouré d’arbres abattus.\r\n', 2, NULL, 3),
(297, 'hariyama', 'Hariyama', 144, 120, 60, 40, 60, 50, 296, 'Il aime mesurer sa force à celle des\r\nplus grands Pokémon. Ses coups de\r\npaume peuvent projeter un camion.\r\n', 2, NULL, 3),
(298, 'azurill', 'Azurill', 50, 20, 40, 20, 40, 20, NULL, 'Sa queue est plus rebondissante\r\nqu’une balle du même nom. C’est\r\naussi une massue très puissante.\r\n', 1, 18, 3),
(299, 'nosepass', 'Tarinor', 30, 45, 135, 45, 90, 30, NULL, 'Face au danger, il se protège en\r\naugmentant son magnétisme et en\r\nattirant tout objet métallique.\r\n', 6, NULL, 3),
(300, 'skitty', 'Skitty', 50, 45, 45, 35, 35, 50, NULL, 'Il poursuit tout ce qui bouge. On\r\nle voit parfois courir en cercle,\r\nchassant sa propre queue.\r\n', 1, NULL, 3),
(301, 'delcatty', 'Delcatty', 70, 65, 65, 55, 55, 90, 300, 'Les femmes Dresseurs raffolent\r\nde sa sublime fourrure. Il ne\r\npossède pas de nid.\r\n', 1, NULL, 3),
(302, 'sableye', 'Ténéfix', 50, 75, 75, 65, 65, 50, NULL, 'À force de manger des gemmes, les\r\nyeux de ce Pokémon des grottes\r\nobscures sont devenus des joyaux.\r\n', 8, 17, 3),
(303, 'mawile', 'Mysdibule', 50, 85, 85, 55, 55, 50, NULL, 'Ses cornes forment une grande\r\nmâchoire attachée à sa tête. Elle\r\npeut broyer des barres de fer.\r\n', 9, 18, 3),
(304, 'aron', 'Galekid', 50, 70, 100, 40, 40, 30, NULL, 'Il vit habituellement au cœur des\r\nmontagnes. Affamé, il dévore\r\nparfois des rails et des voitures.\r\n', 6, 9, 3),
(305, 'lairon', 'Galegon', 60, 90, 140, 50, 50, 40, 304, 'Il raffole du minerai de fer. Il défend son territoire\r\nen chargeant avec son corps d’acier.\r\n', 6, 9, 3),
(306, 'aggron', 'Galeking', 70, 110, 180, 60, 60, 50, 305, 'Il creuse des galeries à travers la\r\npierre avec ses cornes d’acier pour\r\ntrouver le fer qu’il apprécie.\r\n', 6, 9, 3),
(307, 'meditite', 'Méditikka', 30, 40, 55, 40, 55, 60, NULL, 'Son entraînement d’ermite lui\r\npermet de léviter par le seul\r\npouvoir de la méditation.\r\n', 2, 14, 3),
(308, 'medicham', 'Charmina', 60, 60, 75, 60, 75, 80, 307, 'Le jeûne lui permet de concentrer\r\nson esprit au point de déceler\r\nl’aura de ses adversaires.\r\n', 2, 14, 3),
(309, 'electrike', 'Dynavolt', 40, 45, 40, 65, 40, 65, NULL, 'Il améliore ses réflexes en\r\nstimulant ses muscles grâce à \r\nl’électricité dans sa fourrure.\r\n', 13, NULL, 3),
(310, 'manectric', 'Élecsprint', 70, 75, 60, 105, 60, 105, 309, 'Il libère l’électricité par sa crinière.\r\nIl crée un nuage d’orage pour appeler\r\nla foudre.\r\n', 13, NULL, 3),
(311, 'plusle', 'Posipi', 60, 50, 40, 85, 75, 95, NULL, 'Il absorbe l’énergie des poteaux\r\nélectriques et encourage ses amis\r\navec des pompons d’étincelles.\r\n', 13, NULL, 3),
(312, 'minun', 'Négapi', 60, 40, 50, 75, 85, 95, NULL, 'Il encourage ses amis. Son corps\r\nproduit plus d’étincelles quand il\r\nest en mauvaise posture.\r\n', 13, NULL, 3),
(313, 'volbeat', 'Muciole', 65, 73, 75, 47, 85, 85, NULL, 'Il communique avec ses pairs en\r\nallumant son postérieur la nuit. Il\r\nadore le doux parfum de Lumivole.\r\n', 7, NULL, 3),
(314, 'illumise', 'Lumivole', 65, 47, 75, 73, 85, 85, NULL, 'Il utilise son doux parfum pour aider\r\nMuciole à décrire des arabesques\r\ndans le ciel nocturne.\r\n', 7, NULL, 3),
(315, 'roselia', 'Rosélia', 50, 60, 45, 100, 80, 65, 406, 'Plus le Rosélia est en bonne santé\r\net plus son parfum est agréable et\r\nrelaxant pour son entourage.\r\n', 4, 12, 3),
(316, 'gulpin', 'Gloupti', 70, 43, 53, 43, 53, 40, NULL, 'Il est principalement composé d’un\r\nestomac dont les sucs digestifs\r\ndissolvent tout ce qu’il avale.\r\n', 4, NULL, 3),
(317, 'swalot', 'Avaltout', 100, 73, 83, 73, 83, 55, 316, 'Il avale tout en une bouchée. Ses\r\nfollicules sécrètent des fluides\r\ntoxiques qui endorment l’ennemi.\r\n', 4, NULL, 3),
(318, 'carvanha', 'Carvanha', 45, 90, 20, 65, 20, 65, NULL, 'Il attaque en banc quand on menace\r\nson territoire. Ses dents aiguisées\r\npeuvent couler un navire.\r\n', 11, 17, 3),
(319, 'sharpedo', 'Sharpedo', 70, 120, 40, 95, 40, 95, 318, 'Ses crocs lacèrent des feuilles\r\nd’acier. Il nage à 120 km/h. On\r\nl’appelle “tyran des mers”.\r\n', 11, 17, 3),
(320, 'wailmer', 'Wailmer', 130, 70, 35, 70, 35, 60, NULL, 'Par beau temps, il se pose sur les\r\nplages pour jouer et rebondir. Il\r\nexpulse de l’eau par son évent.\r\n', 11, NULL, 3),
(321, 'wailord', 'Wailord', 170, 90, 45, 90, 45, 60, 320, 'Le plus grand des Pokémon. Il peut\r\nplonger à une profondeur de \r\n3 000 m en retenant son souffle.\r\n', 11, NULL, 3),
(322, 'numel', 'Chamallot', 60, 60, 40, 65, 45, 35, NULL, 'Son dos bossu renferme une lave\r\nbouillonnante. Elle refroidit par \r\ntemps de pluie, ce qui le ralentit.\r\n', 5, 10, 3),
(323, 'camerupt', 'Camérupt', 70, 100, 70, 105, 75, 40, 322, 'Il porte des volcans sur son dos.\r\nQuand le magma remonte, il\r\nfrissonne et entre en éruption.\r\n', 5, 10, 3),
(324, 'torkoal', 'Chartor', 70, 85, 140, 85, 70, 20, NULL, 'Il tire son énergie du charbon qu’il\r\nbrûle dans sa carapace et crache\r\nde la suie noire en cas de danger.\r\n', 10, NULL, 3),
(325, 'spoink', 'Spoink', 60, 25, 35, 70, 80, 60, NULL, 'Il utilise sa queue comme un ressort\r\net bondit partout. Chaque nouveau\r\nchoc fait battre son cœur.\r\n', 14, NULL, 3),
(326, 'grumpig', 'Groret', 80, 45, 65, 90, 110, 80, 325, 'Les perles noires amplifient ses\r\npouvoirs psy. Sa danse étrange lui\r\npermet de contrôler ses ennemis.\r\n', 14, NULL, 3),
(327, 'spinda', 'Spinda', 60, 60, 60, 60, 60, 60, NULL, 'Les motifs sur leur fourrure sont\r\npropres à chaque Spinda. Son pas\r\nchancelant le rend difficile à viser.\r\n', 1, NULL, 3),
(328, 'trapinch', 'Kraknoix', 45, 100, 45, 45, 45, 10, NULL, 'Il creuse un trou conique dans le\r\nsable du désert et se terre en\r\nattendant qu’une proie trébuche.\r\n', 5, NULL, 3),
(329, 'vibrava', 'Vibraninf', 50, 70, 50, 50, 50, 70, 328, 'Ses violents battements d’ailes\r\ngénèrent des vagues d’ultrasons\r\nqui donnent mal au crâne.\r\n', 5, 16, 3),
(330, 'flygon', 'Libégon', 80, 100, 80, 80, 80, 100, 329, 'Son puissant battement d’ailes\r\nprovoque des tempêtes de sable.\r\nOn l’appelle “esprit du désert”.\r\n', 5, 16, 3),
(331, 'cacnea', 'Cacnea', 50, 85, 40, 85, 40, 35, NULL, 'En stockant l’eau dans son corps,\r\ncet habitant du désert peut \r\nsurvivre 30 jours sans boire.\r\n', 12, NULL, 3),
(332, 'cacturne', 'Cacturne', 70, 115, 60, 115, 60, 55, 331, 'Il sort la nuit pour chasser. Il\r\ntraque les proies épuisées après\r\nune journée dans le désert.\r\n', 12, 17, 3),
(333, 'swablu', 'Tylton', 45, 40, 60, 40, 75, 50, NULL, 'Il ne se sent à l’aise que dans un\r\nmilieu propre et repousse la\r\npoussière avec ses ailes.\r\n', 1, 3, 3),
(334, 'altaria', 'Altaria', 75, 70, 90, 70, 105, 80, 333, 'Il aime enlacer ses amis dans ses\r\nailes cotonneuses et ronronner.\r\n', 3, 16, 3),
(335, 'zangoose', 'Mangriff', 73, 115, 60, 60, 60, 90, NULL, 'C’est le rival de Séviper depuis\r\ndes générations. Ses griffes\r\naiguisées sont son meilleur atout.\r\n', 1, NULL, 3),
(336, 'seviper', 'Séviper', 73, 100, 60, 100, 60, 65, NULL, 'Le rival historique de Mangriff.\r\nIl affûte la lame de sa queue \r\nsur les rochers avant le combat.\r\n', 4, NULL, 3),
(337, 'lunatone', 'Séléroc', 90, 55, 65, 95, 85, 70, NULL, 'On suppose qu’il est lié au cycle\r\nlunaire, car il ne sort que les\r\nsoirs de pleine lune.\r\n', 6, 14, 3),
(338, 'solrock', 'Solaroc', 90, 95, 85, 55, 65, 70, NULL, 'Le bruit court que cette nouvelle\r\nespèce vient du soleil. Il produit\r\nde la lumière en tournoyant.\r\n', 6, 14, 3),
(339, 'barboach', 'Barloche', 50, 48, 43, 46, 41, 60, NULL, 'Son corps glissant est difficile à\r\nattraper. On dit qu’il a été créé\r\ndans de la boue solidifiée.\r\n', 5, 11, 3),
(340, 'whiscash', 'Barbicha', 110, 78, 73, 76, 71, 60, 339, 'Il défend férocement son territoire\r\nen déclenchant des secousses\r\nterribles.\r\n', 5, 11, 3),
(341, 'corphish', 'Écrapince', 43, 80, 65, 50, 35, 35, NULL, 'Sa nature robuste lui permet de\r\nvivre sur n’importe quel terrain. \r\nSes pinces ne lâchent jamais prise.\r\n', 11, NULL, 3),
(342, 'crawdaunt', 'Colhomard', 63, 120, 85, 90, 55, 55, 341, 'C’est un voyou qui utilise ses\r\npinces pour chasser les autres\r\nPokémon hors de sa mare.\r\n', 11, 17, 3),
(343, 'baltoy', 'Balbuto', 40, 40, 55, 40, 70, 55, NULL, 'Il se déplace en tournoyant sur\r\nson pied. Ce Pokémon rare fut\r\ndécouvert dans de vieilles ruines.\r\n', 5, 14, 3),
(344, 'claydol', 'Kaorine', 60, 70, 105, 70, 120, 75, 343, 'Une ancienne figurine d’argile qui \r\ns’est animée suite à son exposition\r\nà un rayon de lumière étrange.\r\n', 5, 14, 3),
(345, 'lileep', 'Lilia', 66, 41, 77, 61, 87, 23, NULL, 'Il vivait au fond de la mer il y a\r\n100 millions d’années. La science\r\na permis de le ressusciter.\r\n', 6, 12, 3),
(346, 'cradily', 'Vacilys', 86, 81, 97, 81, 107, 43, 345, 'Vit dans les hauts-fonds des mers\r\nchaudes. Il déterre sa proie du\r\nsable quand la marée se retire.\r\n', 6, 12, 3),
(347, 'anorith', 'Anorith', 45, 95, 50, 40, 50, 75, NULL, 'Un ancêtre Pokémon réanimé à partir\r\nd’un fossile. Il vivait en mer et\r\nchassait avec ses pinces.\r\n', 6, 7, 3),
(348, 'armaldo', 'Armaldo', 75, 125, 100, 70, 80, 45, 347, 'Il a gagné la côte après son\r\névolution. Une armure robuste\r\nrecouvre son corps.\r\n', 6, 7, 3),
(349, 'feebas', 'Barpau', 20, 15, 20, 10, 55, 80, NULL, 'Ce Pokémon a peut-être un physique\r\ndisgracieux, mais il est robuste et\r\npeut survivre dans très peu d’eau.\r\n', 11, NULL, 3),
(350, 'milotic', 'Milobellus', 95, 60, 79, 100, 125, 81, 349, 'Ses splendides écailles aux couleurs\r\nde l’arc-en-ciel changent de teinte\r\nselon l’angle de vue.\r\n', 11, NULL, 3),
(351, 'castform', 'Morphéo', 70, 70, 70, 70, 70, 70, NULL, 'Son aspect change avec la météo.\r\nOn a récemment découvert que ses\r\nmolécules étaient pareilles à l’eau.\r\n', 1, NULL, 3),
(352, 'kecleon', 'Kecleon', 60, 90, 70, 60, 120, 40, NULL, 'Il change de couleur à volonté,\r\nmais le motif en zigzag qui orne\r\nson ventre reste le même.\r\n', 1, NULL, 3),
(353, 'shuppet', 'Polichombr', 44, 75, 35, 63, 33, 45, NULL, 'Il aime le goût de la vengeance.\r\nIls vivent en rangs sous les\r\ngouttières des gens vindicatifs.\r\n', 8, NULL, 3),
(354, 'banette', 'Branette', 64, 115, 65, 83, 63, 65, 353, 'Une poupée mue par la tristesse qui\r\nla ronge depuis qu’on l’a délaissée.\r\nElle recherche son propriétaire.\r\n', 8, NULL, 3),
(355, 'duskull', 'Skelénox', 20, 40, 90, 30, 90, 25, NULL, 'Les cris des enfants l’amusent.\r\nIl effraie les moins sages d’entre\r\neux en traversant les murs.\r\n', 8, NULL, 3),
(356, 'dusclops', 'Téraclope', 40, 70, 130, 60, 130, 25, 355, 'Il cherche des feux follets pour\r\nles avaler. Ce qui se passe alors\r\ndans son corps est un mystère.\r\n', 8, NULL, 3),
(357, 'tropius', 'Tropius', 99, 68, 83, 72, 87, 51, NULL, 'À force de toujours manger le même\r\nfruit succulent, il en pousse à la\r\nbase de son cou.\r\n', 3, 12, 3),
(358, 'chimecho', 'Éoko', 75, 50, 80, 95, 90, 65, 433, 'Ses cris se répercutent dans son\r\ncorps creux avant d’exploser en\r\nnotes mélodieuses et saisissantes.\r\n', 14, NULL, 3),
(359, 'absol', 'Absol', 65, 130, 60, 75, 60, 75, NULL, 'La capacité de sa corne à sentir\r\nles catastrophes ayant trop attiré\r\nl’attention sur lui, il vit reclus.\r\n', 17, NULL, 3),
(360, 'wynaut', 'Okéoké', 95, 23, 48, 23, 48, 23, NULL, 'Il se muscle en bousculant ses\r\nsemblables lors de mêlées. Il\r\nraffole des fruits sucrés.\r\n', 14, NULL, 3),
(361, 'snorunt', 'Stalgamin', 50, 50, 50, 50, 50, 50, NULL, 'On dit que des groupes entiers de\r\nStalgamin vivent en harmonie\r\nsous des feuilles gigantesques.\r\n', 15, NULL, 3),
(362, 'glalie', 'Oniglali', 80, 80, 80, 80, 80, 80, 361, 'Il empêche ses proies de prendre\r\nla fuite en gelant instantanément\r\nl’humidité de l’air.\r\n', 15, NULL, 3),
(363, 'spheal', 'Obalie', 70, 40, 50, 55, 50, 25, NULL, 'Il gagne la côte en roulant sur les\r\nblocs de glace car sa morphologie\r\nn’est pas adaptée à la nage.\r\n', 11, 15, 3),
(364, 'sealeo', 'Phogleur', 90, 60, 70, 75, 70, 45, 363, 'Il fait souvent tourner des objets\r\nsur son museau pour connaître\r\nleur odeur et leur texture.\r\n', 11, 15, 3),
(365, 'walrein', 'Kaimorse', 110, 80, 90, 95, 90, 65, 364, 'Il brise la glace avec ses grosses\r\ndéfenses. Sa graisse le protège du\r\nfroid, mais aussi des attaques.\r\n', 11, 15, 3),
(366, 'clamperl', 'Coquiperl', 35, 64, 85, 74, 55, 32, NULL, 'Il ne produit qu’une perle durant\r\nson existence. On raconte qu’elle\r\namplifie les pouvoirs psychiques.\r\n', 11, NULL, 3),
(367, 'huntail', 'Serpang', 55, 104, 105, 94, 75, 52, 366, 'Il vit loin sous la mer. Il appâte\r\nles proies crédules avec sa queue\r\nqui rappelle un petit poisson.\r\n', 11, NULL, 3),
(368, 'gorebyss', 'Rosabyss', 55, 84, 105, 114, 75, 52, 366, 'Au printemps, son corps rose se\r\nfait plus vif sans que l’on sache\r\npourquoi. Il vit au fond de la mer.\r\n', 11, NULL, 3),
(369, 'relicanth', 'Relicanth', 100, 90, 130, 45, 65, 55, NULL, 'Un Pokémon rare aperçu lors d’une\r\nexploration sous-marine. Il n’a pas\r\névolué depuis 100 millions d’années.\r\n', 6, 11, 3),
(370, 'luvdisc', 'Lovdisc', 43, 30, 55, 40, 65, 97, NULL, 'Un habitant des mers chaudes. On\r\ndit que le couple qui tombe sur ce\r\nPokémon connaîtra l’amour éternel.\r\n', 11, NULL, 3),
(371, 'bagon', 'Draby', 45, 75, 60, 40, 30, 50, NULL, 'Il rêve de pouvoir voler un jour.\r\nPour l’heure, il s’entraîne en\r\nsautant du haut des falaises.\r\n', 16, NULL, 3),
(372, 'shelgon', 'Drackhaus', 65, 95, 100, 60, 50, 50, 371, 'Ses cellules ont commencé à changer\r\ndans sa robuste coquille. Il la perd\r\nen évoluant.\r\n', 16, NULL, 3),
(373, 'salamence', 'Drattak', 95, 135, 80, 110, 80, 100, 372, 'À force de rêver de pouvoir voler,\r\nsa structure cellulaire a évolué\r\net il s’est vu pousser des ailes.\r\n', 3, 16, 3),
(374, 'beldum', 'Terhal', 40, 55, 80, 35, 60, 30, NULL, 'Il dialogue avec les siens par \r\nimpulsions magnétiques. En bande,\r\nils se déplacent à l’unisson.\r\n', 9, 14, 3),
(375, 'metang', 'Métang', 60, 75, 100, 55, 80, 50, 374, 'Il est formé de deux Terhal\r\nsoudés. Son corps de fer résiste à\r\nun choc avec un avion à réaction.\r\n', 9, 14, 3),
(376, 'metagross', 'Métalosse', 80, 135, 130, 95, 90, 70, 375, 'Il est né de la fusion de plusieurs\r\nMétang. Ses quatre cerveaux en\r\nfont l’égal d’un superordinateur.\r\n', 9, 14, 3),
(377, 'regirock', 'Regirock', 80, 100, 200, 50, 100, 50, NULL, 'Tout son corps est fait de roche.\r\nS’il s’ébrèche au combat, il utilise\r\ndes pierres pour le réparer.\r\n', 6, NULL, 3),
(378, 'regice', 'Regice', 80, 50, 100, 100, 200, 50, NULL, 'Son corps est fait de glace datant\r\nde l’ère glaciaire. Il contrôle un\r\nair gelé de -200 °C.\r\n', 15, NULL, 3),
(379, 'registeel', 'Registeel', 80, 75, 150, 75, 150, 50, NULL, 'Son corps invulnérable fut forgé\r\npar la pression souterraine durant\r\ndes dizaines de milliers d’années.\r\n', 9, NULL, 3),
(380, 'latias', 'Latias', 80, 80, 90, 110, 130, 110, NULL, 'Son corps est recouvert d’un \r\nduvet qui reflète la lumière\r\net le rend invisible.\r\n', 14, 16, 3),
(381, 'latios', 'Latios', 80, 90, 80, 130, 110, 110, NULL, 'Un Pokémon très intelligent. Il\r\npeut voler plus vite qu’un avion à\r\nréaction en repliant ses ailes.\r\n', 14, 16, 3),
(382, 'kyogre', 'Kyogre', 100, 100, 90, 150, 140, 90, NULL, 'On dit qu’il a fait monter les eaux\r\nen causant des pluies diluviennes.\r\nIl somnolait dans une fosse marine.\r\n', 11, NULL, 3),
(383, 'groudon', 'Groudon', 100, 150, 140, 100, 90, 90, NULL, 'Il dormait dans le magma souterrain\r\ndepuis sa lutte féroce contre\r\nKyogre, il y a longtemps de cela.\r\n', 5, NULL, 3),
(384, 'rayquaza', 'Rayquaza', 105, 150, 90, 150, 90, 95, NULL, 'Il vit dans la couche d’ozone, au\r\ndessus des nuages. Il est invisible\r\ndepuis le sol.\r\n', 3, 16, 3),
(385, 'jirachi', 'Jirachi', 100, 100, 100, 100, 100, 100, NULL, 'On raconte qu’une fois tous les\r\nmille ans, il a la capacité d’exaucer\r\ntous les vœux durant une semaine.\r\n', 9, 14, 3),
(386, 'deoxys-normal', 'Deoxys', 50, 150, 50, 150, 50, 150, NULL, 'Il vient d’un virus extraterrestre\r\narrivé avec une météorite et ayant\r\nsubi une mutation génétique.\r\n', 14, NULL, 3),
(387, 'turtwig', 'Tortipouss', 55, 68, 64, 45, 55, 31, NULL, 'La coquille sur son dos est faite de\r\nterre. Plus il est en bonne santé,\r\nplus elle est humide.\r\n', 12, NULL, 4),
(388, 'grotle', 'Boskara', 75, 89, 85, 55, 65, 36, 387, 'Il vit en forêt près de l’eau. En journée, il la quitte pour dorer\r\nsa carapace feuillue au soleil.\r\n', 12, NULL, 4),
(389, 'torterra', 'Torterra', 95, 109, 105, 75, 85, 56, 388, 'Certains petits Pokémon peuvent\r\nnaître sur son dos et y passer\r\ntoute leur vie.\r\n', 5, 12, 4),
(390, 'chimchar', 'Ouisticram', 44, 58, 44, 58, 44, 61, NULL, 'Il est très agile. Il fait toujours\r\nattention d’éteindre la flamme de\r\nsa queue avant de se coucher.\r\n', 10, NULL, 4),
(391, 'monferno', 'Chimpenfeu', 64, 78, 52, 78, 52, 81, 390, 'Il contrôle habilement l’intensité\r\nde la flamme de sa queue pour tenir\r\nses adversaires à distance.\r\n', 2, 10, 4),
(392, 'infernape', 'Simiabraz', 76, 104, 71, 104, 71, 108, 391, 'Ses techniques de combat inédites\r\nà base de pieds et poings enflammés\r\nsont redoutables.\r\n', 2, 10, 4),
(393, 'piplup', 'Tiplouf', 53, 51, 53, 61, 56, 40, NULL, 'Malgré sa démarche maladroite et\r\nses pertes d’équilibre, il bombe\r\ntoujours fièrement le torse.\r\n', 11, NULL, 4),
(394, 'prinplup', 'Prinplouf', 64, 66, 68, 81, 76, 50, 393, 'La fierté du Prinplouf est si\r\ngrande qu’il lui est impossible\r\nde vivre en groupe.\r\n', 11, NULL, 4),
(395, 'empoleon', 'Pingoléon', 84, 86, 88, 111, 101, 60, 394, 'S’il voit son honneur bafoué, il\r\nest capable de faire des ravages\r\navec ses ailes tranchantes.\r\n', 9, 11, 4),
(396, 'starly', 'Étourmi', 40, 55, 30, 30, 30, 60, NULL, 'La faiblesse des Étourmi les force\r\nà vivre en groupe, mais ils se\r\nchamaillent souvent.\r\n', 1, 3, 4),
(397, 'staravia', 'Étourvol', 55, 75, 50, 40, 40, 80, 396, 'Conscient de sa propre faiblesse,\r\nil vit toujours en groupe, piaillant\r\nbruyamment quand il est seul.\r\n', 1, 3, 4),
(398, 'staraptor', 'Étouraptor', 85, 120, 70, 50, 60, 100, 397, 'Il combattra sans relâche, même\r\nblessé. Il prend grand soin de sa\r\nmèche.\r\n', 1, 3, 4),
(399, 'bidoof', 'Keunotor', 59, 45, 40, 35, 40, 31, NULL, 'Une étude a montré que les dents\r\ndu Keunotor poussaient aussi\r\nvite que celles du Rattata.\r\n', 1, NULL, 4),
(400, 'bibarel', 'Castorno', 79, 85, 60, 55, 60, 71, 399, 'Les digues insubmersibles qu’il\r\nconstruit font le bonheur des\r\nhabitants proches.\r\n', 1, 11, 4),
(401, 'kricketot', 'Crikzik', 37, 25, 41, 25, 41, 25, NULL, 'Quand il tombe du haut de ses\r\npetites pattes, ses antennes\r\nfont un bruit de xylophone.\r\n', 7, NULL, 4),
(402, 'kricketune', 'Mélokrik', 77, 85, 51, 55, 51, 65, 401, 'Ses cris sont tellement variés qu’il\r\nexiste un village où on organise\r\ndes concours en leur honneur.\r\n', 7, NULL, 4),
(403, 'shinx', 'Lixy', 45, 65, 34, 40, 34, 45, NULL, 'Ses muscles génèrent un courant\r\nélectrique lorsqu’ils travaillent.\r\nIl s’illumine en cas de danger.\r\n', 13, NULL, 4),
(404, 'luxio', 'Luxio', 60, 85, 49, 60, 49, 60, 403, 'Le courant qui circule à la pointe\r\nde ses griffes est capable de faire\r\nperdre connaissance à ses proies.\r\n', 13, NULL, 4),
(405, 'luxray', 'Luxray', 80, 120, 79, 95, 79, 70, 404, 'Il est capable de voir à travers\r\nles murs pour chasser des proies\r\nou retrouver ses petits égarés.\r\n', 13, NULL, 4),
(406, 'budew', 'Rozbouton', 40, 30, 35, 50, 70, 55, NULL, 'Il est sensible aux variations de\r\ntempérature et on dit qu’il fleurit\r\nquand il sent la chaleur du soleil.\r\n', 4, 12, 4),
(407, 'roserade', 'Roserade', 60, 70, 65, 125, 105, 90, 315, 'Ses mains contenant chacune des\r\ntoxines différentes sont capables\r\nd’asséner de puissants coups.\r\n', 4, 12, 4),
(408, 'cranidos', 'Kranidos', 67, 125, 40, 30, 30, 58, NULL, 'Il vivait dans la jungle il y a\r\n100 millions d’années. Il abat\r\nun arbre d’un coup de tête.\r\n', 6, NULL, 4),
(409, 'rampardos', 'Charkos', 97, 165, 60, 65, 50, 58, 408, 'Quand deux Charkos se rentrent\r\ndedans, leur crâne extrêmement\r\népais les empêche de s’assommer.\r\n', 6, NULL, 4),
(410, 'shieldon', 'Dinoclier', 30, 42, 118, 42, 88, 30, NULL, 'Il est si bien protégé qu’il peut\r\ndéguster Baies et herbe fraîche\r\nsans craindre de prédateurs.\r\n', 6, 9, 4),
(411, 'bastiodon', 'Bastiodon', 60, 52, 168, 47, 138, 30, 410, 'En groupe, ils protègent leurs\r\npetits en formant autour d’eux\r\nune impénétrable muraille.\r\n', 6, 9, 4),
(412, 'burmy', 'Cheniti', 40, 29, 45, 29, 45, 36, NULL, 'Nul ne sait comment il y parvient,\r\nmais il arrive toujours à se former\r\nune cape, où qu’il soit.\r\n', 7, NULL, 4),
(413, 'wormadam-plant', 'Cheniselle', 60, 59, 85, 79, 105, 36, 412, 'À l’évolution, son corps absorbe\r\nles matériaux à proximité et peut\r\nainsi présenter divers aspects.\r\n', 7, 12, 4),
(414, 'mothim', 'Papilord', 70, 94, 50, 94, 50, 66, 412, 'Il adore le nectar des fleurs mais\r\nn’en récolte pas lui-même.\r\nIl préfère en voler à un Apitrini.\r\n', 3, 7, 4),
(415, 'combee', 'Apitrini', 30, 30, 42, 30, 42, 70, NULL, 'Ce trio inséparable depuis la\r\nnaissance récolte du miel\r\npour satisfaire Apireine.\r\n', 3, 7, 4),
(416, 'vespiquen', 'Apireine', 70, 80, 102, 80, 102, 40, 415, 'Ce Pokémon utilise des phéromones\r\npour donner des ordres de combat\r\naux larves dans son abdomen.\r\n', 3, 7, 4),
(417, 'pachirisu', 'Pachirisu', 60, 45, 70, 45, 90, 95, NULL, 'Il arrive que deux Pachirisu se\r\nfrottent les joues pour partager\r\nl’électricité qu’ils ont accumulée.\r\n', 13, NULL, 4),
(418, 'buizel', 'Mustébouée', 55, 65, 35, 60, 30, 85, NULL, 'Il utilise ses deux queues comme\r\nune hélice pour se propulser sous\r\nl’eau et trancher les algues.\r\n', 11, NULL, 4),
(419, 'floatzel', 'Mustéflott', 85, 105, 55, 85, 50, 115, 418, 'Il chasse souvent près des ports\r\nde pêche où il sauve parfois les\r\ngens tombés à la mer.\r\n', 11, NULL, 4),
(420, 'cherubi', 'Ceribou', 45, 35, 45, 62, 53, 35, NULL, 'Sa petite boule rose est nutritive\r\net délicieuse. Les Étourmi en\r\nraffolent.\r\n', 12, NULL, 4),
(421, 'cherrim', 'Ceriflor', 70, 60, 70, 87, 78, 85, 420, 'Sous un grand soleil, il ouvre\r\nses pétales pour en absorber\r\nles rayons.\r\n', 12, NULL, 4),
(422, 'shellos', 'Sancoki', 76, 48, 48, 57, 62, 34, NULL, 'Un étrange liquide mauve s’échappe\r\nde son corps, ne lui appuyez pas\r\ndessus.\r\n', 11, NULL, 4),
(423, 'gastrodon', 'Tritosor', 111, 83, 68, 92, 82, 39, 422, 'Ses gènes ont démontré qu’il avait\r\njadis une carapace couvrant la\r\ntotalité de son dos.\r\n', 5, 11, 4),
(424, 'ambipom', 'Capidextre', 75, 100, 66, 60, 66, 115, 190, 'Ses queues jumelles sont si agiles\r\net efficaces qu’il ne se sert que\r\ntrès rarement de ses bras.\r\n', 1, NULL, 4),
(425, 'drifloon', 'Baudrive', 90, 50, 34, 60, 44, 70, NULL, 'Sa façon de flotter sans but au\r\ngré du vent lui a valu le surnom\r\nde “bouée des esprits égarés”.\r\n', 3, 8, 4),
(426, 'drifblim', 'Grodrive', 150, 80, 44, 90, 54, 80, 425, 'Des nuées de Grodrive dérivent\r\ndans le ciel au crépuscule. Ils\r\ndisparaissent dès qu’on les voit.\r\n', 3, 8, 4),
(427, 'buneary', 'Laporeille', 55, 66, 44, 44, 56, 85, NULL, 'Ses oreilles repliées se déroulent\r\navec une force capable de briser\r\nun gros rocher.\r\n', 1, NULL, 4),
(428, 'lopunny', 'Lockpin', 65, 76, 84, 54, 96, 105, 427, 'Il est capable de décocher de\r\npuissants coups de pied si on\r\ntouche à ses délicates oreilles.\r\n', 1, NULL, 4),
(429, 'mismagius', 'Magirêve', 60, 60, 60, 105, 105, 105, 200, 'Pareil à une incantation magique,\r\nson cri n’est que très rarement\r\nsource de joie.\r\n', 8, NULL, 4),
(430, 'honchkrow', 'Corboss', 100, 125, 52, 105, 52, 71, 198, 'Son cri profond peut invoquer une\r\nmeute de Cornèbre. On l’appelle\r\n“le crieur de mauvais augure”.\r\n', 3, 17, 4),
(431, 'glameow', 'Chaglam', 49, 55, 42, 42, 37, 85, NULL, 'Il a tendance à planter ses griffes\r\ndans le nez de son Dresseur s’il\r\nn’a pas été nourri à temps.\r\n', 1, NULL, 4),
(432, 'purugly', 'Chaffreux', 71, 82, 64, 64, 59, 112, 431, 'Il plaque ses queues contre son\r\ndos pour paraître plus grand.\r\nIl soutient un regard des heures.\r\n', 1, NULL, 4),
(433, 'chingling', 'Korillon', 45, 30, 50, 65, 50, 45, NULL, 'Quand il sautille, l’orbe qu’il a\r\ndans la bouche s’agite et tinte\r\ncomme une cloche.\r\n', 14, NULL, 4),
(434, 'stunky', 'Moufouette', 63, 63, 47, 41, 41, 74, NULL, 'Son derrière propulse un fluide\r\nnauséabond qui peut faire fuir les\r\nPokémon dans un rayon de 2 km.\r\n', 4, 17, 4),
(435, 'skuntank', 'Moufflair', 103, 93, 67, 71, 61, 84, 434, 'Il attaque en projetant un liquide\r\nfétide du bout de sa queue. Mieux\r\nvaut l’attaquer par les airs.\r\n', 4, 17, 4),
(436, 'bronzor', 'Archéomire', 57, 24, 86, 24, 86, 23, NULL, 'Certains scientifiques soutiennent\r\nque ce Pokémon était jadis aussi\r\nréfléchissant qu’un miroir.\r\n', 9, 14, 4),
(437, 'bronzong', 'Archéodong', 67, 89, 116, 79, 116, 33, 436, 'Il apportait la pluie en créant un\r\nportail vers une autre dimension.\r\nIl était synonyme de bonne récolte.\r\n', 9, 14, 4),
(438, 'bonsly', 'Manzaï', 50, 80, 95, 10, 45, 10, NULL, 'Adepte des climats secs, il régule\r\nson humidité en laissant couler ce\r\nqui ressemble à des larmes.\r\n', 6, NULL, 4),
(439, 'mime-jr', 'Mime Jr.', 20, 25, 45, 70, 90, 60, NULL, 'Il imite les mouvements et les\r\nexpressions des autres pour\r\ncomprendre leurs sentiments.\r\n', 14, 18, 4),
(440, 'happiny', 'Ptiravi', 100, 5, 5, 15, 65, 30, NULL, 'Ce Pokémon ne se sépare jamais de\r\nson petit caillou et a tendance à\r\ntraîner dans les pieds des gens.\r\n', 1, NULL, 4),
(441, 'chatot', 'Pijako', 76, 65, 45, 92, 42, 91, NULL, 'Sa langue comparable à celle\r\nde l’homme lui permet d’imiter\r\nhabilement le langage humain.\r\n', 1, 3, 4),
(442, 'spiritomb', 'Spiritomb', 50, 92, 108, 92, 108, 35, NULL, 'Son mauvais comportement lui a valu\r\nd’être enchaîné à une Clé de Voûte\r\npar un mystérieux sortilège.\r\n', 8, 17, 4),
(443, 'gible', 'Griknot', 58, 70, 45, 40, 45, 42, NULL, 'Malgré sa maladresse, son énorme\r\ngueule lui permet de lancer des\r\nattaques puissantes.\r\n', 5, 16, 4),
(444, 'gabite', 'Carmache', 68, 90, 65, 50, 55, 82, 443, 'Toujours à la recherche d’objets\r\nbrillants, il arpente les grottes\r\npour constituer son butin.\r\n', 5, 16, 4),
(445, 'garchomp', 'Carchacrok', 108, 130, 95, 80, 85, 102, 444, 'On dit que lancé à pleine vitesse,\r\nil crée des lames de vent avec ses\r\nailes pouvant trancher un arbre.\r\n', 5, 16, 4),
(446, 'munchlax', 'Goinfrex', 135, 85, 40, 40, 85, 5, NULL, 'Obnubilé par sa quête incessante\r\nde nourriture, il oublie parfois\r\nqu’il en a caché dans sa fourrure.\r\n', 1, NULL, 4),
(447, 'riolu', 'Riolu', 40, 70, 40, 35, 40, 60, NULL, 'Il a la faculté unique de visualiser\r\nles émotions telles que la joie ou\r\nla colère sous forme d’ondes.\r\n', 2, NULL, 4),
(448, 'lucario', 'Lucario', 70, 110, 70, 115, 70, 90, 447, 'Un spécimen bien entraîné peut\r\nsentir et identifier les émotions de\r\nquelqu’un à plus d’un kilomètre.\r\n', 2, 9, 4),
(449, 'hippopotas', 'Hippopotas', 68, 72, 78, 38, 42, 32, NULL, 'Il peut obturer ses narines et\r\ntraverser le sable comme un ver.\r\nIl vit en colonies de 10 individus.\r\n', 5, NULL, 4),
(450, 'hippowdon', 'Hippodocus', 108, 112, 118, 68, 72, 47, 449, 'Facilement irritable, il ouvre ses\r\nmâchoires au maximum pour intimider\r\nses adversaires.\r\n', 5, NULL, 4),
(451, 'skorupi', 'Rapion', 40, 50, 90, 30, 55, 65, NULL, 'Il saisit sa proie avec les pinces de\r\nsa queue et lui injecte un poison.\r\nIl peut survivre un an sans manger.\r\n', 4, 7, 4),
(452, 'drapion', 'Drascore', 70, 90, 110, 60, 75, 95, 451, 'Fier et robuste, il met un point\r\nd’honneur à battre ses adversaires\r\nsans utiliser de poison.\r\n', 4, 17, 4),
(453, 'croagunk', 'Cradopaud', 48, 61, 40, 61, 40, 50, NULL, 'Son instinct de survie le pousse à\r\nne pas se battre dans les règles.\r\nC’est une mascotte populaire.\r\n', 2, 4, 4),
(454, 'toxicroak', 'Coatox', 83, 106, 65, 86, 65, 85, 453, 'Quand il coasse, le poison stocké\r\nsous sa gorge est secoué, le\r\nrendant ainsi plus efficace.\r\n', 2, 4, 4),
(455, 'carnivine', 'Vortente', 74, 100, 72, 90, 72, 46, NULL, 'Il s’accroche aux arbres des\r\nmarais et attire ses proies avec\r\nsa salive à l’odeur enivrante.\r\n', 12, NULL, 4),
(456, 'finneon', 'Écayon', 49, 49, 56, 49, 61, 66, NULL, 'La ligne qui fait le tour de son\r\ncorps stocke l’énergie solaire\r\net brille dans la nuit.\r\n', 11, NULL, 4),
(457, 'lumineon', 'Luminéon', 69, 69, 76, 69, 86, 91, 456, 'Il rampe au fond de l’océan grâce\r\naux nageoires de son torse.\r\nLe Lanturn est son rival.\r\n', 11, NULL, 4),
(458, 'mantyke', 'Babimanta', 45, 20, 50, 60, 120, 50, NULL, 'On organise des excursions pour le\r\nvoir surfer sur les vagues au côté\r\ndes Rémoraid.\r\n', 3, 11, 4),
(459, 'snover', 'Blizzi', 60, 62, 50, 62, 60, 40, NULL, 'Curieux de rencontrer des gens, ils\r\nse rassemblent autour des traces\r\nde pas trouvées dans la montagne.\r\n', 12, 15, 4),
(460, 'abomasnow', 'Blizzaroi', 90, 92, 75, 92, 85, 60, 459, 'Il apparaît quand les fleurs des\r\nterres enneigées fleurissent et\r\nse retire quand elles se fanent.\r\n', 12, 15, 4),
(461, 'weavile', 'Dimoret', 70, 120, 65, 45, 85, 125, 215, 'L’évolution l’a rendu encore plus\r\nretors. Il communique en griffant\r\ndes signes dans les pierres.\r\n', 15, 17, 4),
(462, 'magnezone', 'Magnézone', 70, 70, 115, 130, 90, 60, 82, 'Des scientifiques ont essayé de\r\nfaire évoluer un Magnézone en\r\nlaboratoire, mais ce fut un échec.\r\n', 9, 13, 4),
(463, 'lickilicky', 'Coudlangue', 110, 85, 95, 80, 95, 50, 108, 'Sa longue langue est couverte en\r\npermanence d’une épaisse couche\r\nde bave anesthésiante.\r\n', 1, NULL, 4),
(464, 'rhyperior', 'Rhinastoc', 115, 140, 130, 55, 55, 40, 112, 'Il peut lancer des pierres à une\r\nvitesse saisissante en bandant et\r\nrelâchant ses muscles en un éclair.\r\n', 5, 6, 4),
(465, 'tangrowth', 'Bouldeneu', 100, 100, 125, 110, 50, 50, 114, 'Ses bras sont en fait des plantes\r\nqui s’attachent à tout ce qu’elles\r\ntouchent et peuvent repousser.\r\n', 12, NULL, 4),
(466, 'electivire', 'Élekable', 75, 123, 67, 95, 85, 95, 125, 'Encaissant les attaques, il vient\r\ntoucher l’ennemi de ses queues\r\net lui inflige une forte décharge.\r\n', 13, NULL, 4),
(467, 'magmortar', 'Maganon', 75, 95, 67, 125, 95, 83, 126, 'Quand il lance des boules de feu\r\nà plus de 2 000 °C, son corps\r\nest comme chauffé à blanc.\r\n', 10, NULL, 4),
(468, 'togekiss', 'Togekiss', 85, 50, 95, 120, 115, 80, 176, 'Il apprécie particulièrement les\r\ngens qui respectent les autres\r\net évitent les conflits inutiles.\r\n', 3, 18, 4),
(469, 'yanmega', 'Yanmega', 86, 76, 86, 116, 56, 95, 193, 'Doté de mandibules à la force\r\ncolossale, il excelle pour mordre\r\nses ennemis en plein vol.\r\n', 3, 7, 4),
(470, 'leafeon', 'Phyllali', 65, 110, 130, 60, 65, 95, 133, 'Il utilise la photosynthèse comme\r\nune plante et a aussi tendance à\r\néviter le combat à tout prix.\r\n', 12, NULL, 4),
(471, 'glaceon', 'Givrali', 65, 60, 110, 130, 95, 65, 133, 'Il ajuste la température de son\r\ncorps pour créer un vent de glace\r\nen gelant l’air qui l’entoure.\r\n', 15, NULL, 4),
(472, 'gliscor', 'Scorvol', 75, 95, 125, 45, 75, 95, 207, 'Le plus petit courant d’air peut lui\r\ndonner assez d’élan pour faire le\r\ntour du monde sans un coup d’aile.\r\n', 3, 5, 4),
(473, 'mamoswine', 'Mammochon', 110, 130, 80, 70, 60, 80, 221, 'On a libéré un spécimen de sa prison\r\nglacée au bout de 10 000 ans.\r\nIl n’en revenait pas.\r\n', 5, 15, 4),
(474, 'porygon-z', 'Porygon-Z', 85, 80, 70, 135, 75, 90, 233, 'Il a été modifié pour être capable\r\nde travailler dans des dimensions\r\nparallèles, mais c’est un échec.\r\n', 1, NULL, 4),
(475, 'gallade', 'Gallame', 68, 125, 65, 65, 115, 80, 281, 'Toujours prêt à protéger son prochain,\r\nil se bat férocement avec ses coudes\r\nen forme d’épées.\r\n', 2, 14, 4),
(476, 'probopass', 'Tarinorme', 60, 55, 145, 75, 150, 40, 299, 'Il utilise sa force magnétique pour\r\ndiriger trois petites unités\r\nappelées Mini-nez.\r\n', 6, 9, 4),
(477, 'dusknoir', 'Noctunoir', 45, 100, 135, 65, 135, 45, 356, 'Son corps se replie sur lui-même\r\npour emprisonner les esprits\r\nperdus et les ramener chez eux.\r\n', 8, NULL, 4),
(478, 'froslass', 'Momartik', 70, 80, 70, 80, 70, 110, 361, 'On dit que Momartik gèle ses proies\r\ngrâce à son souffle à -50 °C avant\r\nde les exposer dans sa tanière.\r\n', 8, 15, 4),
(479, 'rotom', 'Motisma', 50, 50, 77, 95, 77, 91, NULL, 'Un Pokémon longtemps étudié comme source\r\nd’énergie pour un moteur très spécial.\r\n', 8, 13, 4),
(480, 'uxie', 'Créhelf', 75, 75, 130, 75, 130, 95, NULL, 'Quand il prit son envol, les hommes\r\napprirent à résoudre les problèmes.\r\nLe savoir était né.\r\n', 14, NULL, 4),
(481, 'mesprit', 'Créfollet', 80, 105, 105, 105, 105, 80, NULL, 'Quand il prit son envol, les hommes\r\napprirent à ressentir la joie et la\r\npeine. L’émotion était née.\r\n', 14, NULL, 4),
(482, 'azelf', 'Créfadet', 75, 125, 70, 125, 70, 115, NULL, 'Quand il prit son envol, les hommes\r\nacquirent la détermination.\r\nLa volonté était née.\r\n', 14, NULL, 4),
(483, 'dialga', 'Dialga', 100, 120, 120, 150, 100, 90, NULL, 'On dit que le temps s’écoule au\r\nrythme des battements de cœur de\r\nce Pokémon légendaire de Sinnoh.\r\n', 9, 16, 4),
(484, 'palkia', 'Palkia', 90, 120, 100, 150, 120, 100, NULL, 'On dit que la stabilité de l’espace\r\nest maintenue par le souffle de ce\r\nPokémon légendaire de Sinnoh.\r\n', 11, 16, 4),
(485, 'heatran', 'Heatran', 91, 90, 106, 130, 106, 77, NULL, 'Son corps est composé d’acier brut\r\ndont certaines parties ont fondu\r\nà cause de sa propre chaleur.\r\n', 9, 10, 4),
(486, 'regigigas', 'Regigigas', 110, 160, 110, 80, 110, 100, NULL, 'On dit qu’il a créé des Pokémon\r\nà son image à partir de glace,\r\nde roche et de magma.\r\n', 1, NULL, 4),
(487, 'giratina-altered', 'Giratina', 150, 100, 120, 100, 120, 90, NULL, 'Sa grande violence lui a valu d’être\r\nbanni. Il observe les hommes en\r\nsilence depuis le Monde Distorsion.\r\n', 8, 16, 4),
(488, 'cresselia', 'Cresselia', 120, 70, 120, 75, 130, 85, NULL, 'Les nuits où le premier croissant\r\nde lune est visible, l’arc de sa\r\nqueue se déploie de façon superbe.\r\n', 14, NULL, 4),
(489, 'phione', 'Phione', 80, 80, 80, 80, 80, 80, NULL, 'Ce Pokémon des mers chaudes\r\nrevient toujours à son lieu de\r\nnaissance, peu importe la distance.\r\n', 11, NULL, 4),
(490, 'manaphy', 'Manaphy', 100, 100, 100, 100, 100, 100, NULL, 'Il est né avec le pouvoir incroyable\r\nde créer des liens avec n’importe\r\nquel Pokémon.\r\n', 11, NULL, 4),
(491, 'darkrai', 'Darkrai', 70, 90, 90, 135, 90, 125, NULL, 'Menacé, ce Pokémon se protège\r\nen créant des cauchemars dans\r\nl’esprit de ses ennemis.\r\n', 17, NULL, 4),
(492, 'shaymin-land', 'Shaymin', 100, 100, 100, 100, 100, 100, NULL, 'Les fleurs qui couvrent son corps\r\ns’ouvrent quand il est câliné avec\r\namour et perçoit de la gratitude.\r\n', 12, NULL, 4),
(493, 'arceus', 'Arceus', 120, 120, 120, 120, 120, 120, NULL, 'On dit que son œuf a éclos dans\r\nle néant et qu’il est à l’origine\r\nde la création du monde.\r\n', 1, NULL, 4),
(494, 'victini', 'Victini', 100, 100, 100, 100, 100, 100, NULL, 'Un Pokémon qui amène la victoire. On dit\r\nque les Dresseurs qui le possèdent\r\npeuvent gagner n’importe quel combat.\r\n', 10, 14, 4),
(495, 'snivy', 'Vipélierre', 45, 45, 55, 45, 55, 63, NULL, 'Un Pokémon intelligent et très calme.\r\nLorsqu’il a reçu suffisamment de\r\nlumière du soleil, il devient plus agile.\r\n', 12, NULL, 5),
(496, 'servine', 'Lianaja', 60, 60, 75, 60, 75, 83, 495, 'Il court comme s’il glissait sur le sol.\r\nIl déroute l’ennemi par ses mouvements\r\net l’assomme d’un coup de liane.\r\n', 12, NULL, 5),
(497, 'serperior', 'Majaspic', 75, 75, 95, 75, 95, 113, 496, 'Il peut paralyser l’ennemi d’un regard.\r\nIl amplifie l’énergie du soleil dans\r\nson corps.\r\n', 12, NULL, 5),
(498, 'tepig', 'Gruikui', 65, 63, 45, 45, 45, 45, NULL, 'Il évite agilement les attaques ennemies\r\net crache des boules de feu par le groin.\r\nIl aime griller des Baies pour les manger.\r\n', 10, NULL, 5),
(499, 'pignite', 'Grotichon', 90, 93, 55, 70, 55, 55, 498, 'Quand le feu dans son corps s’embrase,\r\nsa vitesse et son agilité augmentent.\r\nEn cas d’urgence, il crache de la fumée.\r\n', 2, 10, 5),
(500, 'emboar', 'Roitiflam', 110, 123, 65, 100, 65, 65, 499, 'Il embrase ses poings avec les flammes\r\nsur son menton et cogne l’ennemi avec.\r\nC’est un Pokémon très solidaire.\r\n', 2, 10, 5),
(501, 'oshawott', 'Moustillon', 55, 55, 45, 63, 45, 45, NULL, 'Il combat avec le coupillage de son\r\nventre. Il peut parer un assaut et\r\nimmédiatement contre-attaquer.\r\n', 11, NULL, 5),
(502, 'dewott', 'Mateloutre', 75, 75, 60, 83, 60, 60, 501, 'L’apprentissage de sa technique de\r\ncombat, qui utilise élégamment ses deux\r\ncoupillages, est extrêmement rigoureux.\r\n', 11, NULL, 5),
(503, 'samurott', 'Clamiral', 95, 100, 85, 108, 70, 70, 502, 'Il peut terrasser ses ennemis d’un coup\r\nde la lame enchâssée dans son armure,\r\net les faire taire d’un seul regard.\r\n', 11, NULL, 5),
(504, 'patrat', 'Ratentif', 45, 55, 39, 35, 39, 42, NULL, 'Ils stockent à manger dans leurs\r\nbajoues et font le guet indéfiniment. Ils\r\nse font des signes en remuant la queue.\r\n', 1, NULL, 5),
(505, 'watchog', 'Miradar', 60, 85, 69, 60, 69, 77, 504, 'Il attaque en crachant les graines des\r\nBaies qu’il accumule dans ses bajoues.\r\nS’il voit un ennemi, il dresse la queue.\r\n', 1, NULL, 5),
(506, 'lillipup', 'Ponchiot', 45, 60, 45, 25, 45, 55, NULL, 'Il fait vaillamment face à des ennemis\r\ntrès puissants, mais il est intelligent\r\net évite les combats trop inégaux.\r\n', 1, NULL, 5),
(507, 'herdier', 'Ponchien', 65, 80, 65, 35, 65, 60, 506, 'Les poils qui entourent son corps\r\ncomme un manteau sont extrêmement\r\ndurs et amortissent les coups.\r\n', 1, NULL, 5),
(508, 'stoutland', 'Mastouffe', 85, 110, 90, 45, 90, 80, 507, 'Il sauve les gens bloqués dans les\r\nmontagnes par des tempêtes de neige.\r\nSes longs poils le protègent du froid.\r\n', 1, NULL, 5),
(509, 'purrloin', 'Chacripan', 41, 50, 37, 50, 37, 66, NULL, 'Il vole les gens pour le plaisir, mais il\r\nest tellement mignon que ses victimes\r\nfinissent toujours par lui pardonner.\r\n', 17, NULL, 5),
(510, 'liepard', 'Léopardus', 64, 88, 50, 88, 50, 106, 509, 'Un Pokémon insaisissable. Son style\r\net son pelage magnifique attirent de\r\nnombreux Dresseurs.\r\n', 17, NULL, 5),
(511, 'pansage', 'Feuillajou', 50, 53, 48, 53, 48, 64, NULL, 'Il vit dans les forêts profondes.\r\nManger la feuille qui pousse sur\r\nsa tête fait disparaître la fatigue.\r\n', 12, NULL, 5),
(512, 'simisage', 'Feuiloutan', 75, 98, 63, 98, 63, 101, 511, 'Il a un tempérament violent et se\r\nbat avec sa queue pleine d’épines.\r\nLa feuille sur sa tête est très amère.\r\n', 12, NULL, 5),
(513, 'pansear', 'Flamajou', 50, 53, 48, 53, 48, 64, NULL, 'Quand il s’énerve, la mèche sur sa\r\ntête chauffe à 300 °C. Il s’en sert\r\npour griller des Baies et les manger.\r\n', 10, NULL, 5),
(514, 'simisear', 'Flamoutan', 75, 98, 63, 98, 63, 101, 513, 'Il adore les aliments sucrés. Ils lui\r\nservent d’énergie pour alimenter le\r\nfeu à l’intérieur de son corps.\r\n', 10, NULL, 5),
(515, 'panpour', 'Flotajou', 50, 53, 48, 53, 48, 64, NULL, 'L’eau contenue dans la mèche sur sa\r\ntête est pleine de nutriments. Elle fait\r\npousser les plantes avec vigueur.\r\n', 11, NULL, 5),
(516, 'simipour', 'Flotoutan', 75, 98, 63, 98, 63, 101, 515, 'Il stocke de l’eau dans les mèches de sa\r\ntête. Quand il est à sec, il se ravitaille\r\nen aspirant de l’eau par la queue.\r\n', 11, NULL, 5),
(517, 'munna', 'Munna', 76, 25, 45, 67, 55, 24, NULL, 'Quand Munna mange un rêve, celui-ci\r\nest aussitôt oublié par le rêveur.\r\nIl lévite en permanence.\r\n', 14, NULL, 5),
(518, 'musharna', 'Mushana', 116, 55, 85, 107, 95, 29, 517, 'La fumée qui sort de son front contient\r\nles rêves de nombreuses personnes et\r\nceux de nombreux Pokémon.\r\n', 14, NULL, 5),
(519, 'pidove', 'Poichigeon', 50, 55, 50, 36, 30, 43, NULL, 'Il suit scrupuleusement les consignes\r\nde son Dresseur, mais il a du mal à\r\ncomprendre les ordres trop compliqués.\r\n', 1, 3, 5),
(520, 'tranquill', 'Colombeau', 62, 77, 62, 50, 42, 65, 519, 'Aussi éloigné qu’il puisse se trouver,\r\nil parvient toujours à retourner\r\nauprès de son Dresseur.\r\n', 1, 3, 5),
(521, 'unfezant', 'Déflaisan', 80, 115, 80, 65, 55, 93, 520, 'Le mâle intimide ses ennemis\r\navec ses caroncules.\r\nLa femelle vole mieux que le mâle.\r\n', 1, 3, 5),
(522, 'blitzle', 'Zébibron', 45, 60, 32, 50, 32, 76, NULL, 'Leur crinière scintille quand ils lâchent\r\nune décharge électrique. Ils se servent\r\nde ces scintillements pour communiquer.\r\n', 13, NULL, 5),
(523, 'zebstrika', 'Zéblitz', 75, 100, 63, 80, 63, 116, 522, 'Il réagit à la vitesse de l’éclair.\r\nLorsqu’il est en plein galop, on peut\r\nentendre le grondement du tonnerre.\r\n', 13, NULL, 5),
(524, 'roggenrola', 'Nodulithe', 55, 75, 85, 25, 25, 15, NULL, 'Il a une oreille hexagonale. Son\r\ncorps, compressé dans des strates\r\nprofondes, est dur comme de l’acier.\r\n', 6, NULL, 5),
(525, 'boldore', 'Géolithe', 70, 105, 105, 50, 40, 20, 524, 'Quand il est gonflé à bloc, ses cristaux\r\norangés scintillent. Il cherche de l’eau\r\nsouterraine dans les grottes.\r\n', 6, NULL, 5),
(526, 'gigalith', 'Gigalithe', 85, 135, 130, 60, 80, 25, 525, 'Il comprime de l’énergie dans son corps,\r\net ses attaques sont assez puissantes\r\npour renverser des montagnes.\r\n', 6, NULL, 5),
(527, 'woobat', 'Chovsourir', 65, 45, 43, 55, 43, 72, NULL, 'Il vit dans les forêts sombres et les\r\ngrottes. Il émet des ultrasons par\r\nle nez pour se diriger.\r\n', 3, 14, 5),
(528, 'swoobat', 'Rhinolove', 67, 57, 55, 77, 55, 114, 527, 'Il émet des ondes sonores de\r\ndifférentes fréquences par le nez.\r\nCertaines peuvent briser des rochers.\r\n', 3, 14, 5),
(529, 'drilbur', 'Rototaupe', 60, 85, 40, 30, 45, 68, NULL, 'Il peut creuser à plus de 50 km/h et\r\nfaire la course avec une voiture roulant\r\nsur le terrain qu’il creuse.\r\n', 5, NULL, 5),
(530, 'excadrill', 'Minotaupe', 110, 135, 60, 50, 65, 88, 529, 'Ses vrilles en acier peuvent transpercer\r\ndes poutres en fer. Il est très employé\r\ndans la construction de tunnels.\r\n', 5, 9, 5),
(531, 'audino', 'Nanméouïe', 103, 60, 86, 60, 86, 50, NULL, 'Il ausculte les gens avec ses antennes\r\nauriculaires pour juger de leurs\r\némotions ou de leur santé.\r\n', 1, NULL, 5),
(532, 'timburr', 'Charpenti', 75, 80, 55, 25, 35, 35, NULL, 'Il combat avec une poutre. Quand il en\r\narrive à transporter de lourds madriers\r\nsans problème, l’évolution est proche.\r\n', 2, NULL, 5),
(533, 'gurdurr', 'Ouvrifier', 85, 105, 85, 40, 50, 40, 532, 'Même une armée de catcheurs se\r\njetant sur son corps hyper musclé\r\nne le ferait pas ciller.\r\n', 2, NULL, 5),
(534, 'conkeldurr', 'Bétochef', 105, 140, 95, 55, 65, 45, 533, 'On pense que la recette du ciment\r\na été enseignée aux humains par\r\nun Bétochef il y a 2 000 ans.\r\n', 2, NULL, 5),
(535, 'tympole', 'Tritonde', 50, 50, 40, 50, 40, 64, NULL, 'Il prévient ses camarades du danger en\r\nfaisant vibrer ses joues pour générer\r\nun signal d’alarme très aigu.\r\n', 11, NULL, 5),
(536, 'palpitoad', 'Batracné', 75, 65, 55, 65, 55, 69, 535, 'Quand il fait vibrer la bosse sur sa tête,\r\nil provoque des ondulations dans l’eau,\r\nmais aussi des tremblements de terre.\r\n', 5, 11, 5),
(537, 'seismitoad', 'Crapustule', 105, 95, 75, 85, 75, 74, 536, 'Il fait gicler un liquide paralysant des\r\npustules sur sa tête, et attaque\r\nses ennemis à coups de vibrations.\r\n', 5, 11, 5),
(538, 'throh', 'Judokrak', 120, 100, 85, 30, 85, 45, NULL, 'Quand il noue sa ceinture, il devient\r\nplus puissant. Les Judokrak sauvages\r\ntressent leur ceinture avec des lianes.\r\n', 2, NULL, 5),
(539, 'sawk', 'Karaclée', 75, 125, 75, 30, 75, 85, NULL, 'Dans les montagnes où il s’entraîne, on\r\npeut l’entendre donner des coups de\r\npoing aux rochers et aux grands arbres.\r\n', 2, NULL, 5),
(540, 'sewaddle', 'Larveyette', 45, 53, 70, 40, 60, 42, NULL, 'Quand il éclot, Manternel lui coud son\r\nvêtement. Il dort en cachant son\r\nvisage sous sa capuche.\r\n', 7, 12, 5),
(541, 'swadloon', 'Couverdure', 55, 63, 90, 50, 80, 42, 540, 'Dans les forêts où il vit, la végétation\r\npousse bien car il transforme les\r\nfeuilles mortes en engrais.\r\n', 7, 12, 5),
(542, 'leavanny', 'Manternel', 75, 103, 80, 70, 80, 92, 541, 'Quand il trouve de petits Pokémon, il\r\nleur coud des vêtements de feuilles en\r\nutilisant ses coupoirs et son fil adhésif.\r\n', 7, 12, 5),
(543, 'venipede', 'Venipatte', 30, 45, 59, 30, 39, 57, NULL, 'Sa morsure est très venimeuse.\r\nElle paralyse même les grands\r\nPokémon Oiseaux, ses prédateurs.\r\n', 4, 7, 5),
(544, 'whirlipede', 'Scobolide', 40, 55, 99, 40, 79, 47, 543, 'Protégé par sa solide carapace,\r\nil fonce sur ses ennemis en\r\nroulant comme un pneu.\r\n', 4, 7, 5),
(545, 'scolipede', 'Brutapode', 60, 100, 89, 55, 69, 112, 544, 'Il accule l’ennemi par ses mouvements\r\nrapides et l’attaque avec ses cornes.\r\nIl ne s’arrête qu’au coup de grâce.\r\n', 4, 7, 5),
(546, 'cottonee', 'Doudouvet', 40, 27, 60, 37, 50, 66, NULL, 'Il sème du coton pour se protéger. Il lui arrive\r\nd’être emporté par une tempête à l’autre bout\r\ndu monde.\r\n', 12, 18, 5),
(547, 'whimsicott', 'Farfaduvet', 60, 67, 85, 77, 75, 116, 546, 'Il se faufile comme le vent par les\r\nespaces les plus étroits, en laissant\r\nune touffe de poils blancs.\r\n', 12, 18, 5),
(548, 'petilil', 'Chlorobule', 45, 35, 50, 70, 50, 30, NULL, 'Les feuilles sur sa tête sont très\r\namères, mais elles remettent d’aplomb\r\nles corps les plus éreintés.\r\n', 12, NULL, 5),
(549, 'lilligant', 'Fragilady', 70, 60, 75, 110, 75, 90, 548, 'Même les Dresseurs confirmés ont\r\ndu mal à faire éclore sa belle fleur.\r\nUn Pokémon apprécié des célébrités.\r\n', 12, NULL, 5),
(550, 'basculin-red-striped', 'Bargantua', 70, 92, 65, 80, 55, 98, NULL, 'Les bleus et les rouges se détestent\r\ncordialement et se battent dès qu’ils\r\ns’aperçoivent. Un Pokémon très violent.\r\n', 11, NULL, 5),
(551, 'sandile', 'Mascaïman', 50, 72, 35, 35, 35, 65, NULL, 'Il vit dans le sable des déserts.\r\nLe sable chauffé par le soleil empêche\r\nson corps de refroidir.\r\n', 5, 17, 5),
(552, 'krokorok', 'Escroco', 60, 82, 45, 45, 45, 74, 551, 'Ils vivent en petits groupes.\r\nLa membrane qui couvre leurs yeux\r\nles protège du soleil.\r\n', 5, 17, 5),
(553, 'krookodile', 'Crocorible', 95, 117, 80, 65, 70, 92, 552, 'Il ne laisse jamais échapper ses proies. Ses mâchoires\r\nsurpuissantes peuvent broyer une voiture.\r\n', 5, 17, 5),
(554, 'darumaka', 'Darumarond', 70, 90, 45, 15, 45, 50, NULL, 'Quand le feu brûle dans son corps,\r\nil est tout excité et court partout.\r\nQuand le feu s’apaise, il s’endort.\r\n', 10, NULL, 5),
(555, 'darmanitan-standard', 'Darumacho', 105, 140, 55, 30, 55, 95, 554, 'Il entretient des flammes à 1 400 °C\r\ndans son corps. De quoi détruire un\r\ncamion-benne d’un coup de poing!\r\n', 10, NULL, 5),
(556, 'maractus', 'Maracachi', 75, 86, 67, 106, 67, 60, NULL, 'Avec une danse et des sons saccadés,\r\nil fait fuir ses prédateurs, les Pokémon\r\nOiseaux dévoreurs de graines de fleur.\r\n', 12, NULL, 5),
(557, 'dwebble', 'Crabicoque', 50, 65, 85, 35, 35, 55, NULL, 'Il peut facilement creuser son trou\r\ndans les pierres les plus dures grâce\r\nau liquide sécrété par sa bouche.\r\n', 6, 7, 5);
INSERT INTO `pokemon_pokemon` (`id`, `name`, `nom`, `pv`, `attaque`, `defense`, `attaque_speciale`, `defense_speciale`, `vitesse`, `evolution_de`, `description`, `type_1`, `type_2`, `gen_id`) VALUES
(558, 'crustle', 'Crabaraque', 70, 105, 125, 65, 75, 45, 557, 'Entre eux, les querelles de territoire\r\nsont fréquentes et violentes. Celui qui\r\nse fait briser son rocher a perdu.\r\n', 6, 7, 5),
(559, 'scraggy', 'Baggiguane', 50, 75, 70, 35, 70, 48, NULL, 'Il remonte sa peau jusqu’à son cou pour\r\nse protéger. Elle a une constitution\r\nélastique qui absorbe les coups.\r\n', 2, 17, 5),
(560, 'scrafty', 'Baggaïd', 65, 90, 115, 45, 115, 58, 559, 'Ils se regroupent pour rosser ceux\r\nqui pénètrent sur leur territoire.\r\nIls crachent un venin corrosif.\r\n', 2, 17, 5),
(561, 'sigilyph', 'Cryptéro', 72, 58, 80, 103, 80, 97, NULL, 'On dit qu’il parcourt toujours le même\r\nitinéraire parce qu’il a gardé la mémoire\r\nde la ville antique qu’il protégeait.\r\n', 3, 14, 5),
(562, 'yamask', 'Tutafeh', 38, 30, 85, 55, 65, 30, NULL, 'Le motif de son masque est son visage\r\nquand il était humain. Parfois, il le\r\nregarde et se met à pleurer.\r\n', 8, NULL, 5),
(563, 'cofagrigus', 'Tutankafer', 58, 50, 145, 95, 105, 30, 562, 'Il paraît qu’il avale les gens\r\net les transforme en momies.\r\nIl adore manger des lingots d’or.\r\n', 8, NULL, 5),
(564, 'tirtouga', 'Carapagos', 54, 78, 103, 53, 45, 22, NULL, 'Il a été recréé à partir d’un fossile\r\npréhistorique. Il peut plonger jusqu’à\r\n1 000 mètres de profondeur.\r\n', 6, 11, 5),
(565, 'carracosta', 'Mégapagos', 74, 108, 133, 83, 65, 32, 564, 'Il vit sur terre et en mer.\r\nSes bras surpuissants peuvent percer\r\ndes trous dans la coque des paquebots.\r\n', 6, 11, 5),
(566, 'archen', 'Arkéapti', 55, 112, 45, 74, 45, 70, NULL, 'On dit qu’il serait l’ancêtre de tous les\r\nPokémon Oiseaux. Il ne sait pas voler et\r\nse déplace en sautant d’arbre en arbre.\r\n', 3, 6, 5),
(567, 'archeops', 'Aéroptéryx', 75, 140, 65, 112, 65, 110, 566, 'Il s’envole après avoir pris de l’élan.\r\nIl est assez intelligent pour chasser\r\nses proies en groupe.\r\n', 3, 6, 5),
(568, 'trubbish', 'Miamiasme', 50, 50, 62, 40, 62, 65, NULL, 'Il aime les endroits mal tenus.\r\nLe gaz qu’il rote endort\r\npendant une semaine.\r\n', 4, NULL, 5),
(569, 'garbodor', 'Miasmax', 80, 95, 82, 60, 82, 75, 568, 'Il étrangle ses ennemis avec son bras\r\ndroit et les achève avec le gaz puant et\r\nempoisonné qu’il exhale par la bouche.\r\n', 4, NULL, 5),
(570, 'zorua', 'Zorua', 40, 65, 40, 80, 40, 65, NULL, 'Il a le pouvoir de prendre l’apparence\r\ndes autres. On dit qu’il se transforme\r\nsouvent en petit enfant silencieux.\r\n', 17, NULL, 5),
(571, 'zoroark', 'Zoroark', 60, 105, 60, 120, 60, 105, 570, 'Ces Pokémon protègent les leurs en\r\nprenant l’apparence de leurs ennemis.\r\nIls forment des groupes très soudés.\r\n', 17, NULL, 5),
(572, 'minccino', 'Chinchidou', 55, 50, 40, 40, 40, 75, NULL, 'Ils se saluent en se caressant\r\nmutuellement le corps avec la queue,\r\nqu’ils gardent toujours immaculée.\r\n', 1, NULL, 5),
(573, 'cinccino', 'Pashmilla', 75, 95, 60, 65, 60, 115, 572, 'Leurs poils blancs sont recouverts\r\nd’une graisse spéciale qui leur permet\r\nd’esquiver facilement les attaques.\r\n', 1, NULL, 5),
(574, 'gothita', 'Scrutella', 45, 30, 50, 55, 65, 45, NULL, 'Concentre des pouvoirs psychiques dans\r\nses antennes semblables à des rubans,\r\net regarde fixement... mais quoi?\r\n', 14, NULL, 5),
(575, 'gothorita', 'Mesmérella', 60, 45, 70, 75, 85, 55, 574, 'Hypnotise les Pokémon et les gens. Il y a\r\nde nombreuses légendes de gens enlevés\r\ndans leur sommeil par des Mesmérella.\r\n', 14, NULL, 5),
(576, 'gothitelle', 'Sidérella', 70, 55, 95, 95, 110, 65, 575, 'Ses pouvoirs psychiques plient l’espace\r\naux alentours. On y voit des étoiles\r\néloignées de millions d’années-lumière.\r\n', 14, NULL, 5),
(577, 'solosis', 'Nucléos', 45, 30, 40, 105, 50, 20, NULL, 'Ils repoussent leurs assaillants\r\navec leurs pouvoirs psychiques et\r\ncommuniquent entre eux par télépathie.\r\n', 14, NULL, 5),
(578, 'duosion', 'Méios', 65, 40, 50, 125, 60, 30, 577, 'Il possède deux cerveaux séparés\r\nqui le font parfois agir de façon\r\ntotalement incohérente.\r\n', 14, NULL, 5),
(579, 'reuniclus', 'Symbios', 110, 65, 75, 125, 85, 30, 578, 'Quand ils se tiennent par la main,\r\nleurs cerveaux forment un réseau qui\r\ndémultiplie leurs pouvoirs psychiques.\r\n', 14, NULL, 5),
(580, 'ducklett', 'Couaneton', 62, 44, 50, 44, 50, 55, NULL, 'Un Pokémon Oiseau doué pour la plongée.\r\nIl nage sous l’eau à la recherche de\r\nsphaigne, son plat préféré.\r\n', 3, 11, 5),
(581, 'swanna', 'Lakmécygne', 75, 87, 63, 87, 63, 98, 580, 'Ils se mettent à danser à la tombée\r\nde la nuit. Le Lakmécygne qui danse\r\nau centre est le chef de la troupe.\r\n', 3, 11, 5),
(582, 'vanillite', 'Sorbébé', 36, 50, 50, 65, 60, 44, NULL, 'Il exhale une haleine à -50 °C.\r\nIl fabrique des cristaux de neige\r\net fait neiger autour de lui.\r\n', 15, NULL, 5),
(583, 'vanillish', 'Sorboul', 51, 65, 65, 80, 75, 59, 582, 'Un Pokémon qui vit dans les montagnes\r\nenneigées. Il a émigré au sud durant\r\nl’ère glaciaire, il y a bien longtemps.\r\n', 15, NULL, 5),
(584, 'vanilluxe', 'Sorbouboul', 71, 95, 85, 110, 95, 79, 583, 'Il avale de l’eau en grande quantité\r\net la transforme en nuages neigeux.\r\nIl souffle du blizzard sur ses ennemis.\r\n', 15, NULL, 5),
(585, 'deerling', 'Vivaldaim', 60, 60, 50, 40, 50, 75, NULL, 'Son pelage prend la même couleur et la\r\nmême odeur que les hautes herbes. S’il\r\nsent un ennemi, il court s’y réfugier.\r\n', 1, 12, 5),
(586, 'sawsbuck', 'Haydaim', 80, 100, 70, 60, 70, 95, 585, 'Il change d’habitat selon les saisons.\r\nLes gens constatent le passage des\r\nsaisons en regardant ses ramures.\r\n', 1, 12, 5),
(587, 'emolga', 'Emolga', 55, 75, 60, 75, 60, 103, NULL, 'Il accumule dans ses membranes\r\nl’électricité qu’il produit dans ses\r\nbajoues, et la relâche tout en planant.\r\n', 3, 13, 5),
(588, 'karrablast', 'Carabing', 50, 75, 45, 40, 45, 60, NULL, 'Un Pokémon mystérieux qui évolue quand\r\nil reçoit un stimulus électrique en étant\r\nproche d’un Escargaume.\r\n', 7, NULL, 5),
(589, 'escavalier', 'Lançargot', 70, 135, 105, 60, 105, 20, 588, 'Il vole à toute vitesse et frappe tout\r\nennemi de ses lances pointues. Il combat\r\nbravement contre les plus forts que lui.\r\n', 7, 9, 5),
(590, 'foongus', 'Trompignon', 69, 55, 45, 55, 55, 15, NULL, 'Grâce à sa ressemblance mystérieuse\r\navec une Poké Ball, il attire ses ennemis\r\net relâche des spores empoisonnées.\r\n', 4, 12, 5),
(591, 'amoonguss', 'Gaulet', 114, 85, 70, 85, 80, 30, 590, 'Il danse en faisant tournoyer les\r\ncoupoles en forme de Poké Ball de\r\nses bras pour attirer ses proies.\r\n', 4, 12, 5),
(592, 'frillish', 'Viskuse', 55, 40, 50, 65, 85, 40, NULL, 'Il s’enfonce dans les profondeurs de la\r\nmer en emmenant son ennemi, qu’il ligote\r\navec ses bras fins comme des voiles.\r\n', 8, 11, 5),
(593, 'jellicent', 'Moyade', 100, 60, 70, 85, 105, 60, 592, 'Les bateaux qui s’aventurent sur\r\nson territoire coulent, et Moyade\r\naspire l’énergie vitale des marins.\r\n', 8, 11, 5),
(594, 'alomomola', 'Mamanbo', 165, 75, 80, 40, 45, 65, NULL, 'La membrane spéciale qui entoure\r\nson corps a la capacité de guérir\r\nles blessures.\r\n', 11, NULL, 5),
(595, 'joltik', 'Statitik', 50, 47, 50, 57, 50, 65, NULL, 'Les Statitik qui vivent en ville ont\r\nappris à aspirer le courant depuis\r\nles prises électriques des maisons.\r\n', 7, 13, 5),
(596, 'galvantula', 'Mygavolt', 70, 77, 60, 97, 60, 108, 595, 'Quand on l’attaque, il projette une\r\nmultitude de fils électrifiés pour\r\ncréer une barrière électrique.\r\n', 7, 13, 5),
(597, 'ferroseed', 'Grindur', 44, 50, 91, 24, 86, 10, NULL, 'Quand il sent un danger, il réagit en\r\nprojetant toutes ses épines d’un coup\r\net s’enfuit en roulant sur lui-même.\r\n', 9, 12, 5),
(598, 'ferrothorn', 'Noacier', 74, 94, 131, 54, 116, 20, 597, 'Il combat avec ses trois tentacules\r\népineux. Un coup de ses épines d’acier\r\npeut briser en deux de gros rochers.\r\n', 9, 12, 5),
(599, 'klink', 'Tic', 40, 55, 70, 45, 60, 30, NULL, 'La compatibilité entre ses deux rouages\r\nest prédéterminée. Ceux-ci ne se\r\nfixeront à aucun autre.\r\n', 9, NULL, 5),
(600, 'klang', 'Clic', 60, 80, 95, 70, 85, 50, 599, 'Ils communiquent entre eux en changeant\r\nleur sens de rotation. Plus ils sont en\r\ncolère, plus ils tournent vite.\r\n', 9, NULL, 5),
(601, 'klinklang', 'Cliticlic', 60, 100, 115, 70, 85, 90, 600, 'Son noyau rouge lui permet d’accumuler\r\nde l’énergie qu’il projette ensuite aux\r\nalentours grâce à ses pics.\r\n', 9, NULL, 5),
(602, 'tynamo', 'Anchwatt', 35, 55, 40, 45, 40, 60, NULL, 'Tout seuls, ils ne dégagent pas beaucoup\r\nd’électricité, mais un banc d’Anchwatt\r\nproduit la même puissance qu’un éclair.\r\n', 13, NULL, 5),
(603, 'eelektrik', 'Lampéroie', 65, 85, 70, 75, 70, 40, 602, 'Ses taches rondes émettent de\r\nl’électricité. Il s’enroule autour\r\nde ses proies et les électrocute.\r\n', 13, NULL, 5),
(604, 'eelektross', 'Ohmassacre', 85, 115, 80, 105, 80, 50, 603, 'Il peut sortir de l’eau à la force de ses\r\nbras pour attraper des proies sur la\r\nrive. Il retourne à l’eau en un instant.\r\n', 13, NULL, 5),
(605, 'elgyem', 'Lewsor', 55, 55, 55, 85, 55, 30, NULL, 'Avec son puissant pouvoir psychique,\r\nil peut compresser le cerveau de ses\r\nennemis et déclencher des migraines.\r\n', 14, NULL, 5),
(606, 'beheeyem', 'Neitram', 75, 75, 75, 125, 95, 40, 605, 'Il peut manipuler les souvenirs de\r\nses ennemis et fait clignoter ses doigts\r\ntricolores pour discuter avec ses amis.\r\n', 14, NULL, 5),
(607, 'litwick', 'Funécire', 50, 30, 55, 65, 55, 20, NULL, 'Sa flamme brûle en consumant\r\nl’énergie vitale des gens et\r\ndes Pokémon.\r\n', 8, 10, 5),
(608, 'lampent', 'Mélancolux', 60, 40, 60, 95, 60, 55, 607, 'Un Pokémon craint pour être de mauvais\r\naugure. Il erre dans les villes à la\r\nrecherche d’âmes de défunts.\r\n', 8, 10, 5),
(609, 'chandelure', 'Lugulabre', 60, 55, 90, 145, 90, 80, 608, 'Il brûle en aspirant les âmes, et\r\nhypnotise ses ennemis en faisant\r\nosciller les flammes de ses bras.\r\n', 8, 10, 5),
(610, 'axew', 'Coupenotte', 46, 87, 60, 30, 40, 57, NULL, 'Il broie les Baies à grands coups de\r\ncrocs. Ceux-ci repoussent plusieurs\r\nfois, toujours plus forts et aiguisés.\r\n', 16, NULL, 5),
(611, 'fraxure', 'Incisache', 66, 117, 70, 40, 50, 67, 610, 'Comme ses crocs brisés ne repoussent\r\npas, il les aiguise après le combat grâce\r\nà une pierre ramassée près d’un fleuve.\r\n', 16, NULL, 5),
(612, 'haxorus', 'Tranchodon', 76, 147, 90, 60, 70, 97, 611, 'Il est doux, mais ne pardonne pas à ceux\r\nqui viennent saccager son territoire.\r\nSes crocs peuvent découper de l’acier.\r\n', 16, NULL, 5),
(613, 'cubchoo', 'Polarhume', 55, 70, 40, 60, 40, 40, NULL, 'Quand il est malade, sa goutte au nez\r\ndégèle et ses capacités de type Glace\r\ndeviennent moins efficaces.\r\n', 15, NULL, 5),
(614, 'beartic', 'Polagriffe', 95, 130, 80, 70, 80, 50, 613, 'Il fait lui-même geler son haleine. Il\r\nest bon nageur et sillonne les mers\r\ndu nord à la recherche de ses proies.\r\n', 15, NULL, 5),
(615, 'cryogonal', 'Hexagel', 80, 50, 50, 95, 135, 105, NULL, 'Si sa température monte, il se dissipe\r\nen vapeur d’eau et disparaît. Quand\r\nelle diminue, il redevient de glace.\r\n', 15, NULL, 5),
(616, 'shelmet', 'Escargaume', 50, 40, 85, 40, 65, 25, NULL, 'Quand on l’attaque, il referme sa\r\nvisière pour se défendre, et\r\nprojette un poison visqueux.\r\n', 7, NULL, 5),
(617, 'accelgor', 'Limaspeed', 80, 70, 40, 100, 60, 145, 616, 'Quand son corps s’assèche, il faiblit.\r\nIl s’en prémunit en s’enroulant dans\r\nplusieurs couches d’une fine muqueuse.\r\n', 7, NULL, 5),
(618, 'stunfisk', 'Limonde', 109, 66, 84, 81, 99, 32, NULL, 'Sa peau est si dure qu’il peut se faire\r\npiétiner par un sumo sans rien sentir.\r\nIl sourit en émettant de l’électricité.\r\n', 5, 13, 5),
(619, 'mienfoo', 'Kungfouine', 45, 85, 50, 55, 50, 65, NULL, 'Il découpe ses ennemis avec ses griffes\r\nacérées et les terrasse avec ses\r\nenchaînements de coups hyper fluides.\r\n', 2, NULL, 5),
(620, 'mienshao', 'Shaofouine', 65, 125, 60, 95, 60, 105, 619, 'Il se sert des poils de ses pattes\r\nantérieures comme de fouets. Ses coups\r\nultrarapides sont invisibles à l’œil nu.\r\n', 2, NULL, 5),
(621, 'druddigon', 'Drakkarmin', 77, 120, 90, 60, 90, 48, NULL, 'Il utilise ses ailes pour emmagasiner la\r\nchaleur du soleil. Si la température de\r\nson corps baisse, il ne peut plus bouger.\r\n', 16, NULL, 5),
(622, 'golett', 'Gringolem', 59, 74, 50, 35, 50, 35, NULL, 'Il se meut grâce à l’énergie stockée\r\ndans son corps. Personne ne sait d’où\r\nvient cette énergie mystérieuse.\r\n', 5, 8, 5),
(623, 'golurk', 'Golemastoc', 89, 124, 80, 55, 80, 55, 622, 'Il vole à la vitesse du son. Si le\r\nsceau sur sa poitrine est retiré,\r\nil perd tout contrôle de lui-même.\r\n', 5, 8, 5),
(624, 'pawniard', 'Scalpion', 45, 85, 70, 40, 40, 60, NULL, 'Tout son corps est fait de lames.\r\nSi une lame s’émousse, il va l’aiguiser\r\ncontre une pierre au bord d’une rivière.\r\n', 9, 17, 5),
(625, 'bisharp', 'Scalproie', 65, 125, 100, 60, 70, 70, 624, 'Ils dirigent les groupes de Scalpion.\r\nIls se battent pour la suprématie sur le\r\ngroupe, et les perdants en sont exclus.\r\n', 9, 17, 5),
(626, 'bouffalant', 'Frison', 95, 110, 95, 40, 95, 55, NULL, 'Son pelage fourni absorbe les chocs\r\net lui permet d’asséner sans broncher\r\nle plus violent des coups de tête.\r\n', 1, NULL, 5),
(627, 'rufflet', 'Furaiglon', 70, 83, 50, 37, 50, 60, NULL, 'Il casse les Baies avec ses serres.\r\nIl combat courageusement, quelle que\r\nsoit la force de l’adversaire.\r\n', 1, 3, 5),
(628, 'braviary', 'Gueriaigle', 100, 123, 75, 57, 75, 80, 627, 'Pour les siens, il combat au mépris du\r\ndanger. Il est capable de soulever\r\nune voiture haut dans le ciel.\r\n', 1, 3, 5),
(629, 'vullaby', 'Vostourno', 70, 55, 75, 45, 65, 60, NULL, 'Ses ailes sont trop petites pour lui\r\npermettre de voler. Quand l’évolution\r\napproche, il sort lui-même de sa coquille.\r\n', 3, 17, 5),
(630, 'mandibuzz', 'Vaututrice', 110, 65, 105, 55, 95, 80, 629, 'Il construit des nids en os. Il attrape\r\nles proies affaiblies avec ses serres\r\net les transporte allègrement au nid.\r\n', 3, 17, 5),
(631, 'heatmor', 'Aflamanoir', 85, 97, 66, 105, 66, 65, NULL, 'Il aspire de l’air par la queue et\r\nentretient un brasier dans son corps.\r\nC’est le prédateur des Fermite.\r\n', 10, NULL, 5),
(632, 'durant', 'Fermite', 58, 109, 112, 48, 48, 109, NULL, 'Ils portent une armure de fer et se\r\nregroupent pour contrer les attaques\r\nde leurs prédateurs, les Aflamanoir.\r\n', 7, 9, 5),
(633, 'deino', 'Solochi', 52, 65, 50, 45, 50, 38, NULL, 'Il mord tout ce qui bouge, et mange tout\r\nce qu’il trouve. Il vaut mieux éviter\r\nde s’en approcher.\r\n', 16, 17, 5),
(634, 'zweilous', 'Diamat', 72, 85, 70, 65, 70, 58, 633, 'Ses deux têtes s’entendent mal. Même en\r\nmangeant, elles se cherchent des noises\r\net dévorent à qui mieux mieux.\r\n', 16, 17, 5),
(635, 'hydreigon', 'Trioxhydre', 92, 105, 90, 125, 90, 98, 634, 'Un Pokémon violent qui patrouille le ciel\r\navec ses six ailes et se jette sur tout\r\nce qui bouge, pensant voir un ennemi.\r\n', 16, 17, 5),
(636, 'larvesta', 'Pyronille', 55, 85, 55, 50, 55, 60, NULL, 'On dit que ce Pokémon serait né du\r\nsoleil. Lorsqu’il évolue, tout son\r\ncorps est enveloppé de flammes.\r\n', 7, 10, 5),
(637, 'volcarona', 'Pyrax', 85, 60, 65, 135, 105, 100, 636, 'On dit que quand le ciel s’est obscurci à\r\ncause des cendres d’un volcan, le feu\r\nd’un Pyrax aurait remplacé le soleil.\r\n', 7, 10, 5),
(638, 'cobalion', 'Cobaltium', 91, 90, 129, 90, 72, 108, NULL, 'Un Pokémon légendaire qui a combattu\r\nles humains pour protéger les Pokémon.\r\nIl est d’un naturel calme et posé.\r\n', 2, 9, 5),
(639, 'terrakion', 'Terrakium', 91, 129, 90, 72, 90, 108, NULL, 'Il a combattu les humains pour protéger\r\nles Pokémon ayant perdu leur foyer à\r\ncause de leurs guerres fratricides.\r\n', 2, 6, 5),
(640, 'virizion', 'Viridium', 91, 90, 72, 90, 129, 108, NULL, 'Il a défié les humains pour protéger\r\nles siens. Son nom est resté dans\r\nla légende.\r\n', 2, 12, 5),
(641, 'tornadus-incarnate', 'Boréas', 79, 115, 70, 125, 80, 111, NULL, 'Le bas de son corps est entouré d’une\r\nmasse d’énergie semblable à un nuage.\r\nIl vole dans le ciel à 300 km/h.\r\n', 3, NULL, 5),
(642, 'thundurus-incarnate', 'Fulguris', 79, 115, 70, 125, 80, 111, NULL, 'Les terres qu’il a survolées sont\r\nrecouvertes d’innombrables traces\r\ndes éclairs qu’il y a fait tomber.\r\n', 3, 13, 5),
(643, 'reshiram', 'Reshiram', 100, 120, 100, 150, 120, 90, NULL, 'Un Pokémon mythique.\r\nIl projette de sa queue un tourbillon\r\nde flammes capable de tout emporter.\r\n', 10, 16, 5),
(644, 'zekrom', 'Zekrom', 100, 150, 120, 120, 100, 90, NULL, 'Sa queue produit de l’électricité.\r\nIl dissimule son corps dans un nuage\r\nélectrique et plane dans le ciel d’Unys.\r\n', 13, 16, 5),
(645, 'landorus-incarnate', 'Démétéros', 89, 125, 90, 115, 80, 101, NULL, 'Il fait fructifier les cultures partout\r\noù il passe, c’est pourquoi on l’appelle\r\n“le dieu des moissons”.\r\n', 3, 5, 5),
(646, 'kyurem', 'Kyurem', 125, 130, 90, 130, 90, 95, NULL, 'Son corps produit une énergie à très\r\nbasse température, mais le souffle \r\nglacé qu’il exhale l’a gelé lui-même.\r\n', 15, 16, 5),
(647, 'keldeo-ordinary', 'Keldeo', 91, 72, 90, 129, 90, 108, NULL, 'Il projette de l’eau depuis ses sabots\r\net se déplace ainsi à grande vitesse.\r\nSa botte secrète est le coup de sabot.\r\n', 2, 11, 5),
(648, 'meloetta-aria', 'Meloetta', 100, 77, 77, 128, 128, 90, NULL, 'Sa voix si particulière lui permet de\r\nchanter des mélodies qui ensorcellent\r\nles gens et modifient leurs émotions.\r\n', 1, 14, 5),
(649, 'genesect', 'Genesect', 71, 120, 95, 120, 95, 99, NULL, 'Il y a 300 millions d’années, il était\r\ncraint comme l’ultime prédateur. Il a\r\nété modifié par la Team Plasma.\r\n', 7, 9, 5),
(650, 'chespin', 'Marisson', 56, 61, 65, 48, 45, 38, NULL, 'Lorsqu’il rassemble ses forces, les piquants souples\r\nqui recouvrent sa tête deviennent si durs et acérés\r\nqu’ils pourraient transpercer un rocher.\r\n', 12, NULL, 6),
(651, 'quilladin', 'Boguérisse', 61, 78, 95, 56, 58, 57, 650, 'La carapace qui recouvre son corps le protège\r\ndes attaques adverses, et ses piquants lui permettent\r\nde contre-attaquer.\r\n', 12, NULL, 6),
(652, 'chesnaught', 'Blindépique', 88, 107, 122, 74, 75, 64, 651, 'Il est si puissant qu’il pourrait renverser un char de\r\n50 t d’un seul assaut. Il se sert de son corps comme\r\nd’un bouclier pour protéger ses alliés.\r\n', 2, 12, 6),
(653, 'fennekin', 'Feunnec', 40, 45, 40, 62, 60, 60, NULL, 'En cas de coup de fatigue, il grignote des rameaux\r\npour recharger ses batteries. Ses oreilles dégagent\r\nune chaleur qui dépasse les 200 °C.\r\n', 10, NULL, 6),
(654, 'braixen', 'Roussil', 59, 59, 58, 90, 70, 73, 653, 'Il allume la branche plantée dans sa queue en la\r\nfrottant contre son pelage et s’en sert au combat.\r\n', 10, NULL, 6),
(655, 'delphox', 'Goupelin', 75, 69, 72, 114, 100, 104, 654, 'Peut prédire l’avenir s’il se concentre en fixant\r\nla flamme qui danse au bout de sa canne.\r\n', 10, 14, 6),
(656, 'froakie', 'Grenousse', 41, 56, 40, 62, 44, 71, NULL, 'Sa poitrine et son dos sécrètent une mousse qui\r\nlui permet de subir moins de dégâts en amortissant\r\nle choc des attaques.\r\n', 11, NULL, 6),
(657, 'frogadier', 'Croâporal', 54, 63, 52, 83, 56, 97, 656, 'Il est capable de lancer des pierres recouvertes\r\nde mousse avec une précision suffisante pour toucher\r\nune canette vide à 30 m.\r\n', 11, NULL, 6),
(658, 'greninja', 'Amphinobi', 72, 95, 67, 103, 71, 122, 657, 'Il transforme des jets d’eau sous pression\r\nen redoutables shuriken. Une fois lancés, ils tournent\r\nsi vite qu’ils peuvent même couper le métal.\r\n', 11, 17, 6),
(659, 'bunnelby', 'Sapereau', 38, 36, 38, 32, 36, 57, NULL, 'Ses larges oreilles lui permettent de creuser\r\ndes terriers. Il est capable de forer sans s’arrêter\r\npendant toute une nuit.\r\n', 1, NULL, 6),
(660, 'diggersby', 'Excavarenne', 85, 56, 77, 50, 77, 78, 659, 'Ses grandes oreilles sont si musclées qu’il peut\r\nsoulever un rocher d’une tonne sans broncher.\r\nCe Pokémon est souvent utilisé sur les chantiers.\r\n', 1, 5, 6),
(661, 'fletchling', 'Passerouge', 45, 50, 43, 40, 38, 62, NULL, 'Peu farouche, il communique avec ses congénères\r\npar un doux gazouillis et par des signaux de la queue.\r\n', 1, 3, 6),
(662, 'fletchinder', 'Braisillon', 62, 73, 55, 56, 52, 84, 661, 'Il crache des étincelles pour brûler les hautes herbes\r\net attraper les proies surprises qui tentent d’échapper\r\nà l’incendie.\r\n', 3, 10, 6),
(663, 'talonflame', 'Flambusard', 78, 81, 71, 74, 69, 126, 662, 'Exalté par un combat sans merci, il s’envole en\r\nsemant dans son sillage les escarbilles du feu qui\r\ncouve dans son plumage.\r\n', 3, 10, 6),
(664, 'scatterbug', 'Lépidonille', 38, 35, 40, 27, 25, 35, NULL, 'Lorsqu’il est poursuivi par un Pokémon Oiseau,\r\nil projette une poudre noire qui paralyse tous ceux\r\nqu’elle touche.\r\n', 7, NULL, 6),
(665, 'spewpa', 'Pérégrain', 45, 22, 60, 27, 30, 29, 664, 'Vit caché sous le couvert des buissons.\r\nPoursuivi par un ennemi, il tente de l’intimider\r\nen hérissant les poils qui lui couvrent le corps.\r\n', 7, NULL, 6),
(666, 'vivillon', 'Prismillon', 80, 52, 50, 90, 50, 89, 665, 'Les ailes des Prismillon arborent différents motifs\r\nen fonction de leur aire d’origine. Il est possible que\r\nle climat y soit pour quelque chose.\r\n', 3, 7, 6),
(667, 'litleo', 'Hélionceau', 62, 50, 58, 73, 54, 72, NULL, 'Plus l’adversaire auquel il est confronté est fort,\r\nplus la température de sa crinière augmente, l’énergie\r\ngénérée se diffusant alors dans tout son corps.\r\n', 1, 10, 6),
(668, 'pyroar', 'Némélios', 86, 68, 72, 109, 66, 106, 667, 'Le mâle qui possède la crinière de feu la plus\r\nimposante devient le chef de la horde.\r\n', 1, 10, 6),
(669, 'flabebe', 'Flabébé', 44, 38, 39, 61, 79, 42, NULL, 'Capable de libérer et de contrôler la force cachée\r\ndes plantes. La fleur qu’il tient fait partie de lui.\r\n', 18, NULL, 6),
(670, 'floette', 'Floette', 54, 45, 47, 75, 98, 52, 669, 'Il survole les champs de fleurs, et prend soin de\r\ncelles qui sont sur le point de faner. Au combat,\r\nil fait appel à leur force cachée.\r\n', 18, NULL, 6),
(671, 'florges', 'Florges', 78, 65, 68, 112, 154, 75, 670, 'Les jardins fleuris sont son territoire.\r\nIl se baigne dans l’énergie dégagée par les plantes\r\nen fleurs pour l’absorber.\r\n', 18, NULL, 6),
(672, 'skiddo', 'Cabriolaine', 66, 65, 48, 62, 57, 52, NULL, 'Ce Pokémon paisible serait le premier à avoir jamais\r\ncohabité avec les humains.\r\n', 12, NULL, 6),
(673, 'gogoat', 'Chevroum', 123, 100, 62, 97, 81, 68, 672, 'Quand son Dresseur le tient par une corne, il est\r\ncapable de deviner ses plus subtils changements\r\nd’humeur et de s’y adapter.\r\n', 12, NULL, 6),
(674, 'pancham', 'Pandespiègle', 67, 82, 62, 46, 48, 43, NULL, 'Il fait son possible pour intimider ses adversaires\r\navec un regard menaçant, mais sans succès. Il a\r\nl’habitude de constamment mâcher une feuille.\r\n', 2, NULL, 6),
(675, 'pangoro', 'Pandarbare', 95, 124, 78, 69, 71, 58, 674, 'D’un tempérament fougueux, il ne supporte pas que\r\nl’on s’en prenne aux faibles. Sa feuille lui permet\r\nde deviner les actions de l’ennemi.\r\n', 2, 17, 6),
(676, 'furfrou', 'Couafarel', 75, 80, 60, 65, 90, 102, NULL, 'Si son abondante fourrure gagne à être toilettée,\r\nce n’est pas qu’une question de style. En effet, cela\r\nlui permet également de se mouvoir plus librement.\r\n', 1, NULL, 6),
(677, 'espurr', 'Psystigri', 62, 48, 54, 63, 60, 68, NULL, 'Il couvre la source de ses pouvoirs psychiques hors\r\ndu commun avec ses oreilles afin d’éviter\r\nles accidents.\r\n', 14, NULL, 6),
(678, 'meowstic-male', 'Mistigrix', 74, 48, 76, 83, 81, 104, 677, 'S’il se sent menacé, il lève les oreilles et déchaîne\r\nun pouvoir psychique capable de réduire en miettes\r\nun camion de 10 t.\r\n', 14, NULL, 6),
(679, 'honedge', 'Monorpale', 45, 80, 100, 35, 37, 28, NULL, 'Ce Pokémon naît quand l’âme d’un défunt prend\r\npossession d’une épée ancienne. Il s’accroche aux\r\nhumains pour absorber leur énergie vitale.\r\n', 8, 9, 6),
(680, 'doublade', 'Dimoclès', 59, 110, 150, 45, 49, 35, 679, 'En évoluant, Monorpale se divise en deux épées qui\r\ncommuniquent par télépathie pour mieux découper\r\nleur adversaire en morceaux.\r\n', 8, 9, 6),
(681, 'aegislash-shield', 'Exagide', 60, 50, 150, 50, 150, 60, 680, 'Ils ont fait le bonheur de générations de rois.\r\nLeurs pouvoirs psychiques leur permettent de\r\ncontrôler hommes et Pokémon.\r\n', 8, 9, 6),
(682, 'spritzee', 'Fluvetin', 78, 52, 60, 63, 65, 23, NULL, 'Son corps dégage un parfum qui envoûte ceux qui le\r\nrespirent et change en fonction de son alimentation.\r\n', 18, NULL, 6),
(683, 'aromatisse', 'Cocotine', 101, 72, 72, 99, 89, 29, 682, 'Ce Pokémon est capable d’émettre toutes sortes\r\nd’odeurs pour incommoder son adversaire et\r\naugmenter ses chances de gagner.\r\n', 18, NULL, 6),
(684, 'swirlix', 'Sucroquin', 62, 48, 66, 59, 57, 49, NULL, 'Il immobilise son adversaire à l’aide de filaments\r\nblancs, sucrés et collants qui ressemblent à de la\r\nbarbe à papa.\r\n', 18, NULL, 6),
(685, 'slurpuff', 'Cupcanaille', 82, 80, 86, 85, 75, 72, 684, 'La finesse de son odorat lui permet de distinguer\r\nles nuances de parfums les plus subtiles, ce qui en\r\nfait un compagnon idéal pour les pâtissiers.\r\n', 18, NULL, 6),
(686, 'inkay', 'Sepiatop', 53, 54, 53, 37, 46, 45, NULL, 'Éblouis par les points lumineux qui clignotent sur son\r\ncorps, les ennemis perdent toute envie de se battre.\r\n', 14, 17, 6),
(687, 'malamar', 'Sepiatroce', 86, 92, 88, 68, 75, 73, 686, 'Doté des pouvoirs hypnotiques les plus puissants\r\ndu règne des Pokémon, il manipule son adversaire\r\nà sa guise.\r\n', 14, 17, 6),
(688, 'binacle', 'Opermine', 42, 52, 67, 39, 56, 50, NULL, 'Chaque rocher abrite deux Opermine.\r\nS’ils se disputent, l’un d’entre eux part à la recherche\r\nd’un nouveau logis.\r\n', 6, 11, 6),
(689, 'barbaracle', 'Golgopathe', 72, 105, 115, 54, 86, 68, 688, 'Après évolution, les deux Opermine laissent place\r\nà sept créatures qui ont la puissance cumulée de\r\nsept Opermine.\r\n', 6, 11, 6),
(690, 'skrelp', 'Venalgue', 50, 60, 60, 60, 60, 30, NULL, 'Il se cache parmi les algues en décomposition pour\r\nmieux approcher ses proies, qu’il achève en les\r\naspergeant de poison.\r\n', 4, 11, 6),
(691, 'dragalge', 'Kravarech', 65, 75, 90, 97, 123, 44, 690, 'Il asperge tous ceux qui osent pénétrer sur son\r\nterritoire d’un poison assez corrosif pour dissoudre\r\nla coque d’un pétrolier.\r\n', 4, 16, 6),
(692, 'clauncher', 'Flingouste', 50, 53, 62, 58, 63, 44, NULL, 'Sa pince projette un jet d’eau sous pression\r\nsuffisamment puissant pour abattre ses proies en plein\r\nvol.\r\n', 11, NULL, 6),
(693, 'clawitzer', 'Gamblast', 71, 73, 88, 120, 89, 59, 692, 'Son énorme pince projette de véritables obus d’eau\r\ncapables de transpercer la coque d’un pétrolier.\r\n', 11, NULL, 6),
(694, 'helioptile', 'Galvaran', 44, 38, 33, 61, 43, 70, NULL, 'Ce Pokémon vit dans le désert.\r\nIl n’a pas besoin de se nourrir, car il est capable\r\nd’absorber l’énergie du soleil.\r\n', 1, 13, 6),
(695, 'heliolisk', 'Iguolta', 62, 55, 52, 109, 94, 109, 694, 'L’électricité que produit un seul Iguolta en agitant\r\nsa collerette suffit à alimenter un immeuble entier.\r\n', 1, 13, 6),
(696, 'tyrunt', 'Ptyranidur', 58, 89, 77, 45, 45, 48, NULL, 'Ramené à la vie à partir d’un fossile, ce Pokémon très\r\ncapricieux devient violent à la moindre contrariété.\r\n', 6, 16, 6),
(697, 'tyrantrum', 'Rexillius', 82, 121, 119, 69, 59, 71, 696, 'Dans les temps anciens, aucun Pokémon ne pouvait\r\nrivaliser avec lui. Ses puissantes mâchoires peuvent\r\nréduire une épaisse plaque de métal en confettis.\r\n', 6, 16, 6),
(698, 'amaura', 'Amagara', 77, 59, 50, 67, 63, 46, NULL, 'Pokémon ancien, ramené à la vie après la découverte\r\nd’une partie de son corps conservée dans la glace\r\npendant 100 millions d’années.\r\n', 6, 15, 6),
(699, 'aurorus', 'Dragmara', 123, 77, 72, 99, 92, 58, 698, 'Les cristaux qui parsèment son corps lui permettent\r\nde piéger ses ennemis dans la glace en émettant\r\nde l’air refroidi à -150 °C.\r\n', 6, 15, 6),
(700, 'sylveon', 'Nymphali', 95, 65, 65, 110, 130, 60, 133, 'Pour arrêter le combat, il émet des ondes apaisantes\r\navec ses rubans.\r\n', 18, NULL, 6),
(701, 'hawlucha', 'Brutalibré', 78, 92, 75, 74, 63, 118, NULL, 'Malgré sa petite taille, son intelligence lui permet\r\nd’affronter sans trembler de grands Pokémon tels que\r\nles Mackogneur ou les Hariyama.\r\n', 2, 3, 6),
(702, 'dedenne', 'Dedenne', 67, 58, 57, 81, 67, 101, NULL, 'Ses moustaches lui servent d’antennes, et lui\r\npermettent d’envoyer et de recevoir des signaux\r\npour communiquer à distance avec ses congénères.\r\n', 13, 18, 6),
(703, 'carbink', 'Strassie', 50, 50, 150, 50, 150, 50, NULL, 'Ce Pokémon vient des profondeurs de la terre,\r\noù la chaleur et la pression sont intenses.\r\nLa pierre qui orne sa tête émet de l’énergie.\r\n', 6, 18, 6),
(704, 'goomy', 'Mucuscule', 45, 50, 35, 55, 75, 40, NULL, 'C’est le plus faible de tous les Pokémon Dragon.\r\nPour éviter que son corps ne se dessèche,\r\nil recherche les lieux humides et ombragés.\r\n', 16, NULL, 6),
(705, 'sliggoo', 'Colimucus', 68, 75, 53, 83, 113, 60, 704, 'Il repousse ses ennemis en sécrétant un fluide\r\nqui dissout tout ce qu’il touche. Suite à une\r\ndégénérescence de ses yeux, il est aveugle.\r\n', 16, NULL, 6),
(706, 'goodra', 'Muplodocus', 90, 100, 70, 110, 150, 80, 705, 'Ce Pokémon Dragon peu farouche aime faire la fête\r\nà son Dresseur adoré, le couvrant au passage de\r\nsa bave collante.\r\n', 16, NULL, 6),
(707, 'klefki', 'Trousselin', 57, 80, 91, 80, 87, 75, NULL, 'Ce Pokémon collectionne les clés qu’il agite devant\r\nses ennemis pour les intimider.\r\n', 9, 18, 6),
(708, 'phantump', 'Brocélôme', 43, 70, 48, 50, 60, 38, NULL, 'Ce Pokémon apparaît lorsqu’un esprit possède\r\nun arbre en décomposition. Il vit dans les forêts où\r\nles humains n’osent pas s’aventurer.\r\n', 8, 12, 6),
(709, 'trevenant', 'Desséliande', 85, 110, 76, 65, 82, 56, 708, 'Commande aux autres arbres.\r\nIl emprisonne jusqu’à la fin de leurs jours les humains\r\nqui abîment la forêt.\r\n', 8, 12, 6),
(710, 'pumpkaboo-average', 'Pitrouille', 49, 66, 70, 44, 55, 51, NULL, 'Ce Pokémon est né du mariage d’un esprit piégé\r\ndans le monde des vivants et d’une citrouille.\r\nIl commence à s’animer au coucher du soleil.\r\n', 8, 12, 6),
(711, 'gourgeist-average', 'Banshitrouye', 65, 90, 122, 58, 75, 84, 710, 'Les nuits de nouvelle lune, ce Pokémon erre dans\r\nles rues en chantant d’une voix lugubre.\r\nCeux qui l’entendent sont maudits.\r\n', 8, 12, 6),
(712, 'bergmite', 'Grelaçon', 55, 69, 85, 32, 35, 28, NULL, 'La glace qui couvre son corps bloque les attaques\r\nennemies. Si elle se fissure, il la reconstitue\r\nimmédiatement en soufflant de l’air glacé.\r\n', 15, NULL, 6),
(713, 'avalugg', 'Séracrawl', 95, 117, 184, 44, 46, 28, 712, 'Son immense corps recouvert de glace est aussi\r\nsolide que de l’acier. Quand il se déplace, il écrase\r\ntout qui se trouve sur son passage.\r\n', 15, NULL, 6),
(714, 'noibat', 'Sonistrelle', 40, 30, 35, 45, 40, 55, NULL, 'Il apprécie les grottes les plus sombres.\r\nSes grandes oreilles lui permettent de générer\r\ndes ultrasons d’une fréquence de 200 000 Hz.\r\n', 3, 16, 6),
(715, 'noivern', 'Bruyverne', 85, 70, 80, 97, 80, 123, 714, 'Les nuits sans lune, il quadrille le ciel à la recherche\r\nd’une proie. Dans l’obscurité, aucun Pokémon ne peut\r\nrivaliser avec lui.\r\n', 3, 16, 6),
(716, 'xerneas', 'Xerneas', 126, 131, 95, 131, 98, 99, NULL, 'On dit que ce Pokémon immortel peut offrir ses forces\r\nvitales. Il a été ranimé après avoir passé 1 000 ans\r\nendormi sous la forme d’un arbre.\r\n', 18, NULL, 6),
(717, 'yveltal', 'Yveltal', 126, 131, 95, 131, 98, 99, NULL, 'Lorsque les plumes de sa queue et de ses ailes sont\r\ndéployées et virent au rouge, ce Pokémon légendaire\r\nabsorbe l’énergie vitale de ce qui l’entoure.\r\n', 3, 17, 6),
(718, 'zygarde', 'Zygarde', 108, 100, 121, 81, 95, 95, NULL, 'Lorsque l’écosystème de Kalos est menacé,\r\nce Pokémon apparaît et utilise son mystérieux pouvoir.\r\n', 5, 16, 6),
(719, 'diancie', 'Diancie', 50, 100, 150, 100, 150, 50, NULL, 'Ce Pokémon est une mutation spontanée de Strassie.\r\nSon corps rose et luisant est d’une beauté\r\nincomparable.\r\n', 6, 18, 6),
(720, 'hoopa', 'Hoopa', 80, 110, 60, 150, 130, 70, NULL, 'Ce fauteur de troubles est doté d’anneaux qui\r\ndéforment l’espace et lui permettent d’expédier\r\nau loin n’importe quel objet.\r\n', 8, 14, 6),
(721, 'volcanion', 'Volcanion', 80, 110, 120, 130, 90, 70, NULL, 'Il est capable de générer un nuage de vapeur pour\r\nse cacher. Il vit dans des montagnes inhabitées.\r\n', 10, 11, 6),
(722, 'rowlet', 'Brindibou', 68, 55, 55, 50, 50, 42, NULL, 'Ce Pokémon ne baisse jamais sa garde.\r\nPendant la journée, il accumule de l’énergie par\r\nphotosynthèse, pour mieux agir la nuit venue.\r\n', 3, 12, 7),
(723, 'dartrix', 'Efflèche', 78, 75, 75, 70, 70, 52, 722, 'Légèrement vaniteux, il aime prendre soin de\r\nses ailes quand il en a le temps. Il refuse parfois\r\nde se battre si ses plumes sont trop sales.\r\n', 3, 12, 7),
(724, 'decidueye', 'Archéduc', 78, 107, 75, 100, 100, 70, 723, 'Les plumes de ses ailes lui servent de flèches.\r\nSa précision au tir est si grande qu’il peut\r\natteindre un gravillon à 100 m.\r\n', 8, 12, 7),
(725, 'litten', 'Flamiaou', 45, 65, 40, 60, 40, 70, NULL, 'Les poils qu’il avale pendant sa toilette brûlent\r\net forment une flamme dans son ventre. Celle-ci\r\nchange selon la façon dont il la recrache.\r\n', 10, NULL, 7),
(726, 'torracat', 'Matoufeu', 65, 85, 50, 80, 50, 90, 725, 'Il porte à son cou une cloche de feu. Lorsqu’il\r\ncrache des flammes, cette dernière se met\r\nà tinter bruyamment.\r\n', 10, NULL, 7),
(727, 'incineroar', 'Félinferno', 95, 115, 90, 80, 90, 60, 726, 'Un Pokémon brutal qui n’en fait qu’à sa tête.\r\nSelon son humeur du moment, il lui arrive\r\nd’ignorer sciemment les ordres de son Dresseur.\r\n', 10, 17, 7),
(728, 'popplio', 'Otaquin', 50, 54, 54, 66, 56, 40, NULL, 'Connu pour son tempérament de battant,\r\nil rassemble l’eau de son corps au bout de son\r\nmuseau en un ballon qu’il projette sur l’ennemi.\r\n', 11, NULL, 7),
(729, 'brionne', 'Otarlette', 60, 69, 69, 91, 81, 50, 728, 'Danseur hors pair, il produit des ballons d’eau\r\nà la chaîne afin d’éclabousser l’adversaire lors\r\nd’un impitoyable ballet funeste.\r\n', 11, NULL, 7),
(730, 'primarina', 'Oratoria', 80, 74, 74, 126, 116, 60, 729, 'Il contrôle ses ballons d’eau par son chant, dont\r\nla mélodie, apprise auprès de ses semblables,\r\nse transmet de génération en génération.\r\n', 11, 18, 7),
(731, 'pikipek', 'Picassaut', 35, 75, 30, 30, 30, 65, NULL, 'Il creuse les arbres à raison de seize coups de\r\nbec par seconde. Le trou creusé lui sert ensuite\r\nà stocker des provisions ou à faire son nid.\r\n', 1, 3, 7),
(732, 'trumbeak', 'Piclairon', 55, 85, 50, 40, 50, 75, 731, 'Il garde toujours dans son bec les pépins des\r\nfruits qu’il mange, ce qui lui permet de faire feu\r\ndès qu’un ennemi se présente.\r\n', 1, 3, 7),
(733, 'toucannon', 'Bazoucan', 80, 120, 75, 75, 75, 60, 732, 'Pendant le combat, son bec chauffe et dépasse\r\nallègrement les 100 °C. Ses adversaires\r\nrepartent généralement avec de graves brûlures.\r\n', 1, 3, 7),
(734, 'yungoos', 'Manglouton', 48, 70, 30, 30, 30, 45, NULL, 'Avec ses crocs acérés, il ne peut s’empêcher\r\nde mordre tout ce qui bouge. Il fut introduit à\r\nAlola il y a longtemps, depuis une autre région.\r\n', 1, NULL, 7),
(735, 'gumshoos', 'Argouste', 88, 110, 60, 55, 60, 45, 734, 'Lorsqu’il est sur les traces d’une proie,\r\nce Pokémon n’est pas du genre à abandonner.\r\nSauf le soir, lorsqu’il tombe de sommeil.\r\n', 1, NULL, 7),
(736, 'grubbin', 'Larvibule', 47, 62, 45, 55, 45, 46, NULL, 'Il creuse l’écorce des arbres avec ses robustes\r\nmandibules pour en siroter la sève. Il vit en\r\ntemps normal dans le sol.\r\n', 7, NULL, 7),
(737, 'charjabug', 'Chrysapile', 57, 82, 95, 55, 75, 36, 736, 'Avec son corps capable de stocker l’électricité\r\ncomme une batterie, il a déjà sauvé la mise à\r\nplus d’un campeur peu prévoyant.\r\n', 7, 13, 7),
(738, 'vikavolt', 'Lucanon', 77, 70, 90, 145, 75, 43, 737, 'Il virevolte dans tous les sens, guettant la faille\r\npour frapper. Il concentre l’électricité dans ses\r\ngrandes mandibules pour foudroyer l’ennemi.\r\n', 7, 13, 7),
(739, 'crabrawler', 'Crabagarre', 47, 82, 57, 42, 47, 63, NULL, 'Il protège ses points faibles avec ses pinces en\r\nguettant le moment propice pour riposter.\r\nIl crache de l’écume lorsqu’il tombe K.O.\r\n', 2, NULL, 7),
(740, 'crabominable', 'Crabominable', 97, 132, 77, 62, 67, 43, 739, 'Il rêvait d’atteindre le sommet d’un pic enneigé,\r\nmais s’est égaré en chemin. Il a dû évoluer pour\r\nsurvivre au froid et a acquis un pelage fourni.\r\n', 2, 15, 7),
(741, 'oricorio-baile', 'Plumeline', 75, 70, 70, 98, 70, 93, NULL, 'Il réunit ses ailes pour générer du feu.\r\nSa chorégraphie envoûtante noie l’ennemi\r\nsous un torrent de flammes.\r\n', 3, 10, 7),
(742, 'cutiefly', 'Bombydou', 40, 45, 40, 55, 40, 84, NULL, 'Il se nourrit du nectar et du pollen des fleurs.\r\nAyant la capacité de percevoir l’aura des\r\nchoses, il sait quelles fleurs vont bientôt éclore.\r\n', 7, 18, 7),
(743, 'ribombee', 'Rubombelle', 60, 55, 60, 95, 70, 124, 742, 'Il confectionne de petites boules de pollen\r\nde diverses natures, certaines lui servant de\r\nrepas, d’autres à attaquer l’ennemi.\r\n', 7, 18, 7),
(744, 'rockruff', 'Rocabot', 45, 65, 40, 30, 40, 60, NULL, 'Comme il s’attache facilement à son Dresseur,\r\non le conseille souvent aux débutants, bien qu’il\r\nait tendance à devenir plus rebelle avec l’âge.\r\n', 6, NULL, 7),
(745, 'lycanroc-midday', 'Lougaroc', 75, 115, 65, 55, 65, 112, 744, 'Il fait tourner en bourrique ses ennemis à cause\r\nde sa vitesse. Outre ses crocs et ses griffes,\r\nil peut compter sur le roc acéré de sa crinière.\r\n', 6, NULL, 7),
(746, 'wishiwashi-solo', 'Froussardine', 45, 20, 20, 25, 25, 40, NULL, 'Quand il est dans le pétrin, ses yeux deviennent\r\nluisants. Ses semblables accourent alors et\r\nforment avec lui un banc de combat.\r\n', 11, NULL, 7),
(747, 'mareanie', 'Vorastérie', 50, 53, 62, 43, 52, 45, NULL, 'Les épines sur sa tête transpercent et\r\nempoisonnent l’ennemi. Ses dix tentacules\r\nse chargent ensuite du reste.\r\n', 4, 11, 7),
(748, 'toxapex', 'Prédastérie', 50, 63, 152, 53, 142, 35, 747, 'Il arpente le fond des océans du haut de ses\r\ndouze pattes, en laissant derrière lui une traînée\r\nde miettes de Corayon.\r\n', 4, 11, 7),
(749, 'mudbray', 'Tiboudet', 70, 100, 70, 45, 55, 45, NULL, 'La boue qui lui colle aux pattes lui sert de\r\nsemelle antidérapante. Il peut ainsi courir\r\nà toute allure avec vigueur et assurance.\r\n', 5, NULL, 7),
(750, 'mudsdale', 'Bourrinos', 100, 125, 100, 55, 85, 35, 749, 'Une fois sèche, la boue qu’il crache reste solide\r\nmême sous le vent et la pluie. Elle était autrefois\r\ntrès utilisée dans les revêtements muraux.\r\n', 5, NULL, 7),
(751, 'dewpider', 'Araqua', 38, 40, 52, 40, 72, 27, NULL, 'Il lui arrive d’aller chercher sa nourriture sur\r\nla terre ferme. Sa bulle lui sert alors aussi bien\r\nà respirer qu’à protéger sa tête molle.\r\n', 7, 11, 7),
(752, 'araquanid', 'Tarenbulle', 68, 70, 92, 50, 132, 42, 751, 'Il se bat en donnant des « coups de bulle ».\r\nLes plus petits Pokémon peuvent se retrouver\r\npiégés à l’intérieur de sa bulle et mourir noyés.\r\n', 7, 11, 7),
(753, 'fomantis', 'Mimantis', 40, 55, 35, 50, 35, 35, NULL, 'La journée, il passe son temps à flâner au soleil\r\net à faire la sieste. Le soir venu, il part à la\r\nrecherche d’un lieu plus sûr pour passer la nuit.\r\n', 12, NULL, 7),
(754, 'lurantis', 'Floramantis', 70, 105, 90, 80, 90, 45, 753, 'Préserver ses couleurs éclatantes requiert\r\nun travail titanesque. On trouve tout de même\r\ndes gens assez fous pour s’y atteler.\r\n', 12, NULL, 7),
(755, 'morelull', 'Spododo', 40, 35, 55, 65, 75, 15, NULL, 'Il répand des spores luminescentes qui\r\nclignotent autour de lui. Quiconque aperçoit\r\nces lumières tombe dans un sommeil profond.\r\n', 12, 18, 7),
(756, 'shiinotic', 'Lampignon', 60, 45, 80, 90, 100, 30, 755, 'Il est risqué de s’aventurer la nuit dans un bois\r\npeuplé de Lampignon. Attirés par leur lumière\r\nenvoûtante, certains n’en sont jamais revenus.\r\n', 12, 18, 7),
(757, 'salandit', 'Tritox', 48, 44, 40, 71, 40, 77, NULL, 'Il consume ses fluides corporels pour générer\r\nun gaz toxique qui étourdit ses ennemis.\r\nIl ne lui reste alors qu’à les achever.\r\n', 4, 10, 7),
(758, 'salazzle', 'Malamandre', 68, 64, 60, 111, 60, 117, 757, 'Curieusement, cette espèce ne compte que\r\ndes femelles. Ces dernières aiment s’entourer\r\nde nombreux admirateurs Tritox mâles.\r\n', 4, 10, 7),
(759, 'stufful', 'Nounourson', 70, 75, 50, 45, 50, 50, NULL, 'Il est à croquer, sauf lorsqu’il s’énerve et frappe\r\nà tout va. Dans ce cas, laissez-le tranquille ou\r\nvous finirez à coup sûr dans le décor.\r\n', 1, 2, 7),
(760, 'bewear', 'Chelours', 120, 125, 80, 55, 60, 60, 759, 'Sa force physique hors norme le rend assez\r\ndangereux. D’ailleurs, les zones qu’il habite sont\r\nsystématiquement interdites d’accès.\r\n', 1, 2, 7),
(761, 'bounsweet', 'Croquine', 42, 30, 38, 30, 38, 32, NULL, 'Une odeur terriblement appétissante émane\r\ndu corps des Croquine. Alléchés, les Bazoucan\r\nrappliquent et les gobent goulument.\r\n', 12, NULL, 7),
(762, 'steenee', 'Candine', 52, 40, 48, 40, 48, 62, 761, 'Pour mieux se défendre, il a fait pousser sur\r\nson corps un calice assez solide pour résister\r\naux coups de bec des Pokémon oiseaux.\r\n', 12, NULL, 7),
(763, 'tsareena', 'Sucreine', 72, 120, 98, 50, 98, 72, 762, 'Spécialiste du coup de pied bien placé, ce\r\nPokémon prend un malin plaisir à savater\r\nses ennemis au sol en riant aux éclats.\r\n', 12, NULL, 7),
(764, 'comfey', 'Guérilande', 51, 52, 90, 82, 110, 100, NULL, 'Sa liane gluante diffuse des nutriments dans les\r\nfleurs qu’elle happe. Ces dernières dégagent\r\nalors un léger parfum.\r\n', 18, NULL, 7),
(765, 'oranguru', 'Gouroutan', 90, 60, 80, 90, 110, 60, NULL, 'Célébré pour son intelligence exceptionnelle,\r\nce Pokémon vous prendra de haut si vous\r\ndébutez. À réserver aux Dresseurs aguerris.\r\n', 1, 14, 7),
(766, 'passimian', 'Quartermac', 100, 120, 90, 40, 60, 80, NULL, 'Ils se rassemblent par groupes de vingt. Dans\r\ncette formation très soudée, nul ne songerait\r\nune seule seconde à abandonner ses alliés.\r\n', 2, NULL, 7),
(767, 'wimpod', 'Sovkipou', 25, 35, 40, 20, 30, 80, NULL, 'De nature poltronne, il prend ses nombreuses\r\npattes à son cou au moindre danger et balaie\r\nle sol sur son passage.\r\n', 7, 11, 7),
(768, 'golisopod', 'Sarmuraï', 75, 125, 140, 60, 90, 40, 767, 'Il fait miroiter ses énormes griffes acérées\r\navant d’attaquer. Elles sont assez puissantes\r\npour fendre en deux l’air comme la mer.\r\n', 7, 11, 7),
(769, 'sandygast', 'Bacabouh', 55, 55, 80, 70, 45, 15, NULL, 'Ce Pokémon est né de la rancœur d’un voyageur\r\nmort dans l’indifférence, laquelle s’est incarnée\r\ndans un pâté de sable.\r\n', 5, 8, 7),
(770, 'palossand', 'Trépassable', 85, 75, 110, 100, 75, 35, 769, 'Il a possédé un humain pour que ce dernier\r\nle remodèle en château de sable, décuplant\r\nau passage ses pouvoirs de malédiction.\r\n', 5, 8, 7),
(771, 'pyukumuku', 'Concombaffe', 55, 60, 130, 30, 130, 5, NULL, 'Il vit en bord de mer, ou en eaux peu profondes.\r\nSes organes peuvent sortir de son corps pour\r\nsaisir de la nourriture ou frapper l’ennemi.\r\n', 11, NULL, 7),
(772, 'type-null', 'Type:0', 95, 95, 95, 95, 95, 59, NULL, 'Le lourd masque qu’il porte est un limiteur qui\r\nl’empêche d’exprimer toute l’étendue de\r\nsa puissance. Il recèle une force mystérieuse.\r\n', 1, NULL, 7),
(773, 'silvally', 'Silvallié', 95, 95, 95, 95, 95, 95, 772, 'La confiance qu’il place en son Dresseur a\r\nréveillé ses pouvoirs. Il est à présent capable\r\nde changer de type à loisir durant le combat.\r\n', 1, NULL, 7),
(774, 'minior-red-meteor', 'Météno', 60, 60, 100, 60, 100, 60, NULL, 'Il vivait dans la couche d’ozone, avant que\r\nsa carapace, devenue trop lourde, ne finisse\r\npar le faire chuter sur la terre ferme.\r\n', 3, 6, 7),
(775, 'komala', 'Dodoala', 65, 115, 65, 75, 95, 65, NULL, 'Il rejoint et quitte ce monde en dormant. Son\r\nexistence se déroule comme un rêve paisible.\r\nIl dort du sommeil du juste toute sa vie durant.\r\n', 1, NULL, 7),
(776, 'turtonator', 'Boumata', 60, 78, 135, 91, 85, 36, NULL, 'Sa carapace est très instable. Si on la heurte\r\npar mégarde, elle explose. Le creux sur\r\nson ventre est son point faible.\r\n', 10, 16, 7),
(777, 'togedemaru', 'Togedemaru', 65, 98, 63, 40, 73, 96, NULL, 'Les épines de ce Pokémon sont normalement\r\nbaissées, mais quand il s’énerve, elles se\r\ndressent afin de pourfendre ses agresseurs.\r\n', 9, 13, 7),
(778, 'mimikyu-disguised', 'Mimiqui', 55, 90, 80, 50, 105, 96, NULL, 'Sa véritable apparence est inconnue. Les\r\nchercheurs qui ont vu sous son déguisement\r\nsont littéralement morts de peur.\r\n', 8, 18, 7),
(779, 'bruxish', 'Denticrisse', 68, 105, 70, 70, 70, 92, NULL, 'Lorsqu’il utilise l’appendice sur sa tête pour\r\nprojeter ses pouvoirs psychiques, il émet un\r\nbruit aussi irritant qu’un grincement de dents.\r\n', 11, 14, 7),
(780, 'drampa', 'Draïeul', 78, 60, 85, 135, 91, 36, NULL, 'C’est une bonne pâte de nature, mais si par\r\nmalheur il s’énerve, il peut détruire tout ce qui\r\nl’entoure de son souffle puissant.\r\n', 1, 16, 7),
(781, 'dhelmise', 'Sinistrail', 70, 131, 100, 86, 90, 40, NULL, 'Avec un bon coup d’ancre bien senti, il peut\r\nmême assommer un Wailord. Son véritable corps\r\nest la partie composée de limon.\r\n', 8, 12, 7),
(782, 'jangmo-o', 'Bébécaille', 45, 55, 65, 45, 45, 45, NULL, 'Il communique ses sentiments en frottant ses\r\nécailles. Les montagnes peuplées de Bébécaille\r\nbruissent d’un écho métallique.\r\n', 16, NULL, 7),
(783, 'hakamo-o', 'Écaïd', 55, 75, 90, 65, 70, 65, 782, 'Il se jette sur ses proies en poussant un terrible\r\ncri de guerre. Il les déchiquette ensuite avec\r\nses poings écaillés.\r\n', 2, 16, 7),
(784, 'kommo-o', 'Ékaïser', 75, 110, 125, 100, 105, 85, 783, 'Quand il voit un ennemi, il cherche à l’intimider\r\nen faisant sonner les écailles de sa queue. Les\r\nplus faibles perdent contenance et s’enfuient.\r\n', 2, 16, 7),
(785, 'tapu-koko', 'Tokorico', 70, 115, 85, 95, 75, 130, NULL, 'Gardien de Mele-Mele, il est curieux de tout.\r\nIl a le pouvoir d’appeler les nuages orageux\r\npour stocker la foudre dans son corps.\r\n', 13, 18, 7),
(786, 'tapu-lele', 'Tokopiyon', 70, 85, 75, 130, 115, 95, NULL, 'Pokémon candide mais terriblement cruel,\r\nc’est le gardien d’Akala. Il puise son énergie\r\ndans le parfum entêtant des fleurs.\r\n', 14, 18, 7),
(787, 'tapu-bulu', 'Tokotoro', 70, 130, 115, 85, 95, 75, NULL, 'Il arrache de grands arbres et fait des moulinets\r\navec. Il a le pouvoir de rendre la végétation\r\nluxuriante pour ensuite se nourrir de cette force.\r\n', 12, 18, 7),
(788, 'tapu-fini', 'Tokopisco', 70, 75, 115, 95, 130, 85, NULL, 'Il confond ses ennemis grâce au brouillard\r\net les pousse à s’en prendre à eux-mêmes.\r\nIl puise son énergie dans les courants marins.\r\n', 11, 18, 7),
(789, 'cosmog', 'Cosmog', 43, 29, 31, 29, 31, 37, NULL, 'Son corps est composé d’un gaz très volatile.\r\nIl croît lentement, en accumulant les poussières\r\natmosphériques au gré de ses déplacements.\r\n', 14, NULL, 7),
(790, 'cosmoem', 'Cosmovum', 43, 29, 131, 29, 131, 37, 789, 'On pourrait le croire mort car il ne bouge pas\r\ndu tout. Au toucher, il reste tiède. On le\r\nsurnomme « cocon céleste » depuis l’Antiquité.\r\n', 14, NULL, 7),
(791, 'solgaleo', 'Solgaleo', 137, 137, 107, 113, 89, 97, 790, 'Il viendrait d’un autre monde. Tout son corps\r\német une puissante lumière qui peut changer\r\nla nuit la plus noire en un jour radieux.\r\n', 9, 14, 7),
(792, 'lunala', 'Lunala', 137, 113, 89, 137, 107, 97, 790, 'On suppose que c’est l’évolution de Cosmog\r\nlorsqu’il s’agit d’une femelle. Son troisième œil\r\napparaît quand il s’envole vers un autre monde.\r\n', 8, 14, 7),
(793, 'nihilego', 'Zéroïd', 109, 53, 47, 127, 131, 103, NULL, 'L’une des mystérieuses Ultra-Chimères.\r\nOn l’aurait vue parasiter des gens en pleine\r\nrue, les rendant extrêmement violents.\r\n', 4, 6, 7),
(794, 'buzzwole', 'Mouscoto', 107, 139, 139, 53, 53, 79, NULL, 'Personne ne sait si cette Ultra-Chimère venue\r\nd’un autre monde montre ses muscles pour être\r\nadmirée ou pour intimider ceux qu’elle rencontre.\r\n', 2, 7, 7),
(795, 'pheromosa', 'Cancrelove', 71, 137, 37, 137, 37, 151, NULL, 'L’une des dangereuses Ultra-Chimères.\r\nOn l’aurait aperçue en train de courir à\r\nune vitesse prodigieuse.\r\n', 2, 7, 7),
(796, 'xurkitree', 'Câblifère', 83, 89, 71, 173, 71, 83, NULL, 'L’une des mystérieuses Ultra-Chimères.\r\nOn l’aurait vue émettre une formidable quantité\r\nd’électricité autour d’elle.\r\n', 13, NULL, 7),
(797, 'celesteela', 'Bamboiselle', 97, 101, 103, 107, 101, 61, NULL, 'Une Ultra-Chimère qui a traversé l’Ultra-Brèche.\r\nOn l’aurait aperçue parcourir le ciel à grande\r\nvitesse.\r\n', 3, 9, 7),
(798, 'kartana', 'Katagami', 59, 181, 131, 59, 31, 109, NULL, 'Une Ultra-Chimère venue de l’Ultra-Brèche.\r\nElle ne semble pas agressive, bien que son\r\ncorps tout entier soit une arme tranchante.\r\n', 9, 12, 7),
(799, 'guzzlord', 'Engloutyran', 223, 101, 53, 97, 53, 43, NULL, 'Une espèce d’Ultra-Chimère.\r\nOn l’aurait aperçue dévorant des montagnes\r\net des gratte-ciel.\r\n', 16, 17, 7),
(800, 'necrozma', 'Necrozma', 97, 107, 101, 127, 89, 79, NULL, 'Une créature endormie sous terre. On suppose\r\nqu’il s’agit d’une Ultra-Chimère et qu’elle serait\r\nvenue d’un autre monde, il y a bien longtemps.\r\n', 14, NULL, 7);
INSERT INTO `pokemon_pokemon` (`id`, `name`, `nom`, `pv`, `attaque`, `defense`, `attaque_speciale`, `defense_speciale`, `vitesse`, `evolution_de`, `description`, `type_1`, `type_2`, `gen_id`) VALUES
(801, 'magearna', 'Magearna', 80, 95, 115, 130, 115, 65, NULL, 'Ce Pokémon a été créé de toutes pièces il y a\r\n500 ans. Il comprend ce qu’on lui dit, mais\r\nil n’est pas doué de parole.\r\n', 9, 18, 7),
(802, 'marshadow', 'Marshadow', 90, 125, 80, 90, 90, 125, NULL, 'Il dissimule son apparence aux autres en se\r\ntapissant dans l’ombre. Son existence pourrait\r\nse révéler n’être qu’une fable.\r\n', 2, 8, 7),
(803, 'poipole', 'Vémini', 67, 73, 67, 73, 67, 73, NULL, 'Dans son monde, cette Ultra-Chimère est fort\r\nappréciée pour son naturel conciliant, qui fait\r\nd’elle un compagnon de voyage idéal.\r\n', 4, NULL, 8),
(804, 'naganadel', 'Mandrillon', 73, 73, 73, 127, 73, 121, 803, 'Son corps contient des hectolitres de poison.\r\nIl fait partie de ces créatures que l’on nomme\r\n« Ultra-Chimères ».\r\n', 4, 16, 8),
(805, 'stakataka', 'Ama-Ama', 61, 131, 211, 53, 101, 13, NULL, 'Il est sorti d’une Ultra-Brèche. Il est composé\r\nde plusieurs petites entités qui s’assemblent\r\npour n’en former qu’une seule.\r\n', 6, 9, 8),
(806, 'blacephalon', 'Pierroteknik', 53, 127, 53, 151, 79, 107, NULL, 'Il s’approche des gens avec son allure\r\ndésarticulée et fait subitement exploser\r\nsa tête. Ce serait une Ultra-Chimère.\r\n', 8, 10, 8),
(807, 'zeraora', 'Zeraora', 88, 112, 75, 102, 80, 143, NULL, 'Il déchiquette son adversaire avec ses griffes\r\nchargées d’électricité. Même si sa cible évite\r\nl’attaque, elle reçoit un coup de jus.\r\n', 13, NULL, 8),
(808, 'meltan', 'Meltan', 46, 65, 65, 55, 35, 34, NULL, 'Ce Pokémon fait fondre le fer et les autres\r\nmétaux contenus dans le sol pour les intégrer\r\nà son corps composé d’acier fondu.\r\n', 9, NULL, 8),
(809, 'melmetal', 'Melmetal', 135, 143, 143, 80, 65, 34, 808, 'Autrefois vénéré pour son aptitude à produire\r\ndu fer, ce Pokémon a mystérieusement ressuscité\r\naprès 3 000 ans.\r\n', 9, NULL, 8),
(810, 'grookey', 'Ouistempo', 50, 65, 50, 40, 40, 65, NULL, 'Le rythme qu’il crée en tapant avec son bâton\r\ntrès spécial génère des ondes sonores\r\nqui ont le pouvoir de vivifier les plantes.\r\n', 12, NULL, 8),
(811, 'thwackey', 'Badabouin', 70, 85, 70, 55, 60, 80, 810, 'Les Badabouin qui parviennent à suivre le rythme\r\nle plus effréné avec leurs deux bâtons sont\r\nles plus respectés par leurs camarades.\r\n', 12, NULL, 8),
(812, 'rillaboom', 'Gorythmic', 100, 125, 90, 60, 70, 85, 811, 'Grâce au pouvoir du Gigamax, sa souche\r\ns’est développée pour devenir une véritable\r\nforêt d’instruments de percussion.\r\n', 12, NULL, 8),
(813, 'scorbunny', 'Flambino', 50, 71, 40, 40, 40, 69, NULL, 'Il court pour augmenter sa température et faire\r\ncirculer l’énergie incandescente dans son corps.\r\nIl peut ainsi déployer toute sa puissance.\r\n', 10, NULL, 8),
(814, 'raboot', 'Lapyro', 65, 86, 60, 55, 60, 94, 813, 'Son pelage épais lui permet de mieux résister\r\nau froid et d’augmenter la température\r\nde ses capacités Feu.\r\n', 10, NULL, 8),
(815, 'cinderace', 'Pyrobut', 80, 116, 75, 65, 75, 119, 814, 'Grâce au pouvoir du Gigamax, ses ballons de feu\r\ndépassent parfois les 100 mètres de diamètre.\r\n', 10, NULL, 8),
(816, 'sobble', 'Larméléon', 50, 40, 40, 70, 40, 70, NULL, 'Quand il a peur, il pleure des larmes contenant\r\nune substance lacrymogène d’une puissance\r\néquivalente à celle de cent oignons.\r\n', 11, NULL, 8),
(817, 'drizzile', 'Arrozard', 65, 60, 55, 95, 55, 90, 816, 'Il crée des bombes à eau grâce au liquide\r\nqu’il sécrète avec la paume de ses mains.\r\nIl s’en sert dans les combats où la stratégie prime.\r\n', 11, NULL, 8),
(818, 'inteleon', 'Lézargus', 70, 85, 65, 125, 65, 120, 817, 'Il a plus d’un tour dans son sac : il peut tirer\r\ndes gerbes d’eau du bout de ses doigts et planer\r\ndans les airs grâce à ses membranes dorsales.\r\n', 11, NULL, 8),
(819, 'skwovet', 'Rongourmand', 70, 55, 55, 35, 35, 25, NULL, 'On le trouve partout à Galar. S’il n’a pas de Baies\r\nen réserve dans ses deux joues, il devient anxieux.\r\n', 1, NULL, 8),
(820, 'greedent', 'Rongrigou', 120, 95, 95, 55, 75, 20, 819, 'Il amasse tellement de Baies dans sa queue\r\nqu’elles tombent les unes après les autres, mais\r\nil est si étourdi qu’il ne s’en rend pas compte.\r\n', 1, NULL, 8),
(821, 'rookidee', 'Minisange', 38, 47, 35, 33, 35, 57, NULL, 'Ce Pokémon brave affronte même les adversaires\r\nles plus redoutables. Il devient plus fort à mesure\r\nqu’il subit les contre-attaques.\r\n', 3, NULL, 8),
(822, 'corvisquire', 'Bleuseille', 68, 67, 55, 43, 55, 77, 821, 'Il est capable d’utiliser des objets avec ses serres :\r\nil peut par exemple saisir et lancer des pierres\r\nou même enrouler une corde autour d’un ennemi.\r\n', 3, NULL, 8),
(823, 'corviknight', 'Corvaillus', 98, 87, 105, 53, 85, 67, 820, 'Grâce au pouvoir du Gigamax, il déclenche\r\nde terribles ouragans qui emportent tout\r\nsur leur passage quand il bat des ailes.\r\n', 3, 9, 8),
(824, 'blipbug', 'Larvadar', 25, 20, 20, 25, 45, 45, NULL, 'Comme il passe tout son temps à recueillir des\r\ninformations, son intelligence est très développée,\r\nmais sa force physique laisse à désirer.\r\n', 7, NULL, 8),
(825, 'dottler', 'Coléodôme', 50, 35, 80, 50, 90, 30, 824, 'Il ne bouge presque pas, mais il est bien vivant.\r\nOn dit qu’il aurait découvert ses pouvoirs\r\npsychiques à force de jeûner dans sa coquille.\r\n', 7, 14, 8),
(826, 'orbeetle', 'Astronelle', 60, 45, 110, 80, 120, 90, 825, 'Il est renommé pour son intelligence.\r\nSon gros cerveau est un indicateur de la puissance\r\nde ses pouvoirs psychiques.\r\n', 7, 14, 8),
(827, 'nickit', 'Goupilou', 40, 28, 28, 47, 52, 50, NULL, 'Il vit en subtilisant la nourriture des autres\r\nPokémon. Grâce à ses coussinets moelleux,\r\nil ne fait aucun bruit quand il marche.\r\n', 17, NULL, 8),
(828, 'thievul', 'Roublenard', 70, 58, 58, 87, 92, 90, 827, 'Il marque sa victime pour la suivre à l’odeur.\r\nSitôt qu’elle est distraite, il s’empresse de lui\r\nvoler ses biens.\r\n', 17, NULL, 8),
(829, 'gossifleur', 'Tournicoton', 40, 40, 60, 40, 60, 10, NULL, 'Quand il plante son unique pied pour absorber\r\nla lumière du soleil, ses pétales prennent\r\nune couleur éclatante.\r\n', 12, NULL, 8),
(830, 'eldegoss', 'Blancoton', 60, 50, 90, 80, 120, 60, 829, 'Les graines de son duvet sont remplies\r\nde nutriments. Elles sont emportées par le vent\r\net redonnent vitalité aux plantes et aux Pokémon.\r\n', 12, NULL, 8),
(831, 'wooloo', 'Moumouton', 42, 40, 55, 40, 45, 48, NULL, 'Son pelage frisé est doux et rebondi comme\r\nun coussin. Une telle toison le protègerait\r\nmême s’il chutait d’une falaise.\r\n', 1, NULL, 8),
(832, 'dubwool', 'Moumouflon', 72, 80, 100, 60, 90, 88, 831, 'La texture de sa laine est si élastique que\r\nla surface des tapis fabriqués avec ce matériau\r\nest aussi rebondissante que celle d’un trampoline.\r\n', 1, NULL, 8),
(833, 'chewtle', 'Khélocrok', 50, 64, 50, 38, 38, 44, NULL, 'Il a pour habitude de mordre tout ce qui lui passe\r\nsous les yeux. On raconte qu’il fait cela parce que\r\nses incisives qui poussent le démangent.\r\n', 11, NULL, 8),
(834, 'drednaw', 'Torgamord', 90, 115, 90, 48, 68, 74, 833, 'De nature très violente, il mord fermement\r\nses proies avec sa mâchoire, assez puissante\r\npour broyer une barre de fer.\r\n', 6, 11, 8),
(835, 'yamper', 'Voltoutou', 59, 45, 50, 40, 50, 26, NULL, 'Lorsqu’il court, on peut voir de l’électricité\r\nse former à la base de sa queue. Ce Pokémon\r\nest très populaire auprès des bergers de Galar.\r\n', 13, NULL, 8),
(836, 'boltund', 'Fulgudog', 69, 90, 60, 90, 60, 121, 835, 'Il produit de l’électricité puis la transfère\r\ndans ses pattes pour mieux courir. Ainsi, il peut\r\nfoncer pendant trois jours et trois nuits.\r\n', 13, NULL, 8),
(837, 'rolycoly', 'Charbi', 30, 40, 50, 40, 50, 30, NULL, 'On l’a découvert dans une mine il y a 400 ans.\r\nSon corps est presque entièrement composé\r\nd’une substance identique au charbon.\r\n', 6, NULL, 8),
(838, 'carkol', 'Wagomine', 80, 60, 90, 60, 70, 50, 837, 'Il produit du charbon dans son corps. Jadis, les\r\nhabitants de Galar ramassaient ce charbon au sol\r\net s’en servaient pour leurs besoins quotidiens.\r\n', 6, 10, 8),
(839, 'coalossal', 'Monthracite', 110, 80, 120, 80, 90, 30, 838, 'Il est d’une nature paisible, mais si les humains\r\ntentent de piller ses mines, il enrage et brûle\r\ntout avec des flammes à 1 500 °C.\r\n', 6, 10, 8),
(840, 'applin', 'Verpom', 40, 40, 80, 40, 40, 20, NULL, 'Il vit toujours dans sa pomme. S’il croise\r\nun Pokémon oiseau, son prédateur naturel, il se\r\nfait passer pour un simple fruit pour se protéger.\r\n', 12, 16, 8),
(841, 'flapple', 'Pomdrapi', 70, 110, 80, 95, 60, 70, 840, 'Il a évolué après avoir mangé une pomme acide.\r\nIl concentre dans ses joues un liquide si corrosif\r\nqu’il peut provoquer des brûlures.\r\n', 12, 16, 8),
(842, 'appletun', 'Dratatin', 110, 85, 80, 100, 80, 30, 840, 'Il a évolué après avoir mangé une pomme sucrée.\r\nIl attire ses proies, les Pokémon Insecte,\r\nen dégageant un parfum mielleux.\r\n', 12, 16, 8),
(843, 'silicobra', 'Dunaja', 52, 57, 75, 35, 50, 46, NULL, 'Il amasse le sable qu’il avale en creusant\r\ndans la poche de son cou. Elle peut en contenir\r\njusqu’à huit kilos.\r\n', 5, NULL, 8),
(844, 'sandaconda', 'Dunaconda', 72, 107, 125, 65, 70, 71, 843, 'Il peut expulser 100 kg de sable de ses narines\r\nen compressant tout son corps. Quand il est\r\nà court de sable, il a tendance à se dérober.\r\n', 5, NULL, 8),
(845, 'cramorant', 'Nigosier', 70, 85, 55, 85, 95, 85, NULL, 'Ce glouton a essayé d’avaler tout rond\r\nun Embrochet bien gras, mais ce dernier\r\nest resté coincé dans sa gorge.\r\n', 3, 11, 8),
(846, 'arrokuda', 'Embrochet', 41, 63, 40, 40, 30, 66, NULL, 'Sa mâchoire fine et aiguisée fait sa fierté.\r\nÀ peine voit-il le moindre mouvement\r\nqu’il fonce dans sa direction.\r\n', 11, NULL, 8),
(847, 'barraskewda', 'Hastacuda', 61, 123, 60, 60, 50, 136, 846, 'Sa mâchoire pointue comme une lance est aussi\r\ndure que l’acier, et sa chair serait, contre toute\r\nattente, succulente.\r\n', 11, NULL, 8),
(848, 'toxel', 'Toxizap', 40, 38, 35, 54, 35, 40, NULL, 'Sa peau sécrète les toxines accumulées\r\ndans sa poche de poison interne.\r\nQuand on le touche, on se fait électrocuter.\r\n', 4, 13, 8),
(849, 'toxtricity', 'Salarsen', 75, 98, 70, 114, 70, 75, 848, 'Il peut libérer des décharges électriques\r\nde 15 000 volts. Il sous-estime souvent\r\nses adversaires, quels qu’ils soient.\r\n', 4, 13, 8),
(850, 'sizzlipede', 'Grillepattes', 50, 65, 45, 50, 50, 45, NULL, 'Il produit de la chaleur grâce au gaz inflammable\r\nqu’il emmagasine dans son corps. La partie jaune\r\nde son abdomen est particulièrement brûlante.\r\n', 7, 10, 8),
(851, 'centiskorch', 'Scolocendre', 100, 115, 65, 90, 90, 65, 850, 'Quand il génère de la chaleur, sa température\r\nest d’environ 800 °C. Il se sert de son corps\r\ncomme d’un fouet pour sauter sur ses ennemis.\r\n', 7, 10, 8),
(852, 'clobbopus', 'Poulpaf', 50, 68, 60, 50, 50, 32, NULL, 'Il se rend sur la terre ferme pour se nourrir.\r\nTrès curieux, son premier réflexe est de frapper\r\ntout ce qu’il voit avec ses tentacules.\r\n', 2, NULL, 8),
(853, 'grapploct', 'Krakos', 80, 118, 90, 70, 80, 42, 852, 'Son corps est un amas de muscles.\r\nLa puissance des prises d’étranglement\r\nqu’il exécute avec ses tentacules est saisissante.\r\n', 2, NULL, 8),
(854, 'sinistea', 'Théffroi', 40, 45, 45, 74, 54, 50, NULL, 'La spirale sur son corps est son point faible.\r\nSi on la touille, elle perd sa forme,\r\nce qui lui donne des vertiges.\r\n', 8, NULL, 8),
(855, 'polteageist', 'Polthégeist', 60, 65, 65, 134, 114, 70, 854, 'Il élit domicile dans des théières antiques.\r\nLa plupart sont des contrefaçons, mais il lui arrive\r\ndans de très rares cas de trouver des originaux.\r\n', 8, NULL, 8),
(856, 'hatenna', 'Bibichut', 42, 30, 45, 56, 53, 39, NULL, 'La protubérance sur sa tête lui permet\r\nde percevoir les émotions des êtres vivants.\r\nIl n’accorde sa confiance qu’aux gens calmes.\r\n', 14, NULL, 8),
(857, 'hattrem', 'Chapotus', 57, 40, 65, 86, 73, 49, 856, 'S’il ressent des émotions trop fortes, il en fera\r\ntaire la source, quelle qu’elle soit, en utilisant\r\ndes méthodes assez violentes.\r\n', 14, NULL, 8),
(858, 'hatterene', 'Sorcilence', 57, 90, 95, 136, 103, 29, 857, 'Il est capable de capter les émotions d’êtres\r\nvivants situés à une distance de 50 km.\r\nS’il ressent de l’animosité, il passe à l’attaque.\r\n', 14, 18, 8),
(859, 'impidimp', 'Grimalin', 45, 45, 30, 55, 40, 50, NULL, 'Pour se refaire une santé, il hume l’énergie\r\nnégative que les humains et les Pokémon\r\ndégagent quand ils sont irrités.\r\n', 17, 18, 8),
(860, 'morgrem', 'Fourbelin', 65, 60, 45, 75, 55, 70, 859, 'Sa technique de combat consiste à faire semblant\r\nde se prosterner devant son adversaire\r\npour le transpercer avec ses cheveux aiguisés.\r\n', 17, 18, 8),
(861, 'grimmsnarl', 'Angoliath', 95, 120, 65, 95, 75, 60, 860, 'Il enroule ses cheveux autour de son corps pour\r\naugmenter sa masse musculaire, ce qui lui donne\r\nassez de force pour enserrer un Mackogneur.\r\n', 17, 18, 8),
(862, 'obstagoon', 'Ixon', 93, 90, 101, 60, 81, 95, NULL, 'Il possède une puissance vocale remarquable.\r\nOn appelle Blocage sa technique qui consiste\r\nà intimider l’ennemi avec son cri guttural.\r\n', 1, 17, 8),
(863, 'perrserker', 'Berserkatt', 70, 110, 100, 50, 60, 50, NULL, 'La fourrure sur sa tête s’est endurcie\r\net a pris la forme d’un casque en fer.\r\nIl est de nature très bagarreuse.\r\n', 9, NULL, 8),
(864, 'cursola', 'Corayôme', 60, 95, 50, 145, 130, 30, NULL, 'Son énergie spectrale accrue s’est répandue\r\nhors de sa coquille. Il protège l’âme de son noyau\r\navec son corps spirituel.\r\n', 8, NULL, 8),
(865, 'sirfetch', 'Palarticho', 62, 135, 95, 68, 82, 65, NULL, 'Seuls les Canarticho ayant survécu à de nombreux\r\ncombats atteignent ce stade d’évolution. Si son\r\npoireau se flétrit, il se retire du champ de bataille.\r\n', 2, NULL, 8),
(866, 'mr.rime', 'M.Glaquette', 80, 85, 75, 110, 100, 70, NULL, 'Véritable virtuose des claquettes, il se donne\r\nen spectacle en dansant d’un pas léger\r\ntout en jonglant avec sa canne de glace.\r\n', 14, 15, 8),
(867, 'runerigus', 'Tutétékri', 58, 95, 145, 50, 105, 30, NULL, 'Les motifs antiques qui ont été gravés\r\nen y incorporant une puissante malédiction\r\nont aspiré l’âme de Tutafeh avant de prendre vie.\r\n', 5, 8, 8),
(868, 'milcery', 'Crèmy', 45, 40, 40, 50, 61, 34, NULL, 'Son corps de crème s’est composé à partir\r\nde particules au parfum sucré flottant dans l’air.\r\n', 18, NULL, 8),
(869, 'alcremie', 'Charmilly', 65, 60, 75, 110, 121, 64, 868, 'Au moment d’évoluer, les cellules de son corps\r\nont été secouées, ce qui lui a donné une saveur\r\nacide.\r\n', 18, NULL, 8),
(870, 'falinks', 'Hexadron', 65, 100, 100, 70, 60, 75, NULL, 'C’est une entité unique composée d’un chef\r\net de cinq cadets. Ces derniers obéissent\r\nsans sourciller à leur supérieur hiérarchique.\r\n', 2, NULL, 8),
(871, 'pincurchin', 'Wattapik', 48, 101, 95, 91, 85, 15, NULL, 'Il libère de l’électricité depuis la pointe de ses\r\népines, et se nourrit des algues accrochées aux\r\nrochers en les arrachant avec ses dents aiguisées.\r\n', 13, NULL, 8),
(872, 'snom', 'Frissonille', 30, 25, 35, 45, 30, 20, NULL, 'Il crache un fil gelé qu’il utilise pour s’accrocher\r\nà une branche : il dort ainsi en imitant\r\nune stalactite.\r\n', 7, 15, 8),
(873, 'frosmoth', 'Beldeneige', 70, 65, 60, 125, 90, 65, 872, 'La température de ses ailes est de - 180 °C.\r\nQuand il vole, ses écailles gelées tombent\r\nsur le paysage comme des flocons de neige.\r\n', 7, 15, 8),
(874, 'stonjourner', 'Dolman', 100, 125, 135, 20, 20, 70, NULL, 'Il se tient immobile au milieu des plaines\r\net passe son temps à observer la course du soleil\r\ndans le ciel. Son jeu de jambes est phénoménal.\r\n', 6, NULL, 8),
(875, 'eiscue', 'Bekaglaçon', 75, 80, 110, 65, 90, 50, NULL, 'C’est son apparence lorsque la glace sur sa tête\r\na fondu. Nombreux sont les gens qui sont captivés\r\npar son air tourmenté.\r\n', 15, NULL, 8),
(876, 'indeedee', 'Wimessir', 60, 65, 55, 105, 95, 95, NULL, 'Ce Pokémon très intelligent presse ses cornes\r\ncontre celles de ses congénères pour échanger\r\ndes informations.\r\n', 1, 14, 8),
(877, 'morpeko', 'Morpeko', 58, 95, 58, 70, 58, 97, NULL, 'Il a toujours un petit creux. Il grignote les graines\r\nqu’il a mises dans ses sortes de poches\r\npour produire de l’électricité.\r\n', 13, 17, 8),
(878, 'cufant', 'Charibari', 72, 80, 49, 40, 49, 40, NULL, 'Ce Pokémon est assez costaud pour porter\r\ndes charges de cinq tonnes sans problème.\r\nIl utilise sa trompe pour creuser le sol.\r\n', 9, NULL, 8),
(879, 'copperajah', 'Pachyradjah', 122, 130, 69, 80, 69, 30, 878, 'Lorsqu’il propulse l’énergie qu’il a accumulée\r\ndans sa trompe, les montagnes s’envolent,\r\ndéfigurant à jamais le paysage.\r\n', 9, NULL, 8),
(880, 'dracozolt', 'Galvagon', 90, 100, 90, 80, 70, 75, NULL, 'Jadis, la partie inférieure de son corps musclé\r\nle rendait invincible, mais il s’est éteint après avoir\r\nmangé toutes les plantes dont il se nourrissait.\r\n', 13, 16, 8),
(881, 'arctozolt', 'Galvagla', 90, 100, 90, 90, 80, 55, NULL, 'Quand la partie supérieure de son corps, couverte\r\nde glace, frissonne, il produit de l’électricité.\r\nIl éprouve de grandes difficultés à marcher.\r\n', 13, 15, 8),
(882, 'dracovish', 'Hydragon', 90, 90, 100, 70, 80, 75, NULL, 'La force de ses pattes et de ses mâchoires\r\nle rendait presque invincible, mais à trop chasser\r\nses proies, celles-ci ont disparu, et lui avec.\r\n', 11, 16, 8),
(883, 'arctovish', 'Hydragla', 90, 90, 100, 80, 90, 55, NULL, 'Il gèle les alentours pour capturer ses proies,\r\nmais comme sa bouche est sur le dessus de\r\nsa tête, il a beaucoup de mal à les manger.\r\n', 11, 15, 8),
(884, 'duraludon', 'Duralugon', 70, 95, 115, 120, 50, 85, NULL, 'Il est devenu aussi immense qu’un gratte-ciel.\r\nL’énergie qui afflue dans son corps le fait briller\r\npar endroits.\r\n', 9, 16, 8),
(885, 'dreepy', 'Fantyrm', 28, 60, 30, 40, 30, 82, NULL, 'Revenu à la vie sous forme de Pokémon Spectre,\r\nil erre au-dessus de la mer dans laquelle il vivait\r\nautrefois.\r\n', 8, 16, 8),
(886, 'drakloak', 'Dispareptil', 68, 80, 50, 60, 50, 102, 885, 'Il vole à une vitesse de 200 km/h et se bat\r\naux côtés d’un Fantyrm, dont il prend soin\r\njusqu’à son évolution.\r\n', 8, 16, 8),
(887, 'dragapult', 'Lanssorien', 88, 120, 75, 100, 75, 142, 886, 'Il vit en compagnie de Fantyrm qui logent\r\ndans les trous de ses cornes. Quand il passe\r\nà l’attaque, il les propulse à la vitesse du son.\r\n', 8, 16, 8),
(888, 'zacian', 'Zacian', 92, 130, 115, 80, 115, 138, NULL, 'Il est équipé d’une arme antique avec laquelle\r\nil tranche d’un seul coup les Pokémon,\r\nmême ceux qui disposent du pouvoir du Gigamax.\r\n', 18, NULL, 8),
(889, 'zamazenta', 'Zamazenta', 92, 130, 115, 80, 115, 138, NULL, 'Ce Pokémon sauva autrefois Galar en joignant\r\nses forces à celles du roi des Hommes.\r\nIl absorbe du métal quand il passe au combat.\r\n', 2, NULL, 8),
(890, 'eternatus', 'Éthernatos', 140, 85, 95, 145, 95, 130, NULL, 'Il devient actif en absorbant l’énergie qui émerge\r\ndu sol de Galar grâce au noyau situé\r\nsur sa poitrine.\r\n', 4, 16, 8),
(891, 'kubfu', 'Wushours', 60, 90, 60, 53, 50, 72, NULL, 'Il suit un entraînement rigoureux pour parfaire\r\nses techniques. La forme de son évolution\r\ndépend des capacités qu’il a assimilées.\r\n', 2, NULL, 8),
(892, 'urshifu', 'Shifours', 100, 130, 100, 63, 60, 97, 891, 'Adepte des techniques pouvant mettre K.O.\r\nen un seul coup, il se jette sur son adversaire\r\net le frappe de son poing robuste.\r\n', 2, 17, 8),
(893, 'zarude', 'Zarude', 105, 120, 105, 70, 95, 105, NULL, 'Ce Pokémon a quitté les siens pour élever\r\nun petit être humain. Il porte une étoffe\r\ndont l’odeur calme le bébé lorsqu’il pleure.\r\n', 12, 17, 8);
